import React, { useState, useEffect } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
} from "lucide-react";

// --- Theme Configuration ---
const theme = {
  colors: {
    primary: "blue-600",
    primaryLight: "blue-100",
    primaryDark: "blue-700",
    secondary: "gray-800",
    textPrimary: "gray-900",
    textSecondary: "gray-600",
    white: "white",
    success: "green-500",
    error: "red-500",
    modalOverlay: "black/50",
  },
  fontFamily: "font-inter font-sans", // Combine font family definitions
};

// --- Data Definitions ---
const serviceOptions = [
  {
    id: "home",
    name: "Home Cleaning",
    icon: <Home />,
    description: "Expert cleaning for homes.",
  },
  {
    id: "office",
    name: "Office Cleaning",
    icon: <Briefcase />,
    description: "Maintain a spotless office space.",
  },
  {
    id: "deep",
    name: "Deep Cleaning",
    icon: <SprayCan />,
    description: "Thorough deep cleaning services.",
  },
];

const initialReviews = [
  { name: "Isaac", text: "Fantastic service! My home has never been this clean." },
  { name: "Kojo", text: "The office cleaning was thorough and professional." },
  { name: "Big Ben", text: "Amazing deep cleaning. Highly recommended!" },
];

// --- Helper Functions ---
const useLocalStorage = (key, initialValue) => {
  const [storedValue, setStoredValue] = useState(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.error("Error getting data from localStorage:", error);
      return initialValue;
    }
  });

  useEffect(() => {
    try {
      window.localStorage.setItem(key, JSON.stringify(storedValue));
    } catch (error) {
      console.error("Error setting data to localStorage:", error);
    }
  }, [storedValue, key]);

  return [storedValue, setStoredValue];
};

const useNotification = (duration = 3000) => {
  const [notification, setNotification] = useState(null);

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), duration);
  };

  return [notification, showNotification];
};

// --- Components ---

const Modal = ({ isOpen, onClose, children }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded shadow-lg w-11/12 md:w-96 relative">
        <button className="absolute top-3 right-3 text-gray-600" onClick={onClose}>
          <XCircle size={20} />
        </button>
        {children}
      </div>
    </div>
  );
};

const ServiceCard = ({ service }) => (
