import React, { useState, useEffect } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
} from "lucide-react";

const themeColors = {
  primary: "blue-600",
  primaryLight: "blue-100",
  primaryDark: "blue-700",
  secondary: "gray-800",
  textPrimary: "gray-900",
  textSecondary: "gray-600",
  white: "white",
  success: "green-500",
  error: "red-500",
  modalOverlay: "black/50",
};

const services = [
  {
    id: "home",
    name: "Home Cleaning",
    icon: <Home />,
    description: "Expert cleaning for homes.",
  },
  {
    id: "office",
    name: "Office Cleaning",
    icon: <Briefcase />,
    description: "Maintain a spotless office space.",
  },
  {
    id: "deep",
    name: "Deep Cleaning",
    icon: <SprayCan />,
    description: "Thorough deep cleaning services.",
  },
];

const prePopulatedReviews = [
  {
    name: "Isaac",
    text: "Fantastic service! My home has never been this clean.",
  },
  {
    name: "Kojo",
    text: "The office cleaning was thorough and professional.",
  },
  { name: "Big Ben", text: "Amazing deep cleaning. Highly recommended!" },
];

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [review, setReview] = useState({ name: "", text: "" });
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });
  const [isReviewModalOpen, setIsReviewModalOpen] = useState(false);
  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [notification, setNotification] = useState(null);

  useEffect(() => {
    const savedReviews = JSON.parse(localStorage.getItem("reviews")) || [];
    setReviews(savedReviews.length > 0 ? savedReviews : prePopulatedReviews);
  }, []);

  useEffect(() => {
    localStorage.setItem("reviews", JSON.stringify(reviews));
  }, [reviews]);

  const handleReviewSubmit = () => {
    if (!review.name || !review.text) {
      showNotification("Please fill in all fields!", "error");
      return;
    }
    setReviews([...reviews, review]);
    setReview({ name: "", text: "" });
    showNotification("Review submitted successfully!", "success");
  };

  const handleQuoteSubmit = () => {
    if (!quote.name || !quote.service) {
      showNotification("Please enter your name and select a service!", "error");
      return;
    }
    showNotification(
      `Quote request for ${quote.service} submitted!`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    if (!appointment.name || !appointment.service || !appointment.date) {
      showNotification("Please fill in all fields!", "error");
      return;
    }
    showNotification(
      `Appointment booked for ${appointment.service} on ${appointment.date}!`,
      "success"
    );
    setAppointment({ name: "", service: "", date: "" });
    setIsAppointmentModalOpen(false);
  };

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  return (
    <div
      className={`min-h-screen bg-${themeColors.primaryLight} text-${themeColors.textPrimary} font-inter font-sans`}
    >
      {" "}
      {/* Added font-sans to ensure tailwind's default font family */}
      {notification && (
        <div
          className={`fixed top-6 right-6 p-3 rounded-lg flex items-center space-x-2 text-white bg-${
            notification.type === "success"
              ? themeColors.success
              : themeColors.error
          }`}
        >
          <CheckCircle />
          <span>{notification.message}</span>
        </div>
      )}
      {/* Header */}
      <header
        className={`bg-${themeColors.primary} text-${themeColors.white} p-4 flex flex-col md:flex-row justify-between items-center shadow-lg`}
      >
        <h1 className="text-2xl font-bold text-center md:text-left">
          CleanConnect
        </h1>
        <div className="flex space-x-4 mt-2 md:mt-0">
          <button
            onClick={() => {
              setIsReviewModalOpen(true);
              setIsQuoteModalOpen(false);
              setIsAppointmentModalOpen(false);
            }}
            className={`hover:text-gray-200 px-4 py-2 bg-${themeColors.white} text-${themeColors.primary} rounded-md`}
          >
            Customer Reviews
          </button>
          <button
            onClick={() => {
              setIsQuoteModalOpen(true);
              setIsReviewModalOpen(false);
              setIsAppointmentModalOpen(false);
            }}
            className={`hover:text-gray-200 px-4 py-2 bg-${themeColors.white} text-${themeColors.primary} rounded-md`}
          >
            Request Quote
          </button>
          <button
            onClick={() => {
              setIsAppointmentModalOpen(true);
              setIsReviewModalOpen(false);
              setIsQuoteModalOpen(false);
            }}
            className={`hover:text-gray-200 px-4 py-2 bg-${themeColors.white} text-${themeColors.primary} rounded-md`}
          >
            Book Appointment
          </button>
        </div>
      </header>
      {/* Hero Section */}
      <section
        className={`text-center py-12 px-4 md:py-16 bg-${themeColors.primaryLight}`}
      >
        <h2 className="text-3xl md:text-4xl font-bold">
          Find Professional Cleaning Services
        </h2>
        <p className="text-md md:text-lg mt-2">
          Book trusted cleaners in your area.
        </p>
      </section>
      {/* Book Appointment Modal */}
      {isAppointmentModalOpen && (
        <div className="fixed inset-0 bg-black/50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-11/12 md:w-96 relative">
            <button
              className="absolute top-3 right-3 text-gray-600"
              onClick={() => setIsAppointmentModalOpen(false)}
            >
              <XCircle size={20} />
            </button>
            <h2 className="text-lg md:text-xl font-bold mb-4">
              Book an Appointment
            </h2>
            <input
              type="text"
              placeholder="Your Name"
              className="w-full p-2 border rounded mb-2"
              value={appointment.name}
              onChange={(e) =>
                setAppointment({ ...appointment, name: e.target.value })
              }
            />
            <select
              className="w-full p-2 border rounded mb-2"
              value={appointment.service}
              onChange={(e) =>
                setAppointment({ ...appointment, service: e.target.value })
              }
            >
              <option value="" disabled>
                Select a service
              </option>
              {services.map((s) => (
                <option key={s.id} value={s.name}>
                  {s.name}
                </option>
              ))}
            </select>
            <input
              type="date"
              className="w-full p-2 border rounded mb-2"
              value={appointment.date}
              onChange={(e) =>
                setAppointment({ ...appointment, date: e.target.value })
              }
            />
            <button
              onClick={handleAppointmentSubmit}
              className={`bg-${themeColors.primary} text-${themeColors.white} px-4 py-2 rounded hover:bg-${themeColors.primaryDark} w-full`}
            >
              Book Now
            </button>
          </div>
        </div>
      )}
      {/* Services Section */}
      <section className="py-10 px-4 md:px-6">
        <h2 className="text-2xl md:text-3xl font-bold text-center mb-6">
          Our Services
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {services.map((service) => (
            <div
              key={service.id}
              className="p-6 bg-white shadow-md rounded-lg text-center"
            >
              <div className={`text-${themeColors.primary} mx-auto`}>
                {service.icon}
              </div>
              <h3 className="text-lg md:text-xl font-semibold mt-2">
                {service.name}
              </h3>
              <p className={`text-${themeColors.textSecondary} mt-2`}>
                {service.description}
              </p>
            </div>
          ))}
        </div>
      </section>
      {/* Customer Review Form */}
      <section className="py-10 px-4 md:px-6">
        <h2 className="text-2xl md:text-3xl font-bold text-center mb-6">
          Leave a Review
        </h2>
        <div className="max-w-lg mx-auto bg-white p-6 rounded-md shadow-md">
          <input
            type="text"
            placeholder="Your Name"
            className="w-full p-2 border rounded mb-2"
            value={review.name}
            onChange={(e) => setReview({ ...review, name: e.target.value })}
          />
          <textarea
            placeholder="Write a review"
            className="w-full p-2 border rounded mb-2"
            value={review.text}
            onChange={(e) => setReview({ ...review, text: e.target.value })}
          />
          <button
            onClick={handleReviewSubmit}
            className={`bg-${themeColors.primary} text-${themeColors.white} px-4 py-2 rounded hover:bg-${themeColors.primaryDark} w-full`}
          >
            Submit Review
          </button>
        </div>
      </section>
      {/* Customer Reviews Modal */}
      {isReviewModalOpen && (
        <div className="fixed inset-0 bg-black/50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-11/12 md:w-96 relative">
            <button
              className="absolute top-3 right-3 text-gray-600"
              onClick={() => setIsReviewModalOpen(false)}
            >
              <XCircle size={20} />
            </button>
            <h2 className="text-lg md:text-xl font-bold mb-4">
              Customer Reviews
            </h2>
            <div className="max-h-60 overflow-y-auto">
              {reviews.map((rev, index) => (
                <div
                  key={index}
                  className="bg-gray-100 p-3 rounded-md my-2 flex items-center space-x-2"
                >
                  <Star className="text-yellow-500 flex-shrink-0" size={20} />
                  <p>
                    <strong>{rev.name}:</strong> {rev.text}
                  </p>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
      {/* Request Quote Modal */}
      {isQuoteModalOpen && (
        <div className="fixed inset-0 bg-black/50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-11/12 md:w-96 relative">
            <button
              className="absolute top-3 right-3 text-gray-600"
              onClick={() => setIsQuoteModalOpen(false)}
            >
              <XCircle size={20} />
            </button>
            <h2 className="text-lg md:text-xl font-bold mb-4">
              Request a Quote
            </h2>
            <input
              type="text"
              placeholder="Your Name"
              className="w-full p-2 border rounded mb-2"
              value={quote.name}
              onChange={(e) => setQuote({ ...quote, name: e.target.value })}
            />
            <select
              className="w-full p-2 border rounded mb-2"
              value={quote.service}
              onChange={(e) => setQuote({ ...quote, service: e.target.value })}
            >
              <option value="" disabled>
                Select a service
              </option>
              {services.map((s) => (
                <option key={s.id} value={s.name}>
                  {s.name}
                </option>
              ))}
            </select>
            <button
              onClick={handleQuoteSubmit}
              className={`bg-${themeColors.primary} text-${themeColors.white} px-4 py-2 rounded hover:bg-${themeColors.primaryDark} w-full`}
            >
              Submit Quote
            </button>
          </div>
        </div>
      )}
      <footer
        className={`bg-${themeColors.secondary} text-${themeColors.white} text-center p-4`}
      >
        <p>© 2024 CleanConnect. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default App;
