import React, { useState, useEffect, useRef } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
  Menu,
  Edit,
  Trash2,
  Info, // Added an info icon for neutral notifications
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion"; // Import AnimatePresence
import "tailwindcss/tailwind.css";

// **THEME DEFINITION**
const theme = {
  colors: {
    primary: "blue-600",
    primaryHover: "blue-700",
    secondary: "gray-50",
    textPrimary: "gray-800",
    textSecondary: "gray-600",
    accent: "blue-400",
    success: "green-500", // Changed to green
    error: "red-500",
    warning: "yellow-500", // Added warning color
    info: "blue-500", // Added info color
    white: "white",
    gray800: "gray-800",
    light: "gray-100",
  },
  font: {
    family: "font-poppins font-sans",
    base: "text-base",
  },
  shadow: "shadow-md",
  borderRadius: "rounded-xl",
  transition: "transition duration-300 ease-in-out",
};

// **DATA**
const services = [
  {
    id: "home",
    name: "Residential Bliss Cleaning",
    icon: <Home size={28} />,
    description:
      "Transform your home into a haven of cleanliness and tranquility. Our residential cleaning service offers meticulous attention to detail, leaving your space refreshed and revitalized.",
  },
  {
    id: "office",
    name: "Commercial Sparkle Cleaning",
    icon: <Briefcase size={28} />,
    description:
      "Elevate your workspace with our comprehensive commercial cleaning. We'll ensure a spotless and hygienic environment, promoting productivity and a positive impression for clients and employees alike.",
  },
  {
    id: "deep",
    name: "Intensive Renewal Cleaning",
    icon: <SprayCan size={28} />,
    description:
      "Experience the ultimate cleanliness with our deep cleaning service. We'll tackle every nook and cranny, removing accumulated dirt and grime for a truly pristine result.",
  },
];

const prePopulatedReviews = [
  {
    id: 1,
    name: "Isaac",
    text: "Absolutely transformed my home! The attention to detail was outstanding. Highly recommend!",
    rating: 5,
  },
  {
    id: 2,
    name: "Kojo",
    text: "Professional and efficient office cleaning service. Noticeably improved our workspace environment.",
    rating: 4,
  },
  {
    id: 3,
    name: "Big Ben",
    text: "Incredible deep cleaning results! My place feels brand new. Worth every penny!",
    rating: 5,
  },
];

// **HELPER FUNCTIONS**
const getStarRating = (rating) => {
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    stars.push(
      <Star
        key={i}
        size={20}
        fill={i <= rating ? "currentColor" : "none"}
        className={`inline ${i <= rating ? "text-yellow-500" : "text-gray-400"}`}
      />
    );
  }
  return stars;
};

// **COMPONENTS**

const Toast = ({ id, message, type, onClose }) => {
  // Default to blue-500 with white text
  let bgColor = "bg-blue-500"; // Set default to blue-500
  let textColor = "text-white"; // Set default text color to white
  let icon;

  switch (type) {
    case "success":
      bgColor = "bg-green-500";
      icon = <CheckCircle size={20} aria-hidden="true" />;
      break;
    case "error":
      bgColor = "bg-red-500";
      icon = <XCircle size={20} aria-hidden="true" />;
      break;
    case "warning":
      bgColor = "bg-yellow-500";
      textColor = "text-gray-800";  // make sure the color shows
      icon = <Info size={20} aria-hidden="true" />; // You can change this to a warning icon if you have one
      break;
    case "info":
      bgColor = "bg-blue-500";
      icon = <Info size={20} aria-hidden="true" />;
      break;
    default:
      // Keep primary (blue) for default cases
      icon = <Info size={20} aria-hidden="true" />;
      break;
  }

  return (
    <motion.div
      key={id} //Important for AnimatePresence
      initial={{ opacity: 0, y: 50, scale: 0.8 }}
      animate={{ opacity: 1, y: 0, scale: 1 }}
      exit={{ opacity: 0, y: 50, scale: 0.8 }}
      transition={{ type: "spring", stiffness: 100, damping: 20 }}
      className={`p-4 rounded-md flex items-center justify-between space-x-2 ${textColor} ${bgColor} ${theme.shadow} ${theme.transition} mb-2`} // Added mb-2 for spacing
      role="alert" // Add role="alert" for accessibility
      aria-live="polite" // Make sure screen readers announce the toast
    >
      <div className="flex items-center space-x-2">
        {icon}
        <span className="text-sm">{message}</span>
      </div>
      <button onClick={onClose} className="focus:outline-none" aria-label="Close notification">
        <XCircle size={20} className="text-white opacity-70 hover:opacity-100" aria-hidden="true"/>
      </button>
    </motion.div>
  );
};

const ToastContainer = ({ toasts, setToasts }) => {
  const handleClose = (id) => {
    setToasts(prevToasts => prevToasts.filter(toast => toast.id !== id));
  };

  return (
    <div className="fixed bottom-4 right-4 z-50 flex flex-col items-end space-y-2" aria-live="polite" aria-atomic="true"> {/* Bottom Right and Flex Column */}
      <AnimatePresence>
        {toasts.map(toast => (
          <Toast
            key={toast.id}
            id={toast.id}
            message={toast.message}
            type={toast.type}
            onClose={() => handleClose(toast.id)}
          />
        ))}
      </AnimatePresence>
    </div>
  );
};

const ConfirmationModal = ({ isOpen, onClose, onConfirm, message }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-70 flex justify-center items-center z-50" aria-modal="true" role="dialog">
      <div className="bg-white p-6 rounded-md shadow-md">
        <p className="mb-4">{message}</p>
        <div className="flex justify-end">
          <button
            className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-md mr-2"
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className="px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600"
            onClick={onConfirm}
          >
            Confirm
          </button>
        </div>
      </div>
    </div>
  );
};

const Modal = ({ isOpen, onClose, children, title }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-70 flex justify-center items-center z-50" aria-modal="true" role="dialog">
      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.8, opacity: 0 }}
        className={`bg-white p-8 rounded-2xl shadow-xl w-11/12 md:w-96 relative`}
      >
        <button
          className="absolute top-4 right-4 text-gray-600 hover:text-gray-800 focus:outline-none"
          onClick={onClose}
          aria-label="Close Modal"
        >
          <XCircle size={28} aria-hidden="true"/>
        </button>
        {title && (
          <h2 className="text-2xl font-semibold mb-6 text-gray-800 text-center" id="modal-title">
            {title}
          </h2>
        )}
        <div aria-labelledby="modal-title">
            {children}
        </div>

      </motion.div>
    </div>
  );
};

const ServiceCard = ({ service }) => (
  <motion.div
    whileHover={{ scale: 1.05, translateY: -3 }}
    className={`p-6 bg-white ${theme.shadow} ${theme.borderRadius} text-center hover:shadow-lg ${theme.transition} cursor-pointer`}
  >
    <div className={`text-${theme.colors.accent} mx-auto mb-4`}>{service.icon}</div>
    <h3 className={`text-xl font-semibold mt-2 text-${theme.colors.textPrimary} mb-1`}>
      {service.name}
    </h3>
    <p className={`text-${theme.colors.textSecondary}`}>{service.description}</p>
  </motion.div>
);

const ReviewCard = ({ review, onDelete, onEdit, isAddedByUser }) => {
  return (
    <motion.div
      whileHover={{ scale: 1.03 }}
      className={`bg-white ${theme.shadow} ${theme.borderRadius} p-5 ${theme.transition}`}
    >
      <div className="flex justify-between items-center mb-3">
        <div className="text-yellow-500">{getStarRating(review.rating)}</div>
        {isAddedByUser && (
          <div>
            <button onClick={onEdit} className="text-blue-500 hover:text-blue-700 mr-2" aria-label="Edit review">
              <Edit size={20} aria-hidden="true"/>
            </button>
            <button onClick={onDelete} className="text-red-500 hover:text-red-700" aria-label="Delete review">
              <Trash2 size={20} aria-hidden="true"/>
            </button>
          </div>
        )}
      </div>
      <p className={`text-${theme.colors.textSecondary} italic mb-3`}>"{review.text}"</p>
      <p className={`text-${theme.colors.textSecondary} font-semibold`}>- {review.name}</p>
    </motion.div>
  );
};

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [userAddedReviews, setUserAddedReviews] = useState([]);
  const [review, setReview] = useState({ name: "", text: "", rating: 5 });
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });

  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isLeaveReviewModalOpen, setIsLeaveReviewModalOpen] = useState(false);
  const [isEditReviewModalOpen, setIsEditReviewModalOpen] = useState(false);
  const [reviewToEdit, setReviewToEdit] = useState(null);

  const [toasts, setToasts] = useState([]); // Use Toasts instead of single notification
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isConfirmationModalOpen, setIsConfirmationModalOpen] = useState(false);
  const [reviewToDeleteId, setReviewToDeleteId] = useState(null);

  const reviewsSectionRef = useRef(null);

  const [quoteNameError, setQuoteNameError] = useState("");
  const [quoteServiceError, setQuoteServiceError] = useState("");

  const [appointmentNameError, setAppointmentNameError] = useState("");
  const [appointmentServiceError, setAppointmentServiceError] = useState("");
  const [appointmentDateError, setAppointmentDateError] = useState("");

  const [reviewNameError, setReviewNameError] = useState("");
  const [reviewTextError, setReviewTextError] = useState("");

  // **LOCAL STORAGE HOOKS**
  useEffect(() => {
    const savedUserReviews = JSON.parse(localStorage.getItem("userReviews")) || [];
    setUserAddedReviews(savedUserReviews);
    setReviews([...prePopulatedReviews, ...savedUserReviews]);
  }, []);

  useEffect(() => {
    localStorage.setItem("userReviews", JSON.stringify(userAddedReviews));
    setReviews([...prePopulatedReviews, ...userAddedReviews]);
  }, [userAddedReviews]);

  // **HANDLERS**
  const handleReviewSubmit = () => {
    let isValid = true;
    setReviewNameError("");
    setReviewTextError("");

    if (!review.name) {
      setReviewNameError("Please enter your name.");
      isValid = false;
    }
    if (!review.text) {
      setReviewTextError("Please enter your review.");
      isValid = false;
    }

    if (!isValid) {
      return;
    }

    const newReview = { ...review, id: Date.now() };
    setUserAddedReviews([...userAddedReviews, newReview]);
    setReview({ name: "", text: "", rating: 5 });
    showNotification("Thank you for your review!", "success");
    setIsLeaveReviewModalOpen(false);
  };

  const handleQuoteSubmit = () => {
    let isValid = true;
    setQuoteNameError("");
    setQuoteServiceError("");

    if (!quote.name) {
      setQuoteNameError("Please enter your name.");
      isValid = false;
    }
    if (!quote.service) {
      setQuoteServiceError("Please select a service.");
      isValid = false;
    }

    if (!isValid) {
      return;
    }

    showNotification(
      `Your quote request for ${quote.service} has been submitted! We'll be in touch soon.`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    let isValid = true;
    setAppointmentNameError("");
    setAppointmentServiceError("");
    setAppointmentDateError("");

    if (!appointment.name) {
      setAppointmentNameError("Please enter your name.");
      isValid = false;
    }
    if (!appointment.service) {
      setAppointmentServiceError("Please select a service.");
      isValid = false;
    }
    if (!appointment.date) {
      setAppointmentDateError("Please select a date.");
      isValid = false;
    }

    if (!isValid) {
      return;
    }

    showNotification(
      `Your appointment for ${appointment.service} on ${appointment.date} is booked!`,
      "success"
    );
    setAppointment({ name: "", service: "" });
    setIsAppointmentModalOpen(false);
  };

  const showNotification = (message, type) => {
    const id = Date.now(); // Generate unique ID for each toast
    setToasts(prevToasts => [...prevToasts, { id, message, type }]);

    setTimeout(() => {
      setToasts(prevToasts => prevToasts.filter(toast => toast.id !== id));
    }, 3000); // Duration for each toast
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const scrollToReviews = () => {
    reviewsSectionRef.current?.scrollIntoView({ behavior: "smooth" });
    setIsMobileMenuOpen(false);
  };

  const handleDeleteReview = (id) => {
    setReviewToDeleteId(id);
    setIsConfirmationModalOpen(true);
  };

  const confirmDeleteReview = () => {
    const newUserAddedReviews = userAddedReviews.filter((review) => review.id !== reviewToDeleteId);
    setUserAddedReviews(newUserAddedReviews);
    setIsConfirmationModalOpen(false);
    setReviewToDeleteId(null);
    showNotification("Review deleted successfully!", "success");
  };

  const handleCancelDelete = () => {
    setIsConfirmationModalOpen(false);
    setReviewToDeleteId(null);
  };

  const handleEditReview = (review) => {
    setReviewToEdit(review);
    setReview(review);
    setIsEditReviewModalOpen(true);
  };

  const handleUpdateReview = () => {
      let isValid = true;
      setReviewNameError("");
      setReviewTextError("");

      if (!review.name) {
        setReviewNameError("Please enter your name.");
        isValid = false;
      }
      if (!review.text) {
        setReviewTextError("Please enter your review.");
        isValid = false;
      }

      if (!isValid) {
        return;
      }

    const updatedUserAddedReviews = userAddedReviews.map((r) =>
      r.id === review.id ? review : r
    );
    setUserAddedReviews(updatedUserAddedReviews);

    setReview({ name: "", text: "", rating: 5 });
    showNotification("Review updated successfully!", "success");
    setIsEditReviewModalOpen(false);
    setReviewToEdit(null);
  };

  const isReviewAddedByUser = (reviewId) => {
    return userAddedReviews.some((review) => review.id === reviewId);
  };

  const heroSectionStyle = {
    backgroundImage: `url('https://plus.unsplash.com/premium_photo-1673435846128-56687ba5959d?q=80&w=1932&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D')`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
    backgroundBlendMode: "multiply",
  };

  // **RENDER**
  return (
    <div
      className={`min-h-screen bg-${theme.colors.light} text-${theme.colors.textPrimary} ${theme.font.family} ${theme.font.base}`}
    >
      {/* Toast Container */}
      <ToastContainer toasts={toasts} setToasts={setToasts} />

      {/* Confirmation Modal */}
      <ConfirmationModal
        isOpen={isConfirmationModalOpen}
        onClose={handleCancelDelete}
        onConfirm={confirmDeleteReview}
        message="Are you sure you want to delete this review?"
      />

      {/* Header */}
      <header
        className={`bg-white text-${theme.colors.textPrimary} p-5 flex justify-between items-center ${theme.shadow} sticky top-0 z-40`}
      >
        <a href="/" className="text-2xl font-bold flex items-center">
          <svg
            className="w-8 h-8 mr-2 text-blue-400"
            fill="none"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M4.5 15.75l7.5-7.5 7.5 7.5" />
            <path d="M4.5 18.75l7.5-7.5 7.5 7.5" />
          </svg>
          CleanConnect
        </a>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden  focus:outline-none p-3 rounded hover:bg-gray-200"
          onClick={toggleMobileMenu}
          aria-label="Toggle Menu"
        >
          <Menu size={28} aria-hidden="true"/>
        </button>

        {/* Navigation Links */}
        <nav className="hidden md:block">
          <ul className="flex space-x-6">
            <li>
              <a
                href="#reviews"
                onClick={scrollToReviews}
                className={` hover:text-blue-600 ${theme.transition} focus:outline-none`}
              >
                Customer Reviews
              </a>
            </li>
            <li>
              <button
                onClick={() => setIsQuoteModalOpen(true)}
                className={` hover:text-blue-600 ${theme.transition} focus:outline-none`}
              >
                Request Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsAppointmentModalOpen(true)}
                className={` hover:text-blue-600 ${theme.transition} focus:outline-none`}
              >
                Book Appointment
              </button>
            </li>
          </ul>
        </nav>
      </header>

      {/* Mobile Menu */}
      <motion.div
        initial={{ x: "-100%" }}
        animate={{ x: isMobileMenuOpen ? "0%" : "-100%" }}
        exit={{ x: "-100%" }}
        transition={{ type: "spring", stiffness: 100, damping: 20 }}
        className={`md:hidden bg-white text-${theme.colors.textPrimary} p-6 rounded-r-2xl ${theme.shadow} fixed top-0 left-0 w-80 h-screen z-50 flex flex-col justify-start items-start space-y-4`}
      >
        <div className="flex justify-between items-center w-full mb-4">
          <span className="text-lg font-semibold">Menu</span>
          <button
            className=" focus:outline-none p-3 rounded hover:bg-gray-200"
            onClick={toggleMobileMenu}
            aria-label="Close Menu"
          >
            <XCircle size={28} aria-hidden="true"/>
          </button>
        </div>
        <nav className="w-full">
          <ul className="flex flex-col space-y-3">
            <li>
              <a
                href="#reviews"
                onClick={() => {
                  scrollToReviews();
                }}
                className="block w-full text-left text-lg font-semibold hover:bg-gray-100 p-2 rounded-md transition duration-300 focus:outline-none"
              >
                Customer Reviews
              </a>
            </li>
            <li>
              <button
                onClick={() => {
                  setIsQuoteModalOpen(true);
                  setIsMobileMenuOpen(false);
                }}
                className="block w-full text-left text-lg font-semibold hover:bg-gray-100 p-2 rounded-md transition duration-300 focus:outline-none"
              >
                Request Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => {
                  setIsAppointmentModalOpen(true);
                  setIsMobileMenuOpen(false);
                }}
                className="block w-full text-left text-lg font-semibold hover:bg-gray-100 p-2 rounded-md transition duration-300 focus:outline-none"
              >
                Book Appointment
              </button>
            </li>
          </ul>
        </nav>
      </motion.div>

      {/* Hero Section */}
      <section style={heroSectionStyle} className="text-center py-20 px-6 md:py-28 text-white">
        <h2 className="text-4xl md:text-5xl font-extrabold  mb-4">
          Experience the Joy of a Spotless Space
        </h2>
        <p className="text-xl md:text-2xl mt-5 leading-relaxed">
          We deliver unparalleled cleaning services that transform your home or office into a
          haven of cleanliness and comfort.
        </p>
        <motion.button
          whileHover={{ scale: 1.06 }}
          onClick={() => setIsQuoteModalOpen(true)}
          className={`mt-8 bg-${theme.colors.primary} text-${theme.colors.white} py-4 px-8 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold`}
        >
          Get a Free Quote
        </motion.button>
      </section>

      {/* Services Section */}
      <section className="py-16 px-6 md:px-8">
        <h2 className="text-3xl md:text-4xl font-bold text-center mb-10 text-gray-800">
          Our Specialized Services
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {services.map((service) => (
            <ServiceCard key={service.id} service={service} />
          ))}
        </div>
      </section>

      {/* Testimonials/Reviews Section */}
      <section
        ref={reviewsSectionRef}
        className="py-16 px-6 md:px-8 bg-gray-50"
        id="reviews"
      >
        <h2 className="text-3xl md:text-4xl font-bold text-center mb-10 text-gray-800">
          Rave Reviews from Our Satisfied Clients
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {reviews.map((review) => (
            <ReviewCard
              key={review.id}
              review={review}
              onDelete={() => handleDeleteReview(review.id)}
              onEdit={() => handleEditReview(review)}
              isAddedByUser={isReviewAddedByUser(review.id)}
            />
          ))}
        </div>
      </section>

      {/* Customer Review Button */}
      <section className="py-16 px-6 md:px-8 text-center">
        <motion.button
          whileHover={{ scale: 1.06 }}
          onClick={() => setIsLeaveReviewModalOpen(true)}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-4 px-8 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold`}
        >
          Share Your Experience
        </motion.button>
      </section>

      {/* Leave a Review Modal */}
      <Modal
        isOpen={isLeaveReviewModalOpen}
        onClose={() => setIsLeaveReviewModalOpen(false)}
        title="Share Your Experience"
      >
        <label htmlFor="reviewName" className="block text-gray-700 text-sm font-bold mb-2">
          Your Name:
        </label>
        <input
          type="text"
          id="reviewName"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
          aria-invalid={!!reviewNameError}
          aria-describedby="reviewName-error"
        />
        {reviewNameError && (
          <p className="text-red-500 text-sm" id="reviewName-error">{reviewNameError}</p>
        )}

        <label htmlFor="reviewText" className="block text-gray-700 text-sm font-bold mb-2">
          Your Review:
        </label>
        <textarea
          id="reviewText"
          placeholder="Write a review"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
          rows="4"
          aria-invalid={!!reviewTextError}
          aria-describedby="reviewText-error"
        />
        {reviewTextError && (
          <p className="text-red-500 text-sm" id="reviewText-error">{reviewTextError}</p>
        )}


        {/* Star Rating Input */}
        <div className="mb-5">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-2">
            Rating:
          </label>
          <div aria-label="Rating">
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-3xl focus:outline-none ${
                  star <= review.rating ? "text-yellow-500" : "text-gray-400"
                }`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
                aria-pressed={star <= review.rating}
              >
                <Star size={24} aria-hidden="true"/>
              </button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleReviewSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Submit Review
        </motion.button>
      </Modal>

      {/* Edit a Review Modal */}
      <Modal
        isOpen={isEditReviewModalOpen}
        onClose={() => setIsEditReviewModalOpen(false)}
        title="Edit Your Review"
      >
        <label htmlFor="editReviewName" className="block text-gray-700 text-sm font-bold mb-2">
          Your Name:
        </label>
        <input
          type="text"
          id="editReviewName"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
          aria-invalid={!!reviewNameError}
          aria-describedby="editReviewName-error"
        />
        {reviewNameError && (
          <p className="text-red-500 text-sm" id="editReviewName-error">{reviewNameError}</p>
        )}

        <label htmlFor="editReviewText" className="block text-gray-700 text-sm font-bold mb-2">
          Your Review:
        </label>
        <textarea
          id="editReviewText"
          placeholder="Write a review"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
          rows="4"
          aria-invalid={!!reviewTextError}
          aria-describedby="editReviewText-error"
        />
        {reviewTextError && (
          <p className="text-red-500 text-sm" id="editReviewText-error">{reviewTextError}</p>
        )}

        {/* Star Rating Input */}
        <div className="mb-5">
          <label htmlFor="editRating" className="block text-gray-700 text-sm font-bold mb-2">
            Rating:
          </label>
          <div aria-label="Rating">
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-3xl focus:outline-none ${
                  star <= review.rating ? "text-yellow-500" : "text-gray-400"
                }`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
                aria-pressed={star <= review.rating}
              >
                <Star size={24} aria-hidden="true"/>
              </button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleUpdateReview}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Update Review
        </motion.button>
      </Modal>

      {/* Request Quote Modal */}
      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Request a Personalized Quote"
      >
                <label htmlFor="quoteName" className="block text-gray-700 text-sm font-bold mb-2">
          Your Name:
        </label>
        <input
          type="text"
          id="quoteName"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
          aria-invalid={!!quoteNameError}
          aria-describedby="quoteName-error"
        />
        {quoteNameError && (
          <p className="text-red-500 text-sm" id="quoteName-error">{quoteNameError}</p>
        )}

        <label htmlFor="quoteService" className="block text-gray-700 text-sm font-bold mb-2">
          Select a service:
        </label>
        <select
          id="quoteService"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
          aria-invalid={!!quoteServiceError}
          aria-describedby="quoteService-error"
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        {quoteServiceError && (
          <p className="text-red-500 text-sm" id="quoteService-error">{quoteServiceError}</p>
        )}

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleQuoteSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Get Your Quote
        </motion.button>
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Schedule Your Cleaning Appointment"
      >
       <label htmlFor="appointmentName" className="block text-gray-700 text-sm font-bold mb-2">
          Your Name:
        </label>
        <input
          type="text"
          id="appointmentName"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
          aria-invalid={!!appointmentNameError}
          aria-describedby="appointmentName-error"
        />
        {appointmentNameError && (
          <p className="text-red-500 text-sm" id="appointmentName-error">{appointmentNameError}</p>
        )}

        <label htmlFor="appointmentService" className="block text-gray-700 text-sm font-bold mb-2">
          Select a service:
        </label>
        <select
          id="appointmentService"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
          aria-invalid={!!appointmentServiceError}
          aria-describedby="appointmentService-error"
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
         {appointmentServiceError && (
          <p className="text-red-500 text-sm" id="appointmentService-error">{appointmentServiceError}</p>
        )}

        <label htmlFor="appointmentDate" className="block text-gray-700 text-sm font-bold mb-2">
          Select a date:
        </label>
        <input
          type="date"
          id="appointmentDate"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
          aria-invalid={!!appointmentDateError}
          aria-describedby="appointmentDate-error"
        />
        {appointmentDateError && (
          <p className="text-red-500 text-sm" id="appointmentDate-error">{appointmentDateError}</p>
        )}

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleAppointmentSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Book Now
        </motion.button>
      </Modal>
    </div>
  );
};

export default App;
