import React, { useState, useCallback } from 'react';
import { motion } from 'framer-motion';

// Placeholder for theme object.  Replace with your actual theme.
const defaultTheme = {
  colors: {
    primary: 'blue-500',
    primaryHover: 'blue-700',
    white: 'white',
  },
  transition: 'transition-all duration-200', // Example transition class
};

// Placeholder for services data. Replace with your actual data.
const initialServices = [
  { id: 1, name: 'Standard Cleaning' },
  { id: 2, name: 'Deep Cleaning' },
  { id: 3, name: 'Move-In/Out Cleaning' },
];

// Placeholder Modal component (replace with your actual Modal component)
const Modal = ({ isOpen, onClose, title, onSubmit, children }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full">
      <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
        <h3 className="text-lg font-bold mb-4">{title}</h3>
        <form onSubmit={onSubmit}>
          {children}
          <div className="flex justify-end mt-4">
            <button type="button" className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded mr-2" onClick={onClose}>
              Cancel
            </button>
            {/* Submit Button is dynamically rendered within children for each modal */}
          </div>
        </form>
      </div>
    </div>
  );
};


const App = () => {
  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [appointment, setAppointment] = useState({ name: '', service: '', date: '' });
  const [errors, setErrors] = useState({});
  const [services] = useState(initialServices); // Use state for services if it can change
  const theme = defaultTheme; // Use a constant or a context provider

  // Memoize the state update function to prevent unnecessary re-renders
  const handleInputChange = useCallback((field) => (e) => {
    setAppointment(prev => ({ ...prev, [field]: e.target.value }));
  }, []);

  const validateForm = useCallback(() => {
    let newErrors = {};
    if (!appointment.name) newErrors.appointmentName = 'Name is required';
    if (!appointment.service) newErrors.appointmentService = 'Service is required';
    if (!appointment.date) newErrors.appointmentDate = 'Date is required';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }, [appointment]);

  const handleAppointmentSubmit = useCallback((e) => {
    e.preventDefault();

    if (validateForm()) {
      // Submit logic here (e.g., API call)
      console.log('Appointment submitted:', appointment);
      setIsAppointmentModalOpen(false);
      setAppointment({ name: '', service: '', date: '' }); // Reset form
      setErrors({});
    }
  }, [appointment, validateForm]);

  const handleQuoteSubmit = useCallback((e) => {
    e.preventDefault();
    // Placeholder for quote submit logic.
    console.log("Quote submitted");
    setIsQuoteModalOpen(false);
  }, []);


  return (
    <div>
      {/* Button to open the quote modal */}
      <button onClick={() => setIsQuoteModalOpen(true)}>Open Quote Modal</button>

      {/* Button to open the appointment modal */}
      <button onClick={() => setIsAppointmentModalOpen(true)}>Open Appointment Modal</button>


      {/* First Modal: Get a Quote */}
      <Modal isOpen={isQuoteModalOpen} onClose={() => setIsQuoteModalOpen(false)} title="Get a Quote" onSubmit={handleQuoteSubmit}>
        <p>Quote form content here...</p>
         <motion.button
          type="submit"
          whileHover={{ scale: 1.04 }} // Adds a slight scale animation on hover
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} ${theme.transition} w-full text-lg font-semibold`} // Applies styling using theme variables and Tailwind CSS
        >
          Get Your Quote
        </motion.button>
      </Modal>

      {/* Second Modal: Schedule a Cleaning Appointment */}
      <Modal isOpen={isAppointmentModalOpen} onClose={() => setIsAppointmentModalOpen(false)} title="Schedule Your Cleaning Appointment" onSubmit={handleAppointmentSubmit}>
        {/* Label for the name input field */}
        <label htmlFor="appointmentName" className="block text-gray-700 text-sm font-bold mb-2">
          Your Name:
        </label>
        {/* Input field for the user's name */}
        <input
          type="text" id="appointmentName" placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-1 focus:ring-2 focus:ring-blue-600" value={appointment.name}
          onChange={handleInputChange('name')} // Use memoized callback for name
        />
        {/* Displays an error message if the name field is invalid */}
        {errors.appointmentName && (<p className="text-red-500 text-sm">{errors.appointmentName}</p>)}

        {/* Label for the service selection dropdown */}
        <label htmlFor="appointmentService" className="block text-gray-700 text-sm font-bold mb-2"> Select a service: </label>
        {/* Dropdown for selecting a service */}
        <select
          id="appointmentService" className="w-full p-3 border rounded-xl mb-1 focus:ring-2 focus:ring-blue-0" value={appointment.service}
          onChange={handleInputChange('service')} // Use memoized callback for service
        >
          {/* Default disabled option */}
          <option value="" disabled> Select a service </option>
          {/* Maps through the services array to generate options */}
          {services.map((s) => (<option key={s.id} value={s.name}> {s.name} </option>))}
        </select>
        {/* Displays an error message if the service selection is invalid */}
        {errors.appointmentService && (<p className="text-red-500 text-sm">{errors.appointmentService}</p>)}

        {/* Label for the date input field */}
        <label htmlFor="appointmentDate" className="block text-gray-700 text-sm font-bold mb-2"> Select a date: </label>
        {/* Input field for selecting a date */}
        <input
          type="date" id="appointmentDate"
          className="w-full p-3 border rounded-xl mb-1 focus:ring-2 focus:ring-blue-600" value={appointment.date}
          onChange={handleInputChange('date')} // Use memoized callback for date
        />
        {/* Displays an error message if the date selection is invalid */}
        {errors.appointmentDate && (<p className="text-red-500 text-sm">{errors.appointmentDate}</p>)}

        {/* Submit button for booking the appointment */}
        <motion.button
          type="submit"
          whileHover={{ scale: 1.04 }} // Adds a slight scale animation on hover
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} ${theme.transition} w-full text-lg font-semibold`} // Applies styling using theme variables and Tailwind CSS
        >
          Book Now
        </motion.button>
      </Modal>
    </div>
  );
};

export default App;
