import React, { useState, useEffect, useRef } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
  Menu,
  ArrowRight, // Icon for call to action buttons
} from "lucide-react";
import "tailwindcss/tailwind.css";

// **THEME (Enhanced for Aesthetics)**
const theme = {
  colors: {
    primary: "emerald-500",       // A fresh, modern primary color
    primaryHover: "emerald-600",
    secondary: "white",           // Clean white background
    secondaryAccent: "gray-50",   // Subtle accent background
    textPrimary: "gray-800",      // Slightly lighter primary text
    textSecondary: "gray-600",    // Softer secondary text
    accent: "emerald-400",        // Brighter accent color for highlights
    success: "green-400",
    error: "red-400",
    white: "white",
    gray800: "gray-800",
    // Gradients for Hero Section
    heroGradientFrom: "emerald-100",
    heroGradientTo: "white",
  },
  font: {
    family: "font-sans", // Removed 'Inter' - let system font shine
    base: "text-base",
    heading: "font-semibold",  // Use for titles
  },
  shadow: "shadow-xl",  // Stronger, more modern shadow
  borderRadius: "rounded-xl", // Softer rounded corners
  transition: "transition-all duration-300 ease-in-out", // Smoother transitions
  // Custom Classes (defined below)
  button: "px-6 py-3 rounded-xl font-semibold text-white focus:outline-none focus:ring-2 focus:ring-emerald-200",
};

// **DATA**
const services = [
  {
    id: "home",
    name: "Home Cleaning",
    icon: <Home size={32} className={`text-${theme.colors.accent}`} />, // Larger icons
    description: "Transform your living space into a haven of freshness.",
  },
  {
    id: "office",
    name: "Office Cleaning",
    icon: <Briefcase size={32} className={`text-${theme.colors.accent}`} />,
    description: "Boost productivity with a spotless and organized workspace.",
  },
  {
    id: "deep",
    name: "Deep Cleaning",
    icon: <SprayCan size={32} className={`text-${theme.colors.accent}`} />,
    description: "Experience a thorough clean that goes beyond the surface.",
  },
];

const prePopulatedReviews = [
  {
    name: "Isaac",
    text: "The team's attention to detail is remarkable! My home feels brand new.",
    rating: 5,
  },
  {
    name: "Kojo",
    text: "Professional, reliable, and they always leave my office sparkling!",
    rating: 4,
  },
  { name: "Big Ben", text: "Incredible deep cleaning service. Highly recommended!", rating: 5 },
];

// **HELPER FUNCTIONS**
const getStarRating = (rating) => {
  const stars = [];
  for (let i = 0; i < 5; i++) {
    stars.push(
      <Star
        key={i}
        size={20} // Slightly larger stars
        className={`inline ${i < rating ? "text-yellow-500" : "text-gray-300"}`}
      />
    );
  }
  return stars;
};

// **COMPONENTS (Styled with Prettier Aesthetics)**

const Notification = ({ message, type }) => (
  <div
    className={`fixed top-6 right-6 p-4 ${theme.borderRadius} flex items-center space-x-3 text-white ${
      type === "success" ? `bg-${theme.colors.success}` : `bg-${theme.colors.error}`
    } ${theme.shadow} z-50 animate-slide-in-right`} // Added animation
  >
    <CheckCircle size={20} />
    <span>{message}</span>
  </div>
);

const Modal = ({ isOpen, onClose, children, title }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-900 bg-opacity-60 flex justify-center items-center z-50"> {/* Darker backdrop */}
      <div className={`bg-${theme.colors.secondary} p-8 ${theme.borderRadius} ${theme.shadow} w-11/12 md:w-96 relative animate-fade-in`}>
        <button
          className="absolute top-4 right-4 text-gray-500 hover:text-gray-700"
          onClick={onClose}
          aria-label="Close Modal"
        >
          <XCircle size={24} />
        </button>
        {title && <h2 className={`text-2xl ${theme.font.heading} mb-6 text-${theme.colors.textPrimary}`}>{title}</h2>}
        {children}
      </div>
    </div>
  );
};

const ServiceCard = ({ service }) => (
  <div
    className={`p-6 bg-${theme.colors.secondary} ${theme.shadow} ${theme.borderRadius} text-center hover:shadow-2xl hover:scale-105 ${theme.transition} border border-gray-100`} // Added hover effects and border
  >
    <div className="flex items-center justify-center h-16 mx-auto mb-4"> {/* Centered Icon */}
      {service.icon}
    </div>
    <h3 className={`text-xl ${theme.font.heading} mt-2 text-${theme.colors.textPrimary}`}>{service.name}</h3>
    <p className={`text-${theme.colors.textSecondary} mt-3`}>{service.description}</p>
  </div>
);

const ReviewCard = ({ review }) => (
  <div className={`bg-${theme.colors.secondaryAccent} ${theme.shadow} ${theme.borderRadius} p-6 border border-gray-100`}> {/* Soft background */}
    <div className="text-yellow-500 mb-3">{getStarRating(review.rating)}</div>
    <p className={`text-${theme.colors.textPrimary} italic mb-4`}>"{review.text}"</p>
    <p className={`text-${theme.colors.textSecondary} font-medium`}>- {review.name}</p>
  </div>
);

// **MAIN COMPONENT**

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [review, setReview] = useState({ name: "", text: "", rating: 5 });
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });

  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isLeaveReviewModalOpen, setIsLeaveReviewModalOpen] = useState(false);

  const [notification, setNotification] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const reviewsSectionRef = useRef(null);

  // **LOCAL STORAGE HOOKS**
  useEffect(() => {
    const savedReviews = JSON.parse(localStorage.getItem("reviews")) || [];
    setReviews(savedReviews.length > 0 ? savedReviews : prePopulatedReviews);
  }, []);

  useEffect(() => {
    localStorage.setItem("reviews", JSON.stringify(reviews));
  }, [reviews]);

  // **HANDLERS**
  const handleReviewSubmit = () => {
    if (!review.name || !review.text) {
      showNotification("Please fill in all fields!", "error");
      return;
    }
    setReviews([...reviews, review]);
    setReview({ name: "", text: "", rating: 5 });
    showNotification("Review submitted successfully!", "success");
    setIsLeaveReviewModalOpen(false);
  };

  const handleQuoteSubmit = () => {
    if (!quote.name || !quote.service) {
      showNotification("Please enter your name and select a service!", "error");
      return;
    }
    showNotification(
      `Quote request for ${quote.service} submitted!`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    if (!appointment.name || !appointment.service || !appointment.date) {
      showNotification("Please fill in all fields!", "error");
      return;
    }
    showNotification(
      `Appointment booked for ${appointment.service} on ${appointment.date}!`,
      "success"
    );
    setAppointment({ name: "", service: "", date: "" });
    setIsAppointmentModalOpen(false);
  };

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const scrollToReviews = () => {
    reviewsSectionRef.current?.scrollIntoView({ behavior: "smooth" });
    setIsMobileMenuOpen(false);
  };

  // **RENDER**
  return (
    <div className={`bg-${theme.colors.secondary} text-${theme.colors.textPrimary} ${theme.font.family} ${theme.font.base} antialiased`}> {/* Added antialiasing */}

      {/* Notification */}
      {notification && <Notification message={notification.message} type={notification.type} />}

      {/* Header */}
      <header className="bg-white border-b border-gray-100 py-4"> {/* White header */}
        <div className="container mx-auto px-4 flex justify-between items-center">
          <h1 className={`text-2xl ${theme.font.heading} text-${theme.colors.textPrimary}`}>CleanConnect</h1>

          {/* Mobile Menu Button */}
          <button
            className="md:hidden text-gray-600 hover:text-gray-800 focus:outline-none p-2"
            onClick={toggleMobileMenu}
            aria-label="Toggle Menu"
          >
            <Menu size={28} /> {/* Slightly larger mobile menu icon */}
          </button>

          {/* Navigation Links */}
          <nav className="hidden md:block">
            <ul className="flex space-x-6">
              <li>
                <button
                  onClick={scrollToReviews}
                  className={`text-${theme.colors.textSecondary} hover:text-${theme.colors.textPrimary} ${theme.transition}`}
                >
                  Customer Reviews
                </button>
              </li>
              <li>
                <button
                  onClick={() => setIsQuoteModalOpen(true)}
                  className={`text-${theme.colors.textSecondary} hover:text-${theme.colors.textPrimary} ${theme.transition}`}
                >
                  Request Quote
                </button>
              </li>
              <li>
                <button
                  onClick={() => setIsAppointmentModalOpen(true)}
                  className={`text-${theme.colors.textSecondary} hover:text-${theme.colors.textPrimary} ${theme.transition}`}
                >
                  Book Appointment
                </button>
              </li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Mobile Menu */}
      <div
        className={`md:hidden bg-white ${theme.shadow} border-b border-gray-100 p-4 ${
          isMobileMenuOpen ? "block" : "hidden"
        } animate-slide-down`} // Added animation
      >
        <ul className="flex flex-col space-y-3">
          <li>
            <button
              onClick={() => {
                scrollToReviews();
              }}
              className={`block w-full text-left hover:bg-gray-50 p-3 ${theme.borderRadius} ${theme.transition}`}
            >
              Customer Reviews
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsQuoteModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className={`block w-full text-left hover:bg-gray-50 p-3 ${theme.borderRadius} ${theme.transition}`}
            >
              Request Quote
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsAppointmentModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className={`block w-full text-left hover:bg-gray-50 p-3 ${theme.borderRadius} ${theme.transition}`}
            >
              Book Appointment
            </button>
          </li>
        </ul>
      </div>

      {/* Hero Section */}
      <section className={`py-20 px-4 md:py-24 text-center bg-gradient-to-b from-${theme.colors.heroGradientFrom} to-${theme.colors.heroGradientTo}`}>
        <div className="container mx-auto">
          <h2 className={`text-4xl md:text-5xl ${theme.font.heading} text-${theme.colors.textPrimary} mb-6`}>
            Experience the Joy of a Spotless Home
          </h2>
          <p className={`text-lg md:text-xl text-${theme.colors.textSecondary} mb-8`}>
            We deliver exceptional cleaning services with a smile.
          </p>
          <button
            onClick={() => setIsQuoteModalOpen(true)}
            className={`${theme.button} bg-${theme.colors.primary} hover:bg-${theme.colors.primaryHover} ${theme.shadow} inline-flex items-center space-x-2`} // Added inline-flex
          >
            <span>Get a Free Quote</span>
            <ArrowRight size={20} />
          </button>
        </div>
      </section>

      {/* Services Section */}
      <section className="py-16 px-4 md:px-6 bg-gray-50">
        <div className="container mx-auto">
          <h2 className={`text-3xl ${theme.font.heading} text-center mb-12 text-${theme.colors.textPrimary}`}>
            Explore Our Cleaning Services
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {services.map((service) => (
              <ServiceCard key={service.id} service={service} />
            ))}
          </div>
        </div>
      </section>

      {/* Testimonials/Reviews Section */}
      <section ref={reviewsSectionRef} className="py-16 px-4 md:px-6">
        <div className="container mx-auto">
          <h2 className={`text-3xl ${theme.font.heading} text-center mb-12 text-${theme.colors.textPrimary}`}>
            What Our Clients Are Saying
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {reviews.map((review, index) => (
              <ReviewCard key={index} review={review} />
            ))}
          </div>
        </div>
      </section>

      {/* Customer Review Button */}
      <section className="py-16 px-4 md:px-6 bg-gray-50">
        <div className="container mx-auto text-center">
          <button
            onClick={() => setIsLeaveReviewModalOpen(true)}
            className={`${theme.button} bg-${theme.colors.primary} hover:bg-${theme.colors.primaryHover} ${theme.shadow} inline-flex items-center space-x-2`}
          >
            <span>Leave a Review</span>
            <ArrowRight size={20} />
          </button>
        </div>
      </section>

      {/* Footer */}
      <footer className={`bg-gray-700 text-${theme.colors.white} text-center py-8`}>
        <p>© 2024 CleanConnect. All rights reserved.</p>
      </footer>

      {/* Leave a Review Modal */}
      <Modal
        isOpen={isLeaveReviewModalOpen}
        onClose={() => setIsLeaveReviewModalOpen(false)}
        title="Share Your Experience"
      >
        <label htmlFor="name" className="block text-gray-700 text-sm font-bold mb-2">Your Name:</label>
        <input
          type="text"
          id="name"
          placeholder="Enter your name"
          className="w-full p-3 border border-gray-300 rounded-md mb-4 focus:outline-none focus:ring-2 focus:ring-emerald-200"
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
        />

        <label htmlFor="reviewText" className="block text-gray-700 text-sm font-bold mb-2">Your Review:</label>
        <textarea
          id="reviewText"
          placeholder="Write your review here..."
          className="w-full p-3 border border-gray-300 rounded-md mb-4 focus:outline-none focus:ring-2 focus:ring-emerald-200"
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
        />

        {/* Star Rating Input */}
        <div className="mb-4">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-2">Rating:</label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-3xl ${star <= review.rating ? 'text-yellow-500' : 'text-gray-300'} hover:text-yellow-400 transition-colors duration-200`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
              >
                <Star size={24} />
              </button>
            ))}
          </div>
        </div>

        <button
          onClick={handleReviewSubmit}
          className={`${theme.button} bg-${theme.colors.primary} hover:bg-${theme.colors.primaryHover} ${theme.shadow} w-full`}
        >
          Submit Review
        </button>
      </Modal>

      {/* Request Quote Modal */}
      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Get a Free Quote"
      >
        <label htmlFor="quoteName" className="block text-gray-700 text-sm font-bold mb-2">Your Name:</label>
        <input
          type="text"
          id="quoteName"
          placeholder="Enter your name"
          className="w-full p-3 border border-gray-300 rounded-md mb-4 focus:outline-none focus:ring-2 focus:ring-emerald-200"
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
        />

        <label htmlFor="quoteService" className="block text-gray-700 text-sm font-bold mb-2">Service Needed:</label>
        <select
          id="quoteService"
          className="w-full p-3 border border-gray-300 rounded-md mb-4 focus:outline-none focus:ring-2 focus:ring-emerald-200"
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <button
          onClick={handleQuoteSubmit}
          className={`${theme.button} bg-${theme.colors.primary} hover:bg-${theme.colors.primaryHover} ${theme.shadow} w-full`}
        >
          Submit Quote
        </button>
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Schedule a Cleaning"
      >
        <label htmlFor="appointmentName" className="block text-gray-700 text-sm font-bold mb-2">Your Name:</label>
        <input
          type="text"
          id="appointmentName"
          placeholder="Enter your name"
          className="w-full p-3 border border-gray-300 rounded-md mb-4 focus:outline-none focus:ring-2 focus:ring-emerald-200"
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
        />

        <label htmlFor="appointmentService" className="block text-gray-700 text-sm font-bold mb-2">Service Needed:</label>
        <select
          id="appointmentService"
          className="w-full p-3 border border-gray-300 rounded-md mb-4 focus:outline-none focus:ring-2 focus:ring-emerald-200"
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>

        <label htmlFor="appointmentDate" className="block text-gray-700 text-sm font-bold mb-2">Preferred Date:</label>
        <input
          type="date"
          id="appointmentDate"
          className="w-full p-3 border border-gray-300 rounded-md mb-4 focus:outline-none focus:ring-2 focus:ring-emerald-200"
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
        />

        <button
          onClick={handleAppointmentSubmit}
          className={`${theme.button} bg-${theme.colors.primary} hover:bg-${theme.colors.primaryHover} ${theme.shadow} w-full`}
        >
          Book Now
        </button>
      </Modal>
    </div>
  );
};

export default App;
