import React, { useState } from 'react';
import { motion } from 'framer-motion';

const App = () => {
  // Existing state and functions (example)
  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [quote, setQuote] = useState({ name: '', service: '' });
  const [appointment, setAppointment] = useState({ name: '', service: '', date: '' });
  const [services, setServices] = useState([{ id: 1, name: "Service 1" }, { id: 2, name: "Service 2" }]); // example
  const [quoteNameError, setQuoteNameError] = useState('');
  const [quoteServiceError, setQuoteServiceError] = useState('');
  const [appointmentNameError, setAppointmentNameError] = useState('');
  const [appointmentServiceError, setAppointmentServiceError] = useState('');
  const [appointmentDateError, setAppointmentDateError] = useState('');
  const theme = { colors: { primary: 'blue-500', primaryHover: 'blue-700', accent: 'blue-200', white: 'white' }, transition: 'transition-all duration-200' };

  const handleQuoteSubmit = () => {
    // example submit
    console.log("quote sumbitted")
  }

  const handleAppointmentSubmit = () => {
      // example submit
      console.log("quote sumbitted")
  }

  // New State for Review Modal & Rating
  const [isReviewModalOpen, setIsReviewModalOpen] = useState(false);
  const [rating, setRating] = useState(0);
  const [reviewText, setReviewText] = useState('');
  const [ratingError, setRatingError] = useState('');

    const handleReviewSubmit = () => {
        if (rating === 0) {
            setRatingError('Please provide a rating.');
            return;
        }

        // Submit the review (e.g., to an API)
        console.log('Review submitted:', { rating, reviewText });

        // Close the modal and reset state
        setIsReviewModalOpen(false);
        setRating(0);
        setReviewText('');
        setRatingError('');
    };

  const Star = ({ filled, onClick, onKeyDown, index }) => {
    const starColor = filled ? 'text-yellow-500' : 'text-gray-400';

    return (
      <button
        onClick={onClick}
        onKeyDown={onKeyDown}
        role="button"
        aria-label={`Rate ${index + 1} stars`}
        tabIndex="0"
        className={`focus:outline-none ${starColor}`}
      >
        <svg
          className="w-6 h-6"
          fill="currentColor"
          viewBox="0 0 20 20"
        >
          <path
            d="M10 0 L13.09 6.26 L20 7.26 L15 12.14 L16.18 19 L10 15.77 L3.82 19 L5 12.14 L0 7.26 L6.91 6.26 L10 0 Z"
          />
        </svg>
      </button>
    );
  };

  const renderStars = () => {
    const stars = [];
    for (let i = 0; i < 5; i++) {
      stars.push(
        <Star
          key={i}
          index={i}
          filled={i < rating}
          onClick={() => setRating(i + 1)}
          onKeyDown={(e) => {
            if (e.key === 'Enter' || e.key === ' ') {
              setRating(i + 1);
              e.preventDefault(); // Prevent scrolling
            }
          }}
        />
      );
    }
    return stars;
  };

  return (
    <div className="container mx-auto py-8">
      {/* Button to Open Review Modal */}
      <button
        onClick={() => setIsReviewModalOpen(true)}
        className="bg-green-500 text-white py-2 px-4 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-400"
      >
        Leave a Review
      </button>

      {/* Review Modal */}
      <Modal
        isOpen={isReviewModalOpen}
        onClose={() => {
          setIsReviewModalOpen(false);
          setRating(0); // Reset rating when closing
          setRatingError(''); // Clear any previous error
        }}
        title="Leave a Review"
      >
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Rating:
          </label>
          <div className="flex items-center">
            {renderStars()}
          </div>
          {ratingError && (
            <p className="text-red-500 text-sm">{ratingError}</p>
          )}
        </div>

        <div className="mb-4">
          <label htmlFor="reviewText" className="block text-gray-700 text-sm font-bold mb-2">
            Review:
          </label>
          <textarea
            id="reviewText"
            className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
            placeholder="Write your review here..."
            value={reviewText}
            onChange={(e) => setReviewText(e.target.value)}
          />
        </div>

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleReviewSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Submit Review
        </motion.button>
      </Modal>

      {/* Existing Modals (Quote & Appointment) - Keep these as they are */}
      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Request a Personalized Quote"
      >
        <label htmlFor="quoteName" className="block text-gray-700 text-sm font-bold mb-2">
          Your Name:
        </label>
        <input
          type="text"
          id="quoteName"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
          aria-invalid={!!quoteNameError}
          aria-describedby="quoteName-error"
        />
        {quoteNameError && (
          <p className="text-red-500 text-sm" id="quoteName-error">{quoteNameError}</p>
        )}

        <label htmlFor="quoteService" className="block text-gray-700 text-sm font-bold mb-2">
          Select a service:
        </label>
        <select
          id="quoteService"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
          aria-invalid={!!quoteServiceError}
          aria-describedby="quoteService-error"
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        {quoteServiceError && (
          <p className="text-red-500 text-sm" id="quoteService-error">{quoteServiceError}</p>
        )}

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleQuoteSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Get Your Quote
        </motion.button>
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Schedule Your Cleaning Appointment"
      >
       <label htmlFor="appointmentName" className="block text-gray-700 text-sm font-bold mb-2">
          Your Name:
        </label>
        <input
          type="text"
          id="appointmentName"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
          aria-invalid={!!appointmentNameError}
          aria-describedby="appointmentName-error"
        />
        {appointmentNameError && (
          <p className="text-red-500 text-sm" id="appointmentName-error">{appointmentNameError}</p>
        )}

        <label htmlFor="appointmentService" className="block text-gray-700 text-sm font-bold mb-2">
          Select a service:
        </label>
        <select
          id="appointmentService"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
          aria-invalid={!!appointmentServiceError}
          aria-describedby="appointmentService-error"
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
         {appointmentServiceError && (
          <p className="text-red-500 text-sm" id="appointmentService-error">{appointmentServiceError}</p>
        )}

        <label htmlFor="appointmentDate" className="block text-gray-700 text-sm font-bold mb-2">
          Select a date:
        </label>
        <input
          type="date"
          id="appointmentDate"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
          aria-invalid={!!appointmentDateError}
          aria-describedby="appointmentDate-error"
        />
        {appointmentDateError && (
          <p className="text-red-500 text-sm" id="appointmentDate-error">{appointmentDateError}</p>
        )}

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleAppointmentSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Book Now
        </motion.button>
      </Modal>
    </div>
  );
};

