import React, { useState, useEffect, useRef } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
  Menu,
  Edit,
  Trash2,
} from "lucide-react";
import { motion } from "framer-motion";
import "tailwindcss/tailwind.css";

// **THEME DEFINITION**
const theme = {
  colors: {
    primary: "blue-600",
    primaryHover: "blue-700",
    secondary: "gray-50",
    textPrimary: "gray-800",
    textSecondary: "gray-600",
    accent: "blue-400",
    success: "blue-500",
    error: "red-500",
    warning: "orange-500", // Added warning color
    white: "white",
    gray800: "gray-800",
    light: "gray-100",
  },
  font: {
    family: "font-poppins font-sans",
    base: "text-base",
  },
  shadow: "shadow-md",
  borderRadius: "rounded-xl",
  transition: "transition duration-300 ease-in-out",
};

// **DATA**
const services = [
  {
    id: "home",
    name: "Residential Bliss Cleaning",
    icon: <Home size={28} />,
    description:
      "Transform your home into a haven of cleanliness and tranquility. Our residential cleaning service offers meticulous attention to detail, leaving your space refreshed and revitalized.",
  },
  {
    id: "office",
    name: "Commercial Sparkle Cleaning",
    icon: <Briefcase size={28} />,
    description:
      "Elevate your workspace with our comprehensive commercial cleaning. We'll ensure a spotless and hygienic environment, promoting productivity and a positive impression for clients and employees alike.",
  },
  {
    id: "deep",
    name: "Intensive Renewal Cleaning",
    icon: <SprayCan size={28} />,
    description:
      "Experience the ultimate cleanliness with our deep cleaning service. We'll tackle every nook and cranny, removing accumulated dirt and grime for a truly pristine result.",
  },
];

const prePopulatedReviews = [
  {
    id: 1,
    name: "Isaac",
    text: "Absolutely transformed my home! The attention to detail was outstanding. Highly recommend!",
    rating: 5,
  },
  {
    id: 2,
    name: "Kojo",
    text: "Professional and efficient office cleaning service. Noticeably improved our workspace environment.",
    rating: 4,
  },
  {
    id: 3,
    name: "Big Ben",
    text: "Incredible deep cleaning results! My place feels brand new. Worth every penny!",
    rating: 5,
  },
];

// **HELPER FUNCTIONS**
const getStarRating = (rating) => {
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    stars.push(
      <Star
        key={i}
        size={20}
        fill={i <= rating ? "currentColor" : "none"}
        className={`inline ${i <= rating ? "text-yellow-500" : "text-gray-400"}`}
      />
    );
  }
  return stars;
};

// **COMPONENTS**

const Notification = ({ message, type, onClose }) => {
  let bgColor;
  let textColor = "white"; //Default text color

  switch (type) {
    case "success":
      bgColor = "blue-500";
      break;
    case "error":
      bgColor = "red-500";
      break;
    case "warning":
      bgColor = "orange-500";
      textColor = "gray-800"; //For better contrast with orange
      break;
    default:
      bgColor = "gray-500";
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      className={`fixed top-4 right-4 md:top-16 md:right-6 p-4 rounded-xl flex items-center justify-between space-x-3 text-${textColor} bg-${bgColor} ${theme.shadow} ${theme.transition} w-full sm:w-auto`}
    >
      <span>{message}</span>
      <button onClick={onClose} className="focus:outline-none">
        <XCircle size={20} color={textColor} />
      </button>
    </motion.div>
  );
};

const ConfirmationModal = ({ isOpen, onClose, onConfirm, message }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-70 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-md shadow-md">
        <p className="mb-4">{message}</p>
        <div className="flex justify-end">
          <button
            className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-md mr-2"
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className="px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600"
            onClick={onConfirm}
          >
            Confirm
          </button>
        </div>
      </div>
    </div>
  );
};

const Modal = ({ isOpen, onClose, children, title }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-70 flex justify-center items-center z-50">
      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.8, opacity: 0 }}
        className={`bg-white p-8 rounded-2xl shadow-xl w-11/12 md:w-96 relative`}
      >
        <button
          className="absolute top-4 right-4 text-gray-600 hover:text-gray-800 focus:outline-none"
          onClick={onClose}
          aria-label="Close Modal"
        >
          <XCircle size={28} />
        </button>
        {title && (
          <h2 className="text-2xl font-semibold mb-6 text-gray-800 text-center">
            {title}
          </h2>
        )}
        {children}
      </motion.div>
    </div>
  );
};

const ServiceCard = ({ service }) => (
  <motion.div
    whileHover={{ scale: 1.05, translateY: -3 }}
    className={`p-6 bg-white ${theme.shadow} ${theme.borderRadius} text-center hover:shadow-lg ${theme.transition} cursor-pointer`}
  >
    <div className={`text-${theme.colors.accent} mx-auto mb-4`}>{service.icon}</div>
    <h3 className={`text-xl font-semibold mt-2 text-${theme.colors.textPrimary} mb-1`}>
      {service.name}
    </h3>
    <p className={`text-${theme.colors.textSecondary}`}>{service.description}</p>
  </motion.div>
);

const ReviewCard = ({ review, onDelete, onEdit, isAddedByUser }) => {
  return (
    <motion.div
      whileHover={{ scale: 1.03 }}
      className={`bg-white ${theme.shadow} ${theme.borderRadius} p-5 ${theme.transition}`}
    >
      <div className="flex justify-between items-center mb-3">
        <div className="text-yellow-500">{getStarRating(review.rating)}</div>
        {isAddedByUser && (
          <div>
            <button onClick={onEdit} className="text-blue-500 hover:text-blue-700 mr-2">
              <Edit size={20} />
            </button>
            <button onClick={onDelete} className="text-red-500 hover:text-red-700">
              <Trash2 size={20} />
            </button>
          </div>
        )}
      </div>
      <p className={`text-${theme.colors.textSecondary} italic mb-3`}>"{review.text}"</p>
      <p className={`text-${theme.colors.textSecondary} font-semibold`}>- {review.name}</p>
    </motion.div>
  );
};

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [userAddedReviews, setUserAddedReviews] = useState([]);
  const [review, setReview] = useState({ name: "", text: "", rating: 5 });
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });

  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isLeaveReviewModalOpen, setIsLeaveReviewModalOpen] = useState(false);
  const [isEditReviewModalOpen, setIsEditReviewModalOpen] = useState(false);
  const [reviewToEdit, setReviewToEdit] = useState(null);

  const [notification, setNotification] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isConfirmationModalOpen, setIsConfirmationModalOpen] = useState(false);
  const [reviewToDeleteId, setReviewToDeleteId] = useState(null);

  const reviewsSectionRef = useRef(null);

  // **LOCAL STORAGE HOOKS**
  useEffect(() => {
    const savedUserReviews = JSON.parse(localStorage.getItem("userReviews")) || [];
    setUserAddedReviews(savedUserReviews);
    setReviews([...prePopulatedReviews, ...savedUserReviews]);
  }, []);

  useEffect(() => {
    localStorage.setItem("userReviews", JSON.stringify(userAddedReviews));
    setReviews([...prePopulatedReviews, ...userAddedReviews]);
  }, [userAddedReviews]);

  // **HANDLERS**
  const handleReviewSubmit = () => {
    if (!review.name || !review.text) {
      showNotification("Please provide your name and review text!", "error");
      return;
    }
    const newReview = { ...review, id: Date.now() };
    setUserAddedReviews([...userAddedReviews, newReview]);
    setReview({ name: "", text: "", rating: 5 });
    showNotification("Thank you for your review!", "success");
    setIsLeaveReviewModalOpen(false);
  };

  const handleQuoteSubmit = () => {
    if (!quote.name || !quote.service) {
      showNotification("Please enter your name and select a service!", "error");
      return;
    }
    showNotification(
      `Your quote request for ${quote.service} has been submitted! We'll be in touch soon.`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    if (!appointment.name || !appointment.service || !appointment.date) {
      showNotification("Please fill in all fields to book your appointment!", "error");
      return;
    }
    showNotification(
      `Your appointment for ${appointment.service} on ${appointment.date} is booked!`,
      "success"
    );
    setAppointment({ name: "", service: "" });
    setIsAppointmentModalOpen(false);
  };

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 4000);
  };

  const closeNotification = () => {
    setNotification(null);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const scrollToReviews = () => {
    reviewsSectionRef.current?.scrollIntoView({ behavior: "smooth" });
    setIsMobileMenuOpen(false);
  };

  const handleDeleteReview = (id) => {
    setReviewToDeleteId(id);
    setIsConfirmationModalOpen(true);
  };

  const confirmDeleteReview = () => {
    const newUserAddedReviews = userAddedReviews.filter((review) => review.id !== reviewToDeleteId);
    setUserAddedReviews(newUserAddedReviews);
    setIsConfirmationModalOpen(false);
    setReviewToDeleteId(null);
    showNotification("Review deleted successfully!", "success");
  };

  const handleCancelDelete = () => {
    setIsConfirmationModalOpen(false);
    setReviewToDeleteId(null);
  };

  const handleEditReview = (review) => {
    setReviewToEdit(review);
    setReview(review);
    setIsEditReviewModalOpen(true);
  };

  const handleUpdateReview = () => {
    if (!review.name || !review.text) {
      showNotification("Please provide your name and review text!", "error");
      return;
    }

    const updatedUserAddedReviews = userAddedReviews.map((r) =>
      r.id === review.id ? review : r
    );
    setUserAddedReviews(updatedUserAddedReviews);

    setReview({ name: "", text: "", rating: 5 });
    showNotification("Review updated successfully!", "success");
    setIsEditReviewModalOpen(false);
    setReviewToEdit(null);
  };

  const isReviewAddedByUser = (reviewId) => {
    return userAddedReviews.some((review) => review.id === reviewId);
  };

  const heroSectionStyle = {
    backgroundImage: `url('https://plus.unsplash.com/premium_photo-1673435846128-56687ba5959d?q=80&w=1932&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D')`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
    backgroundBlendMode: "multiply",
  };

  const ReviewButton = () => (
    <motion.a
      whileHover={{ scale: 1.06 }}
      href="#leave-review"
      className={`inline-block bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold`}
    >
      Share Your Experience
    </motion.a>
  );

  // **RENDER**
  return (
    <div
      className={`min-h-screen bg-${theme.colors.light} text-${theme.colors.textPrimary} ${theme.font.family} ${theme.font.base}`}
    >
      {/* Notification */}
      {notification && (
        <Notification
          message={notification.message}
          type={notification.type}
          onClose={closeNotification}
        />
      )}

      {/* Confirmation Modal */}
      <ConfirmationModal
        isOpen={isConfirmationModalOpen}
        onClose={handleCancelDelete}
        onConfirm={confirmDeleteReview}
        message="Are you sure you want to delete this review?"
      />

      {/* Header */}
      <header
        className={`bg-white text-${theme.colors.textPrimary} p-5 flex justify-between items-center ${theme.shadow} sticky top-0 z-40`}
      >
        <a href="/" className="text-2xl font-bold flex items-center">
          <svg
            className="w-8 h-8 mr-2 text-blue-400"
            fill="none"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M4.5 15.75l7.5-7.5 7.5 7.5" />
            <path d="M4.5 18.75l7.5-7.5 7.5 7.5" />
          </svg>
          CleanConnect
        </a>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden  focus:outline-none p-3 rounded hover:bg-gray-200"
          onClick={toggleMobileMenu}
          aria-label="Toggle Menu"
        >
          <Menu size={28} />
        </button>

        {/* Navigation Links */}
        <nav className="hidden md:block">
          <ul className="flex space-x-6">
            <li>
              <a
                href="#reviews"
                onClick={scrollToReviews}
                className={` hover:text-blue-600 ${theme.transition} focus:outline-none`}
              >
                Customer Reviews
              </a>
            </li>
            <li>
              <button
                onClick={() => setIsQuoteModalOpen(true)}
                className={` hover:text-blue-600 ${theme.transition} focus:outline-none`}
              >
                Request Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsAppointmentModalOpen(true)}
                className={` hover:text-blue-600 ${theme.transition} focus:outline-none`}
              >
                Book Appointment
              </button>
            </li>
          </ul>
        </nav>
      </header>

      {/* Mobile Menu */}
      <motion.div
        initial={{ x: "-100%" }}
        animate={{ x: isMobileMenuOpen ? "0%" : "-100%" }}
        exit={{ x: "-100%" }}
        transition={{ type: "spring", stiffness: 100, damping: 20 }}
        className={`md:hidden bg-white text-${theme.colors.textPrimary} p-6 rounded-r-2xl ${theme.shadow} fixed top-0 left-0 w-80 h-screen z-50 flex flex-col justify-start items-start space-y-4`}
      >
        <div className="flex justify-between items-center w-full mb-4">
          <span className="text-lg font-semibold">Menu</span>
          <button
            className=" focus:outline-none p-3 rounded hover:bg-gray-200"
            onClick={toggleMobileMenu}
            aria-label="Close Menu"
          >
            <XCircle size={28} />
          </button>
        </div>
        <nav className="w-full">
          <ul className="flex flex-col space-y-3">
            <li>
              <a
                href="#reviews"
                onClick={() => {
                  scrollToReviews();
                }}
                className="block w-full text-left text-lg font-semibold hover:bg-gray-100 p-2 rounded-md transition duration-300 focus:outline-none"
              >
                Customer Reviews
              </a>
            </li>
            <li>
              <button
                onClick={() => {
                  setIsQuoteModalOpen(true);
                  setIsMobileMenuOpen(false);
                }}
                className="block w-full text-left text-lg font-semibold hover:bg-gray-100 p-2 rounded-md transition duration-300 focus:outline-none"
              >
                Request Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => {
                  setIsAppointmentModalOpen(true);
                  setIsMobileMenuOpen(false);
                }}
                className="block w-full text-left text-lg font-semibold hover:bg-gray-100 p-2 rounded-md transition duration-300 focus:outline-none"
              >
                Book Appointment
              </button>
            </li>
          </ul>
        </nav>
      </motion.div>

      {/* Hero Section */}
      <section style={heroSectionStyle} className="text-center py-20 px-6 md:py-28 text-white">
        <h2 className="text-4xl md:text-5xl font-extrabold  mb-4">
          Experience the Joy of a Spotless Space
        </h2>
        <p className="text-xl md:text-2xl mt-5 leading-relaxed">
          We deliver unparalleled cleaning services that transform your home or office into a
          haven of cleanliness and comfort.
        </p>
        <ReviewButton />
      </section>

      {/* Services Section */}
      <section className="py-16 px-6 md:px-8">
        <h2 className="text-3xl md:text-4xl font-bold text-center mb-10 text-gray-800">
          Our Specialized Services
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {services.map((service) => (
            <ServiceCard key={service.id} service={service} />
          ))}
        </div>
      </section>

      {/* Testimonials/Reviews Section */}
      <section
        ref={reviewsSectionRef}
        className="py-16 px-6 md:px-8 bg-gray-50"
        id="reviews"
      >
        <h2 className="text-3xl md:text-4xl font-bold text-center mb-10 text-gray-800">
          Rave Reviews from Our Satisfied Clients
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {reviews.map((review) => (
            <ReviewCard
              key={review.id}
              review={review}
              onDelete={() => handleDeleteReview(review.id)}
              onEdit={() => handleEditReview(review)}
              isAddedByUser={isReviewAddedByUser(review.id)}
            />
          ))}
        </div>
      </section>

      {/* Leave a Review Section */}
      <section className="py-16 px-6 md:px-8 text-center" id="leave-review">
        <h2 className="text-3xl md:text-4xl font-bold text-center mb-10 text-gray-800">
          Share Your Experience
        </h2>
        <p className="text-gray-600 mb-6">
          We value your feedback! Tell us about your cleaning experience.
        </p>
        <button
          onClick={() => setIsLeaveReviewModalOpen(true)}
          className={`inline-block bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold`}
        >
          Leave a Review
        </button>
      </section>

      {/* Leave a Review Modal */}
      <Modal
        isOpen={isLeaveReviewModalOpen}
        onClose={() => setIsLeaveReviewModalOpen(false)}
        title="Share Your Experience"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-4 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
        />
        <textarea
          placeholder="Write a review"
          className="w-full p-3 border rounded-xl mb-4 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
          rows="4"
        />

        {/* Star Rating Input */}
        <div className="mb-5">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-2">
            Rating:
          </label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-3xl focus:outline-none ${
                  star <= review.rating ? "text-yellow-500" : "text-gray-400"
                }`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
              >
                <Star size={24} />
              </button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleReviewSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Submit Review
        </motion.button>
      </Modal>

      {/* Edit a Review Modal */}
      <Modal
        isOpen={isEditReviewModalOpen}
        onClose={() => setIsEditReviewModalOpen(false)}
        title="Edit Your Review"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-4 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
        />
        <textarea
          placeholder="Write a review"
          className="w-full p-3 border rounded-xl mb-4 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
          rows="4"
        />

        {/* Star Rating Input */}
        <div className="mb-5">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-2">
            Rating:
          </label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-3xl focus:outline-none ${
                  star <= review.rating ? "text-yellow-500" : "text-gray-400"
                }`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
              >
                <Star size={24} />
              </button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleUpdateReview}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Update Review
        </motion.button>
      </Modal>

      {/* Request Quote Modal */}
      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Request a Personalized Quote"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-4 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
        />
        <select
          className="w-full p-3 border rounded-xl mb-4 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleQuoteSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Get Your Quote
        </motion.button>
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Schedule Your Cleaning Appointment"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-4 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
        />
        <select
          className="w-full p-3 border rounded-xl mb-4 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <input
          type="date"
          className="w-full p-3 border rounded-xl mb-4 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
        />
        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleAppointmentSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Book Now
        </motion.button>
      </Modal>
    </div>
  );
};

export default App;
