import React, { useState, useEffect, useRef } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
  Menu,
} from "lucide-react";
import { motion } from "framer-motion";
import "tailwindcss/tailwind.css";

// **THEME DEFINITION**
const theme = {
  colors: {
    primary: "sky-500", // Airy Sky Blue
    primaryHover: "sky-600",
    secondary: "white",
    textPrimary: "gray-800", // Slightly softer text
    textSecondary: "gray-600", // Lighter secondary text
    accent: "teal-400", // Calming Teal
    success: "emerald-500",
    error: "rose-500", // Softer red for errors
    white: "white",
    gray800: "gray-800",
    light: "gray-50", // Very subtle light gray
    floralWhite: "#FFFAF0", // A touch of warmth
  },
  font: {
    family: "font-nunito font-sans", // Elegant, rounded font
    base: "text-base", // Base font size
  },
  shadow: "shadow-xl", // Deeper shadow for a premium feel
  borderRadius: "rounded-3xl", // Extra rounded for softness
  transition: "transition duration-700 ease-in-out", // Even smoother
};

// **DATA**
const services = [
  {
    id: "home",
    name: "Residential Serenity Cleaning",
    icon: <Home size={32} />, // Larger icons
    description:
      "Crafting serene living spaces with meticulous cleaning. Our residential service offers exceptional care, leaving your home refreshed and harmonious.",
  },
  {
    id: "office",
    name: "Commercial Clarity Cleaning",
    icon: <Briefcase size={32} />,
    description:
      "Elevate your workspace with our comprehensive cleaning. We ensure a spotless and inviting environment, fostering productivity and a positive atmosphere.",
  },
  {
    id: "deep",
    name: "Restorative Renewal Cleaning",
    icon: <SprayCan size={32} />,
    description:
      "Experience the rejuvenating power of deep cleaning. We meticulously cleanse every corner, removing impurities and restoring your space to its pristine best.",
  },
];

const prePopulatedReviews = [
  {
    name: "Eleanor",
    text: "Absolutely delighted with the service! My home feels lighter and cleaner than ever before. Highly recommended for a truly refreshing experience!",
    rating: 5,
  },
  {
    name: "Daniel",
    text: "Our office has never looked better. The cleaning team is professional, efficient, and pays great attention to detail. A wonderful investment!",
    rating: 5,
  },
  {
    name: "Sophia",
    text: "The deep cleaning service was exceptional. My home feels completely renewed and revitalized. Thank you for your outstanding work!",
    rating: 5,
  },
];

// **HELPER FUNCTIONS**
const getStarRating = (rating) => {
  const stars = [];
  for (let i = 0; i < 5; i++) {
    stars.push(
      <Star
        key={i}
        size={24} // Larger stars
        className={`inline ${i < rating ? "text-amber-400" : "text-gray-300"}`} // Soft amber
      />
    );
  }
  return stars;
};

// **COMPONENTS**
const Notification = ({ message, type }) => (
  <motion.div
    initial={{ opacity: 0, y: -30 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -30 }}
    className={`fixed top-8 right-8 p-6 rounded-3xl flex items-center space-x-4 text-white ${
      type === "success" ? `bg-${theme.colors.success}` : `bg-${theme.colors.error}`
    } ${theme.shadow} ${theme.transition}`}
  >
    <CheckCircle size={28} />
    <span>{message}</span>
  </motion.div>
);

const Modal = ({ isOpen, onClose, children, title }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-400 bg-opacity-60 flex justify-center items-center z-50 backdrop-blur-sm">
      <motion.div
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
        className={`bg-${theme.colors.floralWhite} p-12 rounded-3xl shadow-2xl w-11/12 md:w-96 relative`}
      >
        <button
          className="absolute top-5 right-5 text-gray-500 hover:text-gray-700 focus:outline-none"
          onClick={onClose}
          aria-label="Close Modal"
        >
          <XCircle size={30} />
        </button>
        {title && (
          <h2 className="text-3xl font-semibold mb-8 text-gray-700 text-center">
            {title}
          </h2>
        )}
        {children}
      </motion.div>
    </div>
  );
};

const ServiceCard = ({ service }) => (
  <motion.div
    whileHover={{ scale: 1.06, translateY: -8 }}
    className={`p-10 bg-${theme.colors.floralWhite} ${theme.shadow} ${theme.borderRadius} text-center hover:shadow-2xl ${theme.transition} cursor-pointer`}
  >
    <div className={`text-${theme.colors.accent} mx-auto mb-6`}>{service.icon}</div>
    <h3 className={`text-xl font-semibold mt-3 text-${theme.colors.textPrimary} mb-3`}>
      {service.name}
    </h3>
    <p className={`text-${theme.colors.textSecondary}`}>{service.description}</p>
  </motion.div>
);

const ReviewCard = ({ review }) => (
  <motion.div
    whileHover={{ scale: 1.04 }}
    className={`bg-${theme.colors.floralWhite} ${theme.shadow} ${theme.borderRadius} p-8 ${theme.transition}`}
  >
    <div className="text-amber-400 mb-5">{getStarRating(review.rating)}</div>
    <p className={`text-${theme.colors.textSecondary} italic mb-5`}>"{review.text}"</p>
    <p className={`text-${theme.colors.textPrimary} font-semibold`}>- {review.name}</p>
  </motion.div>
);

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [review, setReview] = useState({ name: "", text: "", rating: 5 });
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });

  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isLeaveReviewModalOpen, setIsLeaveReviewModalOpen] = useState(false);

  const [notification, setNotification] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const reviewsSectionRef = useRef(null);

  useEffect(() => {
    const savedReviews = JSON.parse(localStorage.getItem("reviews")) || [];
    setReviews(savedReviews.length > 0 ? savedReviews : prePopulatedReviews);
  }, []);

  useEffect(() => {
    localStorage.setItem("reviews", JSON.stringify(reviews));
  }, [reviews]);

  const handleReviewSubmit = () => {
    if (!review.name || !review.text) {
      showNotification("Please share your name and review.", "error");
      return;
    }
    setReviews([...reviews, review]);
    setReview({ name: "", text: "", rating: 5 });
    showNotification("Thank you for your beautiful review!", "success");
    setIsLeaveReviewModalOpen(false);
  };

  const handleQuoteSubmit = () => {
    if (!quote.name || !quote.service) {
      showNotification("Please tell us your name and which service you're interested in!", "error");
      return;
    }
    showNotification(
      `Your quote request for ${quote.service} has been received! We'll be in touch shortly.`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    if (!appointment.name || !appointment.service || !appointment.date) {
      showNotification("Please provide all details to schedule your appointment.", "error");
      return;
    }
    showNotification(
      `Your appointment for ${appointment.service} on ${appointment.date} is now confirmed!`,
      "success"
    );
    setAppointment({ name: "", service: "", date: "" });
    setIsAppointmentModalOpen(false);
  };

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 5000);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const scrollToReviews = () => {
    reviewsSectionRef.current?.scrollIntoView({ behavior: "smooth" });
    setIsMobileMenuOpen(false);
  };

  // **RENDER**
  return (
    <div
      className={`min-h-screen bg-${theme.colors.light} text-${theme.colors.textPrimary} ${theme.font.family} ${theme.font.base}`}
    >
      {/* Notification */}
      {notification && <Notification message={notification.message} type={notification.type} />}

      {/* Header */}
      <header
        className={`bg-gradient-to-r from-sky-400 to-sky-500 text-${theme.colors.white} p-7 flex justify-between items-center ${theme.shadow} sticky top-0 z-40`}
      >
        <a href="/" className="text-3xl font-bold flex items-center">
          <svg
            className="w-10 h-10 mr-3 text-teal-300"
            fill="none"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M4.5 15.75l7.5-7.5 7.5 7.5" />
            <path d="M4.5 18.75l7.5-7.5 7.5 7.5" />
          </svg>
          CleanHaven
        </a>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden text-white focus:outline-none p-3 rounded hover:bg-sky-600"
          onClick={toggleMobileMenu}
          aria-label="Toggle Menu"
        >
          <Menu size={30} />
        </button>

        {/* Navigation Links */}
        <nav className="hidden md:block">
          <ul className="flex space-x-10">
            <li>
              <button
                onClick={scrollToReviews}
                className={`text-${theme.colors.white} hover:text-sky-200 ${theme.transition} focus:outline-none`}
              >
                Client Stories
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsQuoteModalOpen(true)}
                className={`text-${theme.colors.white} hover:text-sky-200 ${theme.transition} focus:outline-none`}
              >
                Request a Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsAppointmentModalOpen(true)}
                className={`text-${theme.colors.white} hover:text-sky-200 ${theme.transition} focus:outline-none`}
              >
                Schedule Cleaning
              </button>
            </li>
          </ul>
        </nav>
      </header>

      {/* Mobile Menu */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        className={`md:hidden bg-sky-500 text-${theme.colors.white} p-7 rounded-3xl ${theme.shadow} fixed top-0 left-0 w-full h-screen z-50 flex flex-col justify-center items-center space-y-8`}
      >
        <button
          className="absolute top-6 right-6 text-white focus:outline-none p-3 rounded hover:bg-sky-600"
          onClick={toggleMobileMenu}
          aria-label="Close Menu"
        >
          <XCircle size={30} />
        </button>
        <ul className="flex flex-col space-y-8 text-center">
          <li>
            <button
              onClick={() => {
                scrollToReviews();
              }}
              className="block w-full text-xl font-semibold hover:bg-sky-600 p-5 rounded-3xl transition duration-300 focus:outline-none"
            >
              Client Stories
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsQuoteModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className="block w-full text-xl font-semibold hover:bg-sky-600 p-5 rounded-3xl transition duration-300 focus:outline-none"
            >
              Request a Quote
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsAppointmentModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className="block w-full text-xl font-semibold hover:bg-sky-600 p-5 rounded-3xl transition duration-300 focus:outline-none"
            >
              Schedule Cleaning
            </button>
          </li>
        </ul>
      </motion.div>

      {/* Hero Section */}
      <section className="bg-gradient-to-br from-sky-50 to-sky-100 text-center py-28 px-8 md:py-36">
        <h2 className="text-5xl md:text-6xl font-extrabold text-gray-700 mb-6">
          Welcome to a World of Immaculate Cleanliness
        </h2>
        <p className="text-xl md:text-2xl mt-6 text-gray-600 leading-relaxed">
          Experience the unmatched joy of a perfectly clean home or workspace. We are dedicated to
          providing exceptional cleaning services tailored to your every need.
        </p>
        <motion.button
          whileHover={{ scale: 1.1 }}
          onClick={() => setIsQuoteModalOpen(true)}
          className={`mt-12 bg-${theme.colors.primary} text-${theme.colors.white} py-6 px-12 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-xl font-semibold`}
        >
          Discover Your Free Quote
        </motion.button>
      </section>

      {/* Services Section */}
      <section className="py-24 px-8 md:px-10">
        <h2 className="text-4xl md:text-5xl font-bold text-center mb-14 text-gray-700">
          Our Signature Cleaning Services
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-12">
          {services.map((service) => (
            <ServiceCard key={service.id} service={service} />
          ))}
        </div>
      </section>

      {/* Testimonials/Reviews Section */}
      <section
        ref={reviewsSectionRef}
        className={`py-24 px-8 md:px-10 bg-${theme.colors.floralWhite}`}
      >
        <h2 className="text-4xl md:text-5xl font-bold text-center mb-14 text-gray-700">
          Kind Words from Our Cherished Clients
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-12">
          {reviews.map((review, index) => (
            <ReviewCard key={index} review={review} />
          ))}
        </div>
      </section>

      {/* Customer Review Button */}
      <section className="py-24 px-8 md:px-10 text-center">
        <motion.button
          whileHover={{ scale: 1.1 }}
          onClick={() => setIsLeaveReviewModalOpen(true)}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-6 px-12 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-xl font-semibold`}
        >
          Share Your Experience
        </motion.button>
      </section>

      {/* Footer */}
      <footer className={`bg-${theme.colors.gray800} text-${theme.colors.white} text-center p-9`}>
        <p className="text-lg">© 2024 CleanHaven. All rights reserved.</p>
      </footer>

      {/* Leave a Review Modal */}
      <Modal
        isOpen={isLeaveReviewModalOpen}
        onClose={() => setIsLeaveReviewModalOpen(false)}
        title="Share Your Experience"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-5 border rounded-3xl mb-7 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
        />
        <textarea
          placeholder="Write a review"
          className="w-full p-5 border rounded-3xl mb-7 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
          rows="5"
        />

        {/* Star Rating Input */}
        <div className="mb-8">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-4">
            Rating:
          </label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-4xl focus:outline-none ${
                  star <= review.rating ? "text-amber-400" : "text-gray-300"
                }`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
              >
                <Star size={26} />
              </button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.05 }}
          onClick={handleReviewSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-5 px-10 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Submit Review
        </motion.button>
      </Modal>

      {/* Request Quote Modal */}
      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Request a Personalized Quote"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-5 border rounded-3xl mb-7 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
        />
        <select
          className="w-full p-5 border rounded-3xl mb-7 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <motion.button
          whileHover={{ scale: 1.05 }}
          onClick={handleQuoteSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-5 px-10 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Get Your Quote
        </motion.button>
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Schedule Your Cleaning Appointment"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-5 border rounded-3xl mb-7 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
        />
        <select
          className="w-full p-5 border rounded-3xl mb-7 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <input
          type="date"
          className="w-full p-5 border rounded-3xl mb-7 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
        />
        <motion.button
          whileHover={{ scale: 1.05 }}
          onClick={handleAppointmentSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-5 px-10 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Book Now
        </motion.button>
      </Modal>
    </div>
  );
};

export default App;
