// **HELPER FUNCTIONS (Move outside the component)**
const getStarRating = (rating) => {
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    stars.push(
      <Star
        key={i}
        size={20}
        fill={i <= rating ? "currentColor" : "none"}
        className={`inline ${i <= rating ? "text-yellow-500" : "text-gray-400"}`}
      />
    );
  }
  return stars;
};

// **COMPONENTS (Extract into separate files for larger projects)**

const Notification = ({ message, type }) => (
  <motion.div
    initial={{ opacity: 0, y: -20 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -20 }}
    className={`fixed top-16 right-6 p-5 ${theme.borderRadius} flex items-center space-x-3 text-white ${
      type === "success" ? `bg-${theme.colors.success}` : `bg-${theme.colors.error}`
    } ${theme.shadow} ${theme.transition}`}
  >
    <CheckCircle size={24} />
    <span>{message}</span>
  </motion.div>
);

const ConfirmationModal = ({ isOpen, onClose, onConfirm, message }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-70 flex justify-center items-center z-50">
      <div className={`bg-${theme.colors.white} p-6 ${theme.borderRadius} ${theme.shadow}`}>
        <p className="mb-4">{message}</p>
        <div className="flex justify-end">
          <button
            className={`px-4 py-2 bg-gray-300 hover:bg-gray-400 ${theme.borderRadius} mr-2`}
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className={`px-4 py-2 bg-${theme.colors.error} text-${theme.colors.white} ${theme.borderRadius} hover:bg-red-600`}
            onClick={onConfirm}
          >
            Confirm
          </button>
        </div>
      </div>
    </div>
  );
};

const Modal = ({ isOpen, onClose, children, title }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-70 flex justify-center items-center z-50">
      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.8, opacity: 0 }}
        className={`bg-${theme.colors.white} p-8 ${theme.borderRadius} ${theme.shadow} w-11/12 md:w-96 relative`}
      >
        <button
          className="absolute top-4 right-4 text-gray-600 hover:text-gray-800 focus:outline-none"
          onClick={onClose}
          aria-label="Close Modal"
        >
          <XCircle size={28} />
        </button>
        {title && (
          <h2 className={`text-2xl font-semibold mb-6 text-${theme.colors.textPrimary} text-center`}>
            {title}
          </h2>
        )}
        {children}
      </motion.div>
    </div>
  );
};

