import React, { useState, useEffect, useRef } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
  Menu,
  Edit,
  Trash2,
} from "lucide-react";
import { motion } from "framer-motion";
import "tailwindcss/tailwind.css";

// **THEME DEFINITION**
const theme = {
  colors: {
    primary: "emerald-500",  // Vibrant green
    primaryHover: "emerald-600",
    secondary: "slate-50",   // Soft off-white
    textPrimary: "slate-900", // Dark gray
    textSecondary: "slate-700",
    accent: "amber-500",     // Warm yellow/orange
    success: "green-500",
    error: "red-500",
    white: "white",
    gray800: "slate-800",   // Darker gray for footer
    light: "slate-100",     // Very light gray
    // Additional color for shadow accents
    shadowAccent: 'emerald-200'
  },
  font: {
    family: "font-inter font-sans", // Modern sans-serif
    base: "text-base",
  },
  shadow: "shadow-xl",  // Increased shadow intensity
  borderRadius: "rounded-3xl", // Softer rounded corners
  transition: "transition duration-300 ease-in-out", // Faster, smoother transition
  spacing: {
    sectionPadding: "py-20 px-6 md:px-8",
    gridGap: "gap-8 md:gap-10",
  },
};

// **DATA (Move outside the component)**
const services = [
  {
    id: "home",
    name: "Residential Bliss Cleaning",
    icon: <Home size={28} />,
    description:
      "Transform your home into a haven of cleanliness and tranquility.  Meticulous detail for a refreshed and revitalized space.",
  },
  {
    id: "office",
    name: "Commercial Sparkle Cleaning",
    icon: <Briefcase size={28} />,
    description:
      "Elevate your workspace with comprehensive commercial cleaning.  Spotless and hygienic environment promotes productivity and a positive impression.",
  },
  {
    id: "deep",
    name: "Intensive Renewal Cleaning",
    icon: <SprayCan size={28} />,
    description:
      "Experience the ultimate cleanliness with deep cleaning. Tackling every nook and cranny, removing accumulated dirt for a truly pristine result.",
  },
];

const prePopulatedReviews = [
  {
    id: 1,
    name: "Isaac",
    text: "Absolutely transformed my home! The attention to detail was outstanding. Highly recommend!",
    rating: 5,
  },
  {
    id: 2,
    name: "Kojo",
    text: "Professional and efficient office cleaning service. Noticeably improved our workspace environment.",
    rating: 4,
  },
  {
    id: 3,
    name: "Big Ben",
    text: "Incredible deep cleaning results! My place feels brand new. Worth every penny!",
    rating: 5,
  },
];

// **HELPER FUNCTIONS (Move outside the component)**
const getStarRating = (rating) => {
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    stars.push(
      <Star
        key={i}
        size={20}
        fill={i <= rating ? "currentColor" : "none"}
        className={`inline ${i <= rating ? "text-amber-400" : "text-gray-400"}`} // Use theme-consistent colors
      />
    );
  }
  return stars;
};

// **COMPONENTS (Extract into separate files for larger projects)**

const Notification = ({ message, type }) => (
  <motion.div
    initial={{ opacity: 0, y: -20 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -20 }}
    className={`fixed top-6 right-6 p-4 rounded-xl flex items-center space-x-3 text-white ${
      type === "success" ? `bg-${theme.colors.success}` : `bg-${theme.colors.error}`
    } ${theme.shadow} ${theme.transition}`}
  >
    <CheckCircle size={20} />
    <span>{message}</span>
  </motion.div>
);

const ConfirmationModal = ({ isOpen, onClose, onConfirm, message }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-70 flex justify-center items-center z-50">
      <div className={`bg-white p-8 ${theme.borderRadius} ${theme.shadow}`}>
        <p className="mb-6 text-lg">{message}</p>
        <div className="flex justify-end space-x-4">
          <button
            className={`px-6 py-3 bg-gray-300 hover:bg-gray-400 ${theme.borderRadius} ${theme.transition}`}
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className={`px-6 py-3 bg-red-500 text-white ${theme.borderRadius} hover:bg-red-600 ${theme.transition}`}
            onClick={onConfirm}
          >
            Confirm
          </button>
        </div>
      </div>
    </div>
  );
};

const Modal = ({ isOpen, onClose, children, title }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-70 flex justify-center items-center z-50">
      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.8, opacity: 0 }}
        className={`bg-white p-12 ${theme.borderRadius} ${theme.shadow} w-11/12 md:w-96 relative`}
      >
        <button
          className="absolute top-4 right-4 text-gray-600 hover:text-gray-800 focus:outline-none"
          onClick={onClose}
          aria-label="Close Modal"
        >
          <XCircle size={28} />
        </button>
        {title && (
          <h2 className="text-3xl font-semibold mb-8 text-gray-800 text-center">
            {title}
          </h2>
        )}
        {children}
      </motion.div>
    </div>
  );
};

const ServiceCard = ({ service }) => (
  <motion.div
    whileHover={{ scale: 1.05, translateY: -5, boxShadow: `0 4px 12px rgba(0, 0, 0, 0.15)` }} // Adding shadow on hover
    className={`p-8 bg-white ${theme.shadow} ${theme.borderRadius} text-center ${theme.transition} cursor-pointer`}
  >
    <div className={`text-${theme.colors.accent} mx-auto mb-5`}>{service.icon}</div>
    <h3 className={`text-xl font-semibold mt-3 text-${theme.colors.textPrimary} mb-2`}>
      {service.name}
    </h3>
    <p className={`text-${theme.colors.textSecondary}`}>{service.description}</p>
  </motion.div>
);

const ReviewCard = ({ review, onDelete, onEdit, isAddedByUser }) => {
  return (
    <motion.div
      whileHover={{ scale: 1.03, boxShadow: `0 2px 8px rgba(0, 0, 0, 0.1)` }} // Adding shadow on hover
      className={`bg-white ${theme.shadow} ${theme.borderRadius} p-6 ${theme.transition}`}
    >
      <div className="flex justify-between items-center mb-4">
        <div className="text-yellow-500">{getStarRating(review.rating)}</div>
        {isAddedByUser && (
          <div className="flex space-x-2">
            <button onClick={onEdit} className="text-blue-500 hover:text-blue-700">
              <Edit size={20} />
            </button>
            <button onClick={onDelete} className="text-red-500 hover:text-red-700">
              <Trash2 size={20} />
            </button>
          </div>
        )}
      </div>
      <p className={`text-${theme.colors.textSecondary} italic mb-4`}>"{review.text}"</p>
      <p className={`text-${theme.colors.textSecondary} font-semibold`}>- {review.name}</p>
    </motion.div>
  );
};

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [userAddedReviews, setUserAddedReviews] = useState([]);
  const [review, setReview] = useState({ name: "", text: "", rating: 5 });
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });

  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isLeaveReviewModalOpen, setIsLeaveReviewModalOpen] = useState(false);
  const [isEditReviewModalOpen, setIsEditReviewModalOpen] = useState(false);
  const [reviewToEdit, setReviewToEdit] = useState(null);

  const [notification, setNotification] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isConfirmationModalOpen, setIsConfirmationModalOpen] = useState(false);
  const [reviewToDeleteId, setReviewToDeleteId] = useState(null);

  const reviewsSectionRef = useRef(null);

  // **LOCAL STORAGE HOOKS**
  useEffect(() => {
    const savedUserReviews = JSON.parse(localStorage.getItem("userReviews")) || [];
    setUserAddedReviews(savedUserReviews);

    setReviews([...prePopulatedReviews, ...savedUserReviews]);
  }, []);

  useEffect(() => {
    localStorage.setItem("userReviews", JSON.stringify(userAddedReviews));

    setReviews([...prePopulatedReviews, ...userAddedReviews]);
  }, [userAddedReviews]);

  // **HANDLERS**
  const handleReviewSubmit = () => {
    if (!review.name || !review.text) {
      showNotification("Please provide your name and review text!", "error");
      return;
    }
    const newReview = { ...review, id: Date.now() };
    setUserAddedReviews([...userAddedReviews, newReview]);
    setReview({ name: "", text: "", rating: 5 });
    showNotification("Thank you for your review!", "success");
    setIsLeaveReviewModalOpen(false);
  };

  const handleQuoteSubmit = () => {
    if (!quote.name || !quote.service) {
      showNotification("Please enter your name and select a service!", "error");
      return;
    }
    showNotification(
      `Your quote request for ${quote.service} has been submitted! We'll be in touch soon.`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    if (!appointment.name || !appointment.service || !appointment.date) {
      showNotification("Please fill in all fields to book your appointment!", "error");
      return;
    }
    showNotification(
      `Your appointment for ${appointment.service} on ${appointment.date} is booked!`,
      "success"
    );
    setAppointment({ name: "", service: "", date: "" });
    setIsAppointmentModalOpen(false);
  };

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 4000);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const scrollToReviews = () => {
    reviewsSectionRef.current?.scrollIntoView({ behavior: "smooth" });
    setIsMobileMenuOpen(false);
  };

  const handleDeleteReview = (id) => {
    setReviewToDeleteId(id);
    setIsConfirmationModalOpen(true);
  };

  const confirmDeleteReview = () => {
    const newUserAddedReviews = userAddedReviews.filter((review) => review.id !== reviewToDeleteId);
    setUserAddedReviews(newUserAddedReviews);
    setIsConfirmationModalOpen(false);
    setReviewToDeleteId(null);
    showNotification("Review deleted successfully!", "success");
  };

  const handleCancelDelete = () => {
    setIsConfirmationModalOpen(false);
    setReviewToDeleteId(null);
  };

  const handleEditReview = (review) => {
    setReviewToEdit(review);
    setReview(review);
    setIsEditReviewModalOpen(true);
  };

  const handleUpdateReview = () => {
    if (!review.name || !review.text) {
      showNotification("Please provide your name and review text!", "error");
      return;
    }

    const updatedUserAddedReviews = userAddedReviews.map((r) =>
      r.id === review.id ? review : r
    );
    setUserAddedReviews(updatedUserAddedReviews);

    setReview({ name: "", text: "", rating: 5 });
    showNotification("Review updated successfully!", "success");
    setIsEditReviewModalOpen(false);
    setReviewToEdit(null);
  };

  const isReviewAddedByUser = (reviewId) => {
    return userAddedReviews.some((review) => review.id === reviewId);
  };

  // **RENDER**
  return (
    <div
      className={`min-h-screen bg-${theme.colors.light} text-${theme.colors.textPrimary} ${theme.font.family} ${theme.font.base}`}
    >
      {/* Notification */}
      {notification && <Notification message={notification.message} type={notification.type} />}

      {/* Confirmation Modal */}
      <ConfirmationModal
        isOpen={isConfirmationModalOpen}
        onClose={handleCancelDelete}
        onConfirm={confirmDeleteReview}
        message="Are you sure you want to delete this review?"
      />

      {/* Header */}
      <header
        className={`bg-white border-b border-slate-200 text-${theme.colors.textPrimary} p-6 flex justify-between items-center ${theme.shadow} sticky top-0 z-40`}
      >
        <a href="/" className="text-2xl font-bold flex items-center">
          <svg
            className="w-8 h-8 mr-2 text-emerald-500"
            fill="none"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M4.5 15.75l7.5-7.5 7.5 7.5" />
            <path d="M4.5 18.75l7.5-7.5 7.5 7.5" />
          </svg>
          CleanConnect
        </a>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden text-gray-700 focus:outline-none p-3 rounded hover:bg-gray-100"
          onClick={toggleMobileMenu}
          aria-label="Toggle Menu"
        >
          <Menu size={28} />
        </button>

        {/* Navigation Links */}
        <nav className="hidden md:block">
          <ul className="flex space-x-8">
            <li>
              <button
                onClick={scrollToReviews}
                className={`text-${theme.colors.textPrimary} hover:text-emerald-500 ${theme.transition} focus:outline-none`}
              >
                Customer Reviews
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsQuoteModalOpen(true)}
                className={`text-${theme.colors.textPrimary} hover:text-emerald-500 ${theme.transition} focus:outline-none`}
              >
                Request Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsAppointmentModalOpen(true)}
                className={`text-${theme.colors.textPrimary} hover:text-emerald-500 ${theme.transition} focus:outline-none`}
              >
                Book Appointment
              </button>
            </li>
          </ul>
        </nav>
      </header>

      {/* Mobile Menu */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        className={`md:hidden bg-white text-${theme.colors.textPrimary} p-6 rounded-2xl ${theme.shadow} fixed top-0 left-0 w-full h-screen z-50 flex flex-col justify-center items-center space-y-6 ${
          isMobileMenuOpen ? "block" : "hidden"
        }`}
      >
        <button
          className="absolute top-4 right-4 text-gray-700 focus:outline-none p-3 rounded hover:bg-gray-100"
          onClick={toggleMobileMenu}
          aria-label="Close Menu"
        >
          <XCircle size={28} />
        </button>
        <ul className="flex flex-col space-y-6 text-center">
          <li>
            <button
              onClick={() => {
                scrollToReviews();
              }}
              className="block w-full text-xl font-semibold hover:bg-gray-100 p-4 rounded-2xl transition duration-300 focus:outline-none"
            >
              Customer Reviews
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsQuoteModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className="block w-full text-xl font-semibold hover:bg-gray-100 p-4 rounded-2xl transition duration-300 focus:outline-none"
            >
              Request Quote
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsAppointmentModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className="block w-full text-xl font-semibold hover:bg-gray-100 p-4 rounded-2xl transition duration-300 focus:outline-none"
            >
              Book Appointment
            </button>
          </li>
        </ul>
      </motion.div>

      {/* Hero Section */}
      <section className="bg-gradient-to-br from-emerald-100 to-emerald-300 text-center py-24 px-6 md:py-32">
        <h2 className="text-5xl md:text-6xl font-extrabold text-slate-900 mb-4">
          Experience the Joy of a Spotless Space
        </h2>
        <p className="text-xl md:text-2xl mt-5 text-slate-700 leading-relaxed">
          We deliver unparalleled cleaning services that transform your home or office into a
          haven of cleanliness and comfort.
        </p>
        <motion.button
          whileHover={{ scale: 1.08 }}
          onClick={() => setIsQuoteModalOpen(true)}
          className={`mt-10 bg-${theme.colors.primary} text-${theme.colors.white} py-5 px-10 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-xl font-semibold`}
        >
          Get a Free Quote
        </motion.button>
      </section>

      {/* Services Section */}
      <section className={`${theme.spacing.sectionPadding}`}>
        <h2 className="text-4xl md:text-5xl font-bold text-center mb-12 text-slate-900">
          Our Specialized Services
        </h2>
        <div className={`grid grid-cols-1 md:grid-cols-3 ${theme.spacing.gridGap}`}>
          {services.map((service) => (
            <ServiceCard key={service.id} service={service} />
          ))}
        </div>
      </section>

      {/* Testimonials/Reviews Section */}
      <section
        ref={reviewsSectionRef}
        className={`bg-${theme.colors.secondary} ${theme.spacing.sectionPadding}`}
      >
        <h2 className="text-4xl md:text-5xl font-bold text-center mb-12 text-slate-900">
          Rave Reviews from Our Satisfied Clients
        </h2>
        <div className={`grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 ${theme.spacing.gridGap}`}>
          {reviews.map((review) => (
            <ReviewCard
              key={review.id}
              review={review}
              onDelete={() => handleDeleteReview(review.id)}
              onEdit={() => handleEditReview(review)}
              isAddedByUser={isReviewAddedByUser(review.id)}
            />
          ))}
        </div>
      </section>

      {/* Customer Review Button */}
      <section className="py-16 px-6 md:px-8 text-center">
        <motion.button
          whileHover={{ scale: 1.08 }}
          onClick={() => setIsLeaveReviewModalOpen(true)}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-5 px-10 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-xl font-semibold`}
        >
          Share Your Experience
        </motion.button>
      </section>

      {/* Footer */}
      <footer className={`bg-${theme.colors.gray800} text-${theme.colors.white} text-center p-8`}>
        <p className="text-lg">© 2024 CleanConnect. All rights reserved.</p>
      </footer>

      {/* Leave a Review Modal */}
      <Modal
        isOpen={isLeaveReviewModalOpen}
        onClose={() => setIsLeaveReviewModalOpen(false)}
        title="Share Your Experience"
      >
        <input
          type="text"
          placeholder="Your Name"
          className={`w-full p-4 border border-gray-300 ${theme.borderRadius} mb-5 focus:outline-none focus:ring-2 focus:ring-${theme.colors.primary}`}
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
        />
        <textarea
          placeholder="Write a review"
          className={`w-full p-4 border border-gray-300 ${theme.borderRadius} mb-5 focus:outline-none focus:ring-2 focus:ring-${theme.colors.primary}`}
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
          rows="4"
        />

        {/* Star Rating Input */}
        <div className="mb-6">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-3">
            Rating:
          </label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-4xl focus:outline-none ${
                  star <= review.rating ? "text-amber-400" : "text-gray-400"
                }`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
              >
                <Star size={24} />
              </button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.05 }}
          onClick={handleReviewSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-4 px-8 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Submit Review
        </motion.button>
      </Modal>

      {/* Edit a Review Modal */}
      <Modal
        isOpen={isEditReviewModalOpen}
        onClose={() => setIsEditReviewModalOpen(false)}
        title="Edit Your Review"
      >
        <input
          type="text"
          placeholder="Your Name"
          className={`w-full p-4 border border-gray-300 ${theme.borderRadius} mb-5 focus:outline-none focus:ring-2 focus:ring-${theme.colors.primary}`}
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
        />
        <textarea
          placeholder="Write a review"
          className={`w-full p-4 border border-gray-300 ${theme.borderRadius} mb-5 focus:outline-none focus:ring-2 focus:ring-${theme.colors.primary}`}
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
          rows="4"
        />

        {/* Star Rating Input */}
        <div className="mb-6">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-3">
            Rating:
          </label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-4xl focus:outline-none ${
                  star <= review.rating ? "text-amber-400" : "text-gray-400"
                }`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
              >
                <Star size={24} />
              </button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.05 }}
          onClick={handleUpdateReview}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-4 px-8 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Update Review
        </motion.button>
      </Modal>

      {/* Request Quote Modal */}
      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Request a Personalized Quote"
      >
        <input
          type="text"
          placeholder="Your Name"
          className={`w-full p-4 border border-gray-300 ${theme.borderRadius} mb-5 focus:outline-none focus:ring-2 focus:ring-${theme.colors.primary}`}
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
        />
        <select
          className={`w-full p-4 border border-gray-300 ${theme.borderRadius} mb-5 focus:outline-none focus:ring-2 focus:ring-${theme.colors.primary}`}
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <motion.button
          whileHover={{ scale: 1.05 }}
          onClick={handleQuoteSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-4 px-8 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Get Your Quote
        </motion.button>
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Schedule Your Cleaning Appointment"
      >
        <input
          type="text"
          placeholder="Your Name"
          className={`w-full p-4 border border-gray-300 ${theme.borderRadius} mb-5 focus:outline-none focus:ring-2 focus:ring-${theme.colors.primary}`}
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
        />
        <select
          className={`w-full p-4 border border-gray-300 ${theme.borderRadius} mb-5 focus:outline-none focus:ring-2 focus:ring-${theme.colors.primary}`}
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <input
          type="date"
          className={`w-full p-4 border border-gray-300 ${theme.borderRadius} mb-5 focus:outline-none focus:ring-2 focus:ring-${theme.colors.primary}`}
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
        />
        <motion.button
          whileHover={{ scale: 1.05 }}
          onClick={handleAppointmentSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-4 px-8 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Book Now
        </motion.button>
      </Modal>
    </div>
  );
};

export default App;
