import React, { useState } from 'react';
import { motion } from 'framer-motion';

const Modal = ({ isOpen, onClose, title, children }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full">
      <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
        <div className="mt-3 text-center">
          <h3 className="text-lg leading-6 font-medium text-gray-900">{title}</h3>
          <div className="mt-2">
            {children}
          </div>
          <div className="items-center px-4 py-3">
            <button
              className="px-4 py-2 bg-gray-500 text-white text-base font-medium rounded-md w-full shadow-sm hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-300"
              onClick={onClose}
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isReviewModalOpen, setIsReviewModalOpen] = useState(false); // Review Modal State
  const [quote, setQuote] = useState({ name: '', service: '' });
  const [appointment, setAppointment] = useState({ name: '', service: '', date: '' });
  const [review, setReview] = useState({ rating: 0, comment: '' }); // Review State
  const [quoteNameError, setQuoteNameError] = useState('');
  const [quoteServiceError, setQuoteServiceError] = useState('');
  const [appointmentNameError, setAppointmentNameError] = useState('');
  const [appointmentServiceError, setAppointmentServiceError] = useState('');
  const [appointmentDateError, setAppointmentDateError] = useState('');
  const [reviewRatingError, setReviewRatingError] = useState(''); // Review Error state


  const services = [
    { id: 1, name: 'Standard Cleaning' },
    { id: 2, name: 'Deep Cleaning' },
    { id: 3, name: 'Move-In/Move-Out Cleaning' },
  ];

  const theme = {
    colors: {
      primary: 'blue-500',
      primaryHover: 'blue-700',
      accent: 'blue-300',
      white: 'white',
    },
    transition: 'transition ease-in-out duration-300',
  };

  const handleQuoteSubmit = () => {
    let isValid = true;
    if (!quote.name) {
      setQuoteNameError('Name is required');
      isValid = false;
    } else {
      setQuoteNameError('');
    }

    if (!quote.service) {
      setQuoteServiceError('Service is required');
      isValid = false;
    } else {
      setQuoteServiceError('');
    }

    if (isValid) {
      alert(JSON.stringify(quote)); // Replace with your actual submission logic
      setIsQuoteModalOpen(false);
    }
  };

  const handleAppointmentSubmit = () => {
    let isValid = true;
    if (!appointment.name) {
      setAppointmentNameError('Name is required');
      isValid = false;
    } else {
      setAppointmentNameError('');
    }

    if (!appointment.service) {
      setAppointmentServiceError('Service is required');
      isValid = false;
    } else {
      setAppointmentServiceError('');
    }

    if (!appointment.date) {
      setAppointmentDateError('Date is required');
      isValid = false;
    } else {
      setAppointmentDateError('');
    }

    if (isValid) {
      alert(JSON.stringify(appointment)); // Replace with your actual submission logic
      setIsAppointmentModalOpen(false);
    }
  };

  const handleReviewSubmit = () => {
    if (review.rating === 0) {
      setReviewRatingError("Please provide a rating.");
      return;
    } else {
      setReviewRatingError("");
    }

    alert(JSON.stringify(review)); // Replace with your actual submission logic
    setIsReviewModalOpen(false);
  };

  const handleStarClick = (rating) => {
    setReview({ ...review, rating: rating });
  };

  const renderStars = () => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <button
          key={i}
          onClick={() => handleStarClick(i)}
          aria-label={`Rate ${i} stars`}
          tabIndex="0" // Make the button keyboard accessible
          className="focus:outline-none" // Remove focus outline for better accessibility styling
        >
          <svg
            className={`h-8 w-8 ${i <= review.rating ? 'text-yellow-500' : 'text-gray-300'}`}
            fill={i <= review.rating ? 'currentColor' : 'none'}
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M12 2C6.477 2 2 6.477 2 12s4.477 10 10 10 10-4.477 10-10S17.523 2 12 2z"
            />
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M12 16.5L7.5 19l1-5.117L4.183 8.75l5.284-.423L12 3.5l2.533 4.827 5.284.423L15.5 13.883l1 5.117z"
            />
          </svg>
        </button>
      );
    }
    return stars;
  };


  return (
    <div className="container mx-auto p-4">
      <button
        onClick={() => setIsQuoteModalOpen(true)}
        className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold mb-4`}
      >
        Request a Quote
      </button>

      <button
        onClick={() => setIsAppointmentModalOpen(true)}
        className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold mb-4`}
      >
        Book an Appointment
      </button>


      <button
        onClick={() => setIsReviewModalOpen(true)}
        className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold mb-4`}
      >
        Add a Review
      </button>

      {/* Review Modal */}
      <Modal
        isOpen={isReviewModalOpen}
        onClose={() => setIsReviewModalOpen(false)}
        title="Submit Your Review"
      >
        <div className="mb-4">
          <label htmlFor="reviewRating" className="block text-gray-700 text-sm font-bold mb-2">
            Rating:
          </label>
          <div className="flex items-center" id="reviewRating">
            {renderStars()}
          </div>
          {reviewRatingError && (
            <p className="text-red-500 text-sm">{reviewRatingError}</p>
          )}
        </div>

        <div className="mb-4">
          <label htmlFor="reviewComment" className="block text-gray-700 text-sm font-bold mb-2">
            Comment:
          </label>
          <textarea
            id="reviewComment"
            rows="4"
            className="w-full p-3 border rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-600"
            value={review.comment}
            onChange={(e) => setReview({ ...review, comment: e.target.value })}
          ></textarea>
        </div>

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleReviewSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Submit Review
        </motion.button>
      </Modal>

      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Request a Personalized Quote"
      >
        <label htmlFor="quoteName" className="block text-gray-700 text-sm font-bold mb-2">
          Your Name:
        </label>
        <input
          type="text"
          id="quoteName"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
          aria-invalid={!!quoteNameError}
          aria-describedby="quoteName-error"
        />
        {quoteNameError && (
          <p className="text-red-500 text-sm" id="quoteName-error">{quoteNameError}</p>
        )}

        <label htmlFor="quoteService" className="block text-gray-700 text-sm font-bold mb-2">
          Select a service:
        </label>
        <select
          id="quoteService"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
          aria-invalid={!!quoteServiceError}
          aria-describedby="quoteService-error"
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        {quoteServiceError && (
          <p className="text-red-500 text-sm" id="quoteService-error">{quoteServiceError}</p>
        )}

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleQuoteSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Get Your Quote
        </motion.button>
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Schedule Your Cleaning Appointment"
      >
       <label htmlFor="appointmentName" className="block text-gray-700 text-sm font-bold mb-2">
          Your Name:
        </label>
        <input
          type="text"
          id="appointmentName"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
          aria-invalid={!!appointmentNameError}
          aria-describedby="appointmentName-error"
        />
        {appointmentNameError && (
          <p className="text-red-500 text-sm" id="appointmentName-error">{appointmentNameError}</p>
        )}

        <label htmlFor="appointmentService" className="block text-gray-700 text-sm font-bold mb-2">
          Select a service:
        </label>
        <select
          id="appointmentService"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
          aria-invalid={!!appointmentServiceError}
          aria-describedby="appointmentService-error"
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
         {appointmentServiceError && (
          <p className="text-red-500 text-sm" id="appointmentService-error">{appointmentServiceError}</p>
        )}

        <label htmlFor="appointmentDate" className="block text-gray-700 text-sm font-bold mb-2">
          Select a date:
        </label>
        <input
          type="date"
          id="appointmentDate"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
          aria-invalid={!!appointmentDateError}
          aria-describedby="appointmentDate-error"
        />
        {appointmentDateError && (
          <p className="text-red-500 text-sm" id="appointmentDate-error">{appointmentDateError}</p>
        )}

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleAppointmentSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Book Now
        </motion.button>
      </Modal>
    </div>
  );
};

export default App;
