import React, { useState, useEffect, useRef } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
  Menu,
  Edit,
  Trash2,
} from "lucide-react";
import { motion } from "framer-motion";
import "tailwindcss/tailwind.css";

// **THEME DEFINITION - MODERN AESTHETIC**
const theme = {
  colors: {
    primary: "indigo-500", // Deep Indigo
    primaryHover: "indigo-600",
    secondary: "gray-50",
    textPrimary: "gray-900", // Darker Gray
    textSecondary: "gray-700", // Slightly Darker
    accent: "emerald-500", // Emerald Green
    accentHover: "emerald-600",
    success: "green-500",
    error: "red-500",
    white: "white",
    gray800: "gray-800",
    light: "gray-100",
    background: "white", // A Clean White Background
  },
  font: {
    family: "font-inter font-sans", // Inter font - modern and clean
    base: "text-base",
  },
  shadow: "shadow-lg", // More pronounced shadow
  borderRadius: "rounded-2xl", // More Rounded Corners
  transition: "transition duration-300 ease-in-out",
  spacing: {
    small: "4",   // Adjusted spacing scale
    medium: "6",
    large: "8",
  },
};

// **DATA (Move outside the component)**
const services = [
  {
    id: "home",
    name: "Residential Bliss Cleaning",
    icon: <Home size={32} color={theme.colors.accent} />, // Larger, colored icons
    description:
      "Transform your home into a haven of cleanliness and tranquility. Our residential cleaning service offers meticulous attention to detail, leaving your space refreshed and revitalized.",
  },
  {
    id: "office",
    name: "Commercial Sparkle Cleaning",
    icon: <Briefcase size={32} color={theme.colors.accent} />, // Larger, colored icons
    description:
      "Elevate your workspace with our comprehensive commercial cleaning. We'll ensure a spotless and hygienic environment, promoting productivity and a positive impression for clients and employees alike.",
  },
  {
    id: "deep",
    name: "Intensive Renewal Cleaning",
    icon: <SprayCan size={32} color={theme.colors.accent} />, // Larger, colored icons
    description:
      "Experience the ultimate cleanliness with our deep cleaning service. We'll tackle every nook and cranny, removing accumulated dirt and grime for a truly pristine result.",
  },
];

const prePopulatedReviews = [
  {
    id: 1,
    name: "Isaac",
    text: "Absolutely transformed my home! The attention to detail was outstanding. Highly recommend!",
    rating: 5,
  },
  {
    id: 2,
    name: "Kojo",
    text: "Professional and efficient office cleaning service. Noticeably improved our workspace environment.",
    rating: 4,
  },
  {
    id: 3,
    name: "Big Ben",
    text: "Incredible deep cleaning results! My place feels brand new. Worth every penny!",
    rating: 5,
  },
];

// **HELPER FUNCTIONS (Move outside the component)**
const getStarRating = (rating) => {
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    stars.push(
      <Star
        key={i}
        size={24} // Slightly larger stars
        fill={i <= rating ? "currentColor" : "none"}
        className={`inline ${i <= rating ? "text-yellow-500" : "text-gray-300"}`}
      />
    );
  }
  return stars;
};

// **COMPONENTS (Extract into separate files for larger projects)**

const Notification = ({ message, type }) => (
  <motion.div
    initial={{ opacity: 0, y: -30 }} // More noticeable entrance
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -30 }}
    transition={{ duration: 0.4, ease: "easeInOut" }}
    className={`fixed top-${theme.spacing.medium} right-${theme.spacing.medium} p-${theme.spacing.medium} rounded-${theme.borderRadius} flex items-center space-x-${theme.spacing.small} text-white ${
      type === "success" ? `bg-green-500` : `bg-red-500`
    } ${theme.shadow} ${theme.transition} z-50`} // Added z-50 to ensure it's above the navbar
  >
    <CheckCircle size={24} />
    <span>{message}</span>
  </motion.div>
);

const ConfirmationModal = ({ isOpen, onClose, onConfirm, message }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-70 flex justify-center items-center z-50">
      <div className={`bg-${theme.colors.background} p-${theme.spacing.large} rounded-${theme.borderRadius} shadow-xl`}>
        <p className={`mb-${theme.spacing.medium} text-${theme.colors.textPrimary}`}>{message}</p>
        <div className="flex justify-end">
          <button
            className={`px-${theme.spacing.medium} py-${theme.spacing.small} bg-gray-300 hover:bg-gray-400 rounded-${theme.borderRadius} mr-${theme.spacing.small} ${theme.transition}`}
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className={`px-${theme.spacing.medium} py-${theme.spacing.small} bg-red-500 text-white rounded-${theme.borderRadius} hover:bg-red-600 ${theme.transition}`}
            onClick={onConfirm}
          >
            Confirm
          </button>
        </div>
      </div>
    </div>
  );
};

const Modal = ({ isOpen, onClose, children, title }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-70 flex justify-center items-center z-50">
      <motion.div
        initial={{ scale: 0.9, opacity: 0 }}  // More subtle entrance
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
        transition={{ duration: 0.3, ease: "easeInOut" }}
        className={`bg-${theme.colors.background} p-${theme.spacing.large} rounded-${theme.borderRadius} shadow-2xl w-11/12 md:w-96 relative`} // Adjusted padding and shadow
      >
        <button
          className="absolute top-4 right-4 text-gray-600 hover:text-gray-800 focus:outline-none"
          onClick={onClose}
          aria-label="Close Modal"
        >
          <XCircle size={28} />
        </button>
        {title && (
          <h2 className={`text-2xl font-semibold mb-${theme.spacing.medium} text-${theme.colors.textPrimary} text-center`}>
            {title}
          </h2>
        )}
        {children}
      </motion.div>
    </div>
  );
};

const ServiceCard = ({ service }) => (
  <motion.div
    whileHover={{ scale: 1.07, translateY: -5 }} // More pronounced hover
    className={`p-${theme.spacing.large} bg-${theme.colors.background} ${theme.shadow} ${theme.borderRadius} text-center hover:shadow-2xl ${theme.transition} cursor-pointer`} // Adjusted padding and shadow
  >
    <div className={`mx-auto mb-${theme.spacing.medium}`}>{service.icon}</div> {/* Reduced margin */}
    <h3 className={`text-xl font-semibold mt-2 text-${theme.colors.textPrimary} mb-${theme.spacing.small}`}>
      {service.name}
    </h3>
    <p className={`text-${theme.colors.textSecondary}`}>{service.description}</p>
  </motion.div>
);

const ReviewCard = ({ review, onDelete, onEdit, isAddedByUser }) => {
  return (
    <motion.div
      whileHover={{ scale: 1.05 }}  // More noticeable hover
      className={`bg-${theme.colors.background} ${theme.shadow} ${theme.borderRadius} p-${theme.spacing.medium} ${theme.transition}`} // Adjusted padding
    >
      <div className="flex justify-between items-center mb-3">
        <div className="text-yellow-500">{getStarRating(review.rating)}</div>
        {isAddedByUser && (
          <div>
            <button onClick={onEdit} className={`text-indigo-500 hover:text-indigo-700 mr-${theme.spacing.small} ${theme.transition}`}>
              <Edit size={20} />
            </button>
            <button onClick={onDelete} className={`text-red-500 hover:text-red-700 ${theme.transition}`}>
              <Trash2 size={20} />
            </button>
          </div>
        )}
      </div>
      <p className={`text-${theme.colors.textSecondary} italic mb-${theme.spacing.small}`}>"{review.text}"</p>
      <p className={`text-${theme.colors.textSecondary} font-semibold`}>- {review.name}</p>
    </motion.div>
  );
};

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [userAddedReviews, setUserAddedReviews] = useState([]); //Separate state for user added reviews
  const [review, setReview] = useState({ name: "", text: "", rating: 5 });
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });

  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isLeaveReviewModalOpen, setIsLeaveReviewModalOpen] = useState(false);
  const [isEditReviewModalOpen, setIsEditReviewModalOpen] = useState(false); //New state for edit modal
  const [reviewToEdit, setReviewToEdit] = useState(null); // New state for setting review id for edit

  const [notification, setNotification] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isConfirmationModalOpen, setIsConfirmationModalOpen] = useState(false); // Add confirmation modal state
  const [reviewToDeleteId, setReviewToDeleteId] = useState(null); // Add review id to delete

  const reviewsSectionRef = useRef(null);

  // **LOCAL STORAGE HOOKS**
  useEffect(() => {
    const savedUserReviews = JSON.parse(localStorage.getItem("userReviews")) || [];
    setUserAddedReviews(savedUserReviews);

    // Combine pre-populated and user-added reviews
    setReviews([...prePopulatedReviews, ...savedUserReviews]);
  }, []);

  useEffect(() => {
    localStorage.setItem("userReviews", JSON.stringify(userAddedReviews));

    // Update combined reviews whenever user-added reviews change
    setReviews([...prePopulatedReviews, ...userAddedReviews]);
  }, [userAddedReviews]);

  // **HANDLERS**
  const handleReviewSubmit = () => {
    if (!review.name || !review.text) {
      showNotification("Please provide your name and review text!", "error");
      return;
    }
    const newReview = { ...review, id: Date.now() }; // Create a unique ID
    setUserAddedReviews([...userAddedReviews, newReview]); //add the review to useradded review state
    setReview({ name: "", text: "", rating: 5 });
    showNotification("Thank you for your review!", "success");
    setIsLeaveReviewModalOpen(false);
  };

  const handleQuoteSubmit = () => {
    if (!quote.name || !quote.service) {
      showNotification("Please enter your name and select a service!", "error");
      return;
    }
    showNotification(
      `Your quote request for ${quote.service} has been submitted! We'll be in touch soon.`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    if (!appointment.name || !appointment.service || !appointment.date) {
      showNotification("Please fill in all fields to book your appointment!", "error");
      return;
    }
    showNotification(
      `Your appointment for ${appointment.service} on ${appointment.date} is booked!`,
      "success"
    );
    setAppointment({ name: "", service: "", date: "" });
    setIsAppointmentModalOpen(false);
  };

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 4000);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const scrollToReviews = () => {
    reviewsSectionRef.current?.scrollIntoView({ behavior: "smooth" });
    setIsMobileMenuOpen(false);
  };

  const handleDeleteReview = (id) => {
    setReviewToDeleteId(id);
    setIsConfirmationModalOpen(true);
  };

  const confirmDeleteReview = () => {
    const newUserAddedReviews = userAddedReviews.filter((review) => review.id !== reviewToDeleteId); //filter reviews that are stored in user added reviews
    setUserAddedReviews(newUserAddedReviews); //updating the state for the user added reviews which is stored in local storage
    setIsConfirmationModalOpen(false);
    setReviewToDeleteId(null);
    showNotification("Review deleted successfully!", "success");
  };

  const handleCancelDelete = () => {
    setIsConfirmationModalOpen(false);
    setReviewToDeleteId(null);
  };

  const handleEditReview = (review) => {
    setReviewToEdit(review); //setting review id and its previous state
    setReview(review);
    setIsEditReviewModalOpen(true);
  };

  const handleUpdateReview = () => {
    if (!review.name || !review.text) {
      showNotification("Please provide your name and review text!", "error");
      return;
    }

    // Update the userAddedReviews state
    const updatedUserAddedReviews = userAddedReviews.map((r) =>
      r.id === review.id ? review : r
    );
    setUserAddedReviews(updatedUserAddedReviews);

    setReview({ name: "", text: "", rating: 5 });
    showNotification("Review updated successfully!", "success");
    setIsEditReviewModalOpen(false);
    setReviewToEdit(null);
  };

  const isReviewAddedByUser = (reviewId) => {
    return userAddedReviews.some((review) => review.id === reviewId);
  };

  // **RENDER**
  return (
    <div
      className={`min-h-screen bg-${theme.colors.background} text-${theme.colors.textPrimary} ${theme.font.family} ${theme.font.base}`}
    >
      {/* Notification */}
      {notification && <Notification message={notification.message} type={notification.type} />}

      {/* Confirmation Modal */}
      <ConfirmationModal
        isOpen={isConfirmationModalOpen}
        onClose={handleCancelDelete}
        onConfirm={confirmDeleteReview}
        message="Are you sure you want to delete this review?"
      />

      {/* Header */}
      <header
        className={`bg-${theme.colors.background} text-${theme.colors.textPrimary} p-${theme.spacing.medium} flex justify-between items-center ${theme.shadow} sticky top-0 z-40`}
      >
        <a href="/" className="text-2xl font-bold flex items-center">
          <svg
            className="w-8 h-8 mr-2 text-emerald-500" // Emerald Icon
            fill="none"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M4.5 15.75l7.5-7.5 7.5 7.5" />
            <path d="M4.5 18.75l7.5-7.5 7.5 7.5" />
          </svg>
          CleanConnect
        </a>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden text-gray-600 focus:outline-none rounded hover:bg-gray-100"
          onClick={toggleMobileMenu}
          aria-label="Toggle Menu"
        >
          <Menu size={28} />
        </button>

        {/* Navigation Links */}
        <nav className="hidden md:block">
          <ul className="flex space-x-8">
            <li>
              <button
                onClick={scrollToReviews}
                className={`text-${theme.colors.textSecondary} hover:text-${theme.colors.textPrimary} ${theme.transition} focus:outline-none`}
              >
                Customer Reviews
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsQuoteModalOpen(true)}
                className={`text-${theme.colors.textSecondary} hover:text-${theme.colors.textPrimary} ${theme.transition} focus:outline-none`}
              >
                Request Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsAppointmentModalOpen(true)}
                className={`text-${theme.colors.textSecondary} hover:text-${theme.colors.textPrimary} ${theme.transition} focus:outline-none`}
              >
                Book Appointment
              </button>
            </li>
          </ul>
        </nav>
      </header>

      {/* Mobile Menu */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        className={`md:hidden bg-${theme.colors.background} text-${theme.colors.textPrimary} p-${theme.spacing.medium} rounded-${theme.borderRadius} ${theme.shadow} fixed top-0 left-0 w-full h-screen z-50 flex flex-col justify-center items-center space-y-${theme.spacing.medium} ${
          isMobileMenuOpen ? "block" : "hidden"
        }`}
      >
        <button
          className="absolute top-4 right-4 text-gray-600 focus:outline-none rounded hover:bg-gray-100"
          onClick={toggleMobileMenu}
          aria-label="Close Menu"
        >
          <XCircle size={28} />
        </button>
        <ul className="flex flex-col space-y-${theme.spacing.medium} text-center">
          <li>
            <button
              onClick={() => {
                scrollToReviews();
              }}
              className={`block w-full text-xl font-semibold hover:bg-gray-100 p-${theme.spacing.small} rounded-${theme.borderRadius} transition duration-300 focus:outline-none`}
            >
              Customer Reviews
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsQuoteModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className={`block w-full text-xl font-semibold hover:bg-gray-100 p-${theme.spacing.small} rounded-${theme.borderRadius} transition duration-300 focus:outline-none`}
            >
              Request Quote
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsAppointmentModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className={`block w-full text-xl font-semibold hover:bg-gray-100 p-${theme.spacing.small} rounded-${theme.borderRadius} transition duration-300 focus:outline-none`}
            >
              Book Appointment
            </button>
          </li>
        </ul>
      </motion.div>

      {/* Hero Section */}
      <section className="py-24 px-8 md:py-32 text-center">
        <h2 className="text-5xl md:text-6xl font-extrabold text-gray-900 mb-6">
          Experience the Joy of a Spotless Space
        </h2>
        <p className="text-xl md:text-2xl mt-5 text-gray-700 leading-relaxed">
          We deliver unparalleled cleaning services that transform your home or office into a
          haven of cleanliness and comfort.
        </p>
        <motion.button
          whileHover={{ scale: 1.06 }}
          className={`mt-10 bg-${theme.colors.primary} text-${theme.colors.white} py-4 px-8 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold`}
          onClick={() => setIsQuoteModalOpen(true)}
        >
          Get a Free Quote
        </motion.button>
      </section>

      {/* Services Section */}
      <section className="py-20 px-8 md:px-12">
        <h2 className="text-4xl md:text-5xl font-bold text-center mb-12 text-gray-900">
          Our Specialized Services
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
          {services.map((service) => (
            <ServiceCard key={service.id} service={service} />
          ))}
        </div>
      </section>

      {/* Testimonials/Reviews Section */}
      <section
        ref={reviewsSectionRef}
        className="py-20 px-8 md:px-12 bg-gray-50"
      >
        <h2 className="text-4xl md:text-5xl font-bold text-center mb-12 text-gray-900">
          Rave Reviews from Our Satisfied Clients
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10">
          {reviews.map((review) => (
            <ReviewCard
              key={review.id}
              review={review}
              onDelete={() => handleDeleteReview(review.id)}
              onEdit={() => handleEditReview(review)}
              isAddedByUser={isReviewAddedByUser(review.id)}
            />
          ))}
        </div>
      </section>

      {/* Customer Review Button */}
      <section className="py-20 px-8 md:px-12 text-center">
        <motion.button
          whileHover={{ scale: 1.06 }}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-4 px-8 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold`}
          onClick={() => setIsLeaveReviewModalOpen(true)}
        >
          Share Your Experience
        </motion.button>
      </section>

      {/* Leave a Review Modal */}
      <Modal
        isOpen={isLeaveReviewModalOpen}
        onClose={() => setIsLeaveReviewModalOpen(false)}
        title="Share Your Experience"
      >
        <input
          type="text"
          placeholder="Your Name"
          className={`w-full p-4 border rounded-${theme.borderRadius} mb-${theme.spacing.medium} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent}`}
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
        />
        <textarea
          placeholder="Write a review"
          className={`w-full p-4 border rounded-${theme.borderRadius} mb-${theme.spacing.medium} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent}`}
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
          rows="4"
        />

        {/* Star Rating Input */}
        <div className="mb-5">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-2">
            Rating:
          </label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-3xl focus:outline-none ${
                  star <= review.rating ? "text-yellow-500" : "text-gray-300"
                }`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
              >
                <Star size={24} />
              </button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.04 }}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
          onClick={handleReviewSubmit}
        >
          Submit Review
        </motion.button>
      </Modal>

      {/* Edit a Review Modal */}
      <Modal
        isOpen={isEditReviewModalOpen}
        onClose={() => setIsEditReviewModalOpen(false)}
        title="Edit Your Review"
      >
        <input
          type="text"
          placeholder="Your Name"
          className={`w-full p-4 border rounded-${theme.borderRadius} mb-${theme.spacing.medium} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent}`}
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
        />
        <textarea
          placeholder="Write a review"
          className={`w-full p-4 border rounded-${theme.borderRadius} mb-${theme.spacing.medium} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent}`}
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
          rows="4"
        />

        {/* Star Rating Input */}
        <div className="mb-5">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-2">
            Rating:
          </label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-3xl focus:outline-none ${
                  star <= review.rating ? "text-yellow-500" : "text-gray-300"
                }`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
              >
                <Star size={24} />
              </button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.04 }}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
          onClick={handleUpdateReview}
        >
          Update Review
        </motion.button>
      </Modal>

      {/* Request Quote Modal */}
      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Request a Personalized Quote"
      >
        <input
          type="text"
          placeholder="Your Name"
          className={`w-full p-4 border rounded-${theme.borderRadius} mb-${theme.spacing.medium} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent}`}
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
        />
        <select
          className={`w-full p-4 border rounded-${theme.borderRadius} mb-${theme.spacing.medium} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent}`}
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <motion.button
          whileHover={{ scale: 1.04 }}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
          onClick={handleQuoteSubmit}
        >
          Get Your Quote
        </motion.button>
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Schedule Your Cleaning Appointment"
      >
        <input
          type="text"
          placeholder="Your Name"
          className={`w-full p-4 border rounded-${theme.borderRadius} mb-${theme.spacing.medium} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent}`}
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
        />
        <select
          className={`w-full p-4 border rounded-${theme.borderRadius} mb-${theme.spacing.medium} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent}`}
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <input
          type="date"
          className={`w-full p-4 border rounded-${theme.borderRadius} mb-${theme.spacing.medium} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent}`}
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
        />
        <motion.button
          whileHover={{ scale: 1.04 }}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
          onClick={handleAppointmentSubmit}
        >
          Book Now
        </motion.button>
      </Modal>
    </div>
  );
};

export default App;
