import React, { useState, useEffect } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
  Menu,
} from "lucide-react";
import "tailwindcss/tailwind.css";

const services = [
  {
    id: "home",
    name: "Home Cleaning",
    icon: <Home />,
    description: "Expert cleaning for homes.",
  },
  {
    id: "office",
    name: "Office Cleaning",
    icon: <Briefcase />,
    description: "Maintain a spotless office space.",
  },
  {
    id: "deep",
    name: "Deep Cleaning",
    icon: <SprayCan />,
    description: "Thorough deep cleaning services.",
  },
];

const prePopulatedReviews = [
  {
    name: "Isaac",
    text: "Fantastic service! My home has never been this clean.",
  },
  {
    name: "Kojo",
    text: "The office cleaning was thorough and professional.",
  },
  { name: "Big Ben", text: "Amazing deep cleaning. Highly recommended!" },
];

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [review, setReview] = useState({ name: "", text: "" });
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });
  const [isReviewModalOpen, setIsReviewModalOpen] = useState(false);
  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [notification, setNotification] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const savedReviews = JSON.parse(localStorage.getItem("reviews")) || [];
    setReviews(savedReviews.length > 0 ? savedReviews : prePopulatedReviews);
  }, []);

  useEffect(() => {
    localStorage.setItem("reviews", JSON.stringify(reviews));
  }, [reviews]);

  const handleReviewSubmit = () => {
    if (!review.name || !review.text) {
      showNotification("Please fill in all fields!", "error");
      return;
    }
    setReviews([...reviews, review]);
    setReview({ name: "", text: "" });
    showNotification("Review submitted successfully!", "success");
  };

  const handleQuoteSubmit = () => {
    if (!quote.name || !quote.service) {
      showNotification("Please enter your name and select a service!", "error");
      return;
    }
    showNotification(
      `Quote request for ${quote.service} submitted!`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    if (!appointment.name || !appointment.service || !appointment.date) {
      showNotification("Please fill in all fields!", "error");
      return;
    }
    showNotification(
      `Appointment booked for ${appointment.service} on ${appointment.date}!`,
      "success"
    );
    setAppointment({ name: "", service: "", date: "" });
    setIsAppointmentModalOpen(false);
  };

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900 font-inter font-sans">
      {/* Notification */}
      {notification && (
        <div
          className={`fixed top-6 right-6 p-3 rounded-lg flex items-center space-x-2 text-white ${
            notification.type === "success" ? "bg-green-500" : "bg-red-500"
          }`}
        >
          <CheckCircle />
          <span>{notification.message}</span>
        </div>
      )}

      {/* Header */}
      <header className="bg-gradient-to-r from-blue-500 to-blue-700 text-white p-4 flex flex-col md:flex-row justify-between items-center shadow-md">
        <h1 className="text-2xl font-bold text-center md:text-left">
          CleanConnect
        </h1>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden text-white focus:outline-none p-2 rounded hover:bg-blue-800"  // Added padding, rounded corners, hover effect
          onClick={toggleMobileMenu}
          aria-label="Toggle Menu" // Add accessibility label
        >
          <Menu size={24} />
        </button>

        {/* Navigation Links (as Lists) */}
        <nav
          className={`md:flex md:items-center ${
            isMobileMenuOpen ? "block" : "hidden"
          } md:block w-full md:w-auto`}
        >
          <ul className="flex flex-col md:flex-row md:space-x-4 text-center">
            <li>
              <button
                onClick={() => {
                  setIsReviewModalOpen(true);
                  setIsMobileMenuOpen(false);
                }} // Close menu on selection
                className="block py-2 px-4 text-white hover:bg-blue-600 md:hover:bg-transparent md:text-blue-100 md:hover:text-white transition duration-200" // Styled as a link-like item
              >
                Customer Reviews
              </button>
            </li>
            <li>
              <button
                onClick={() => {
                  setIsQuoteModalOpen(true);
                  setIsMobileMenuOpen(false);
                }} // Close menu on selection
                className="block py-2 px-4 text-white hover:bg-blue-600 md:hover:bg-transparent md:text-blue-100 md:hover:text-white transition duration-200" // Styled as a link-like item
              >
                Request Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => {
                  setIsAppointmentModalOpen(true);
                  setIsMobileMenuOpen(false);
                }} // Close menu on selection
                className="block py-2 px-4 text-white hover:bg-blue-600 md:hover:bg-transparent md:text-blue-100 md:hover:text-white transition duration-200" // Styled as a link-like item
              >
                Book Appointment
              </button>
            </li>
          </ul>
        </nav>
      </header>

      {/* Hero Section */}
      <section className="bg-gradient-to-br from-blue-100 to-blue-300 text-center py-12 px-4 md:py-16">
        <h2 className="text-3xl md:text-4xl font-bold text-gray-800">
          Find Professional Cleaning Services
        </h2>
        <p className="text-md md:text-lg mt-2 text-gray-700">
          Book trusted cleaners in your area.
        </p>
      </section>

      {/* Book Appointment Modal */}
      {isAppointmentModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-11/12 md:w-96 relative">
            <button
              className="absolute top-3 right-3 text-gray-600 hover:text-gray-800"
              onClick={() => setIsAppointmentModalOpen(false)}
            >
              <XCircle size={20} />
            </button>
            <h2 className="text-xl font-semibold mb-4 text-gray-800">
              Book an Appointment
            </h2>
            <input
              type="text"
              placeholder="Your Name"
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={appointment.name}
              onChange={(e) =>
                setAppointment({ ...appointment, name: e.target.value })
              }
            />
            <select
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={appointment.service}
              onChange={(e) =>
                setAppointment({ ...appointment, service: e.target.value })
              }
            >
              <option value="" disabled>
                Select a service
              </option>
              {services.map((s) => (
                <option key={s.id} value={s.name}>
                  {s.name}
                </option>
              ))}
            </select>
            <input
              type="date"
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={appointment.date}
              onChange={(e) =>
                setAppointment({ ...appointment, date: e.target.value })
              }
            />
            <button
              onClick={handleAppointmentSubmit}
              className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-200 w-full"
            >
              Book Now
            </button>
          </div>
        </div>
      )}

      {/* Services Section */}
      <section className="py-10 px-4 md:px-6">
        <h2 className="text-2xl md:text-3xl font-bold text-center mb-6 text-gray-800">
          Our Services
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {services.map((service) => (
            <div
              key={service.id}
              className="p-6 bg-white shadow-md rounded-lg text-center hover:shadow-lg transition duration-200"
            >
              <div className="text-blue-500 mx-auto">{service.icon}</div>
              <h3 className="text-lg md:text-xl font-semibold mt-2 text-gray-800">
                {service.name}
              </h3>
              <p className="text-gray-600 mt-2">{service.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Customer Review Form */}
      <section className="py-10 px-4 md:px-6">
        <h2 className="text-2xl md:text-3xl font-bold text-center mb-6 text-gray-800">
          Leave a Review
        </h2>
        <div className="max-w-lg mx-auto bg-white p-6 rounded-md shadow-md">
          <input
            type="text"
            placeholder="Your Name"
            className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={review.name}
            onChange={(e) => setReview({ ...review, name: e.target.value })}
          />
          <textarea
            placeholder="Write a review"
            className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={review.text}
            onChange={(e) => setReview({ ...review, text: e.target.value })}
          />
          <button
            onClick={handleReviewSubmit}
            className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-200 w-full"
          >
            Submit Review
          </button>
        </div>
      </section>

      {/* Customer Reviews Modal */}
      {isReviewModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-11/12 md:w-96 relative">
            <button
              className="absolute top-3 right-3 text-gray-600 hover:text-gray-800"
              onClick={() => setIsReviewModalOpen(false)}
            >
