      {isQuoteModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-11/12 md:w-96 relative">
            <button
              className="absolute top-3 right-3 text-gray-600 hover:text-gray-800"
              onClick={() => setIsQuoteModalOpen(false)}
            >
              <XCircle size={20} />
            </button>
            <h2 className="text-xl font-semibold mb-4 text-gray-800">
              Request a Quote
            </h2>
            <input
              type="text"
              placeholder="Your Name"
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={quote.name}
              onChange={(e) => setQuote({ ...quote, name: e.target.value })}
            />
            <select
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={quote.service}
              onChange={(e) => setQuote({ ...quote, service: e.target.value })}
            >
              <option value="" disabled>
                Select a service
              </option>
              {services.map((s) => (
                <option key={s.id} value={s.name}>
                  {s.name}
                </option>
              ))}
            </select>
            <button
              onClick={handleQuoteSubmit}
              className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-200 w-full"
            >
              Submit Quote
            </button>
          </div>
        </div>
      )}

      {/* Footer */}
      <footer className="bg-gray-800 text-white text-center p-4">
        <p>© 2024 CleanConnect. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default App;
