import React, { useState, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';

const App = () => {
  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isReviewModalOpen, setIsReviewModalOpen] = useState(false); // New state for review modal
  const [quote, setQuote] = useState({ name: '', service: '' });
  const [appointment, setAppointment] = useState({ name: '', service: '', date: '' });
  const [review, setReview] = useState({ rating: 0, comment: '' }); // State for review
  const [quoteNameError, setQuoteNameError] = useState('');
  const [quoteServiceError, setQuoteServiceError] = useState('');
  const [appointmentNameError, setAppointmentNameError] = useState('');
  const [appointmentServiceError, setAppointmentServiceError] = useState('');
  const [appointmentDateError, setAppointmentDateError] = useState('');
  const [reviewRatingError, setReviewRatingError] = useState(''); // Error for review rating

  const theme = {
    colors: {
      primary: 'green-500',
      primaryHover: 'green-700',
      accent: 'blue-500',
      white: 'white',
    },
    transition: 'transition duration-300 ease-in-out',
  };

  const services = [
    { id: 1, name: 'Standard Cleaning' },
    { id: 2, name: 'Deep Cleaning' },
    { id: 3, name: 'Move-In/Move-Out Cleaning' },
  ];

  const handleQuoteSubmit = () => {
    let hasErrors = false;
    if (!quote.name) {
      setQuoteNameError('Please enter your name.');
      hasErrors = true;
    } else {
      setQuoteNameError('');
    }

    if (!quote.service) {
      setQuoteServiceError('Please select a service.');
      hasErrors = true;
    } else {
      setQuoteServiceError('');
    }

    if (hasErrors) {
      return;
    }

    alert(
      `Quote requested!\nName: ${quote.name}\nService: ${quote.service}`
    );
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    let hasErrors = false;
    if (!appointment.name) {
      setAppointmentNameError('Please enter your name.');
      hasErrors = true;
    } else {
      setAppointmentNameError('');
    }

    if (!appointment.service) {
      setAppointmentServiceError('Please select a service.');
      hasErrors = true;
    } else {
      setAppointmentServiceError('');
    }

    if (!appointment.date) {
      setAppointmentDateError('Please select a date.');
      hasErrors = true;
    } else {
      setAppointmentDateError('');
    }

    if (hasErrors) {
      return;
    }

    alert(
      `Appointment booked!\nName: ${appointment.name}\nService: ${appointment.service}\nDate: ${appointment.date}`
    );
    setIsAppointmentModalOpen(false);
  };

  const handleReviewSubmit = () => {
    if (review.rating === 0) {
      setReviewRatingError('Please provide a rating.');
      return;
    } else {
      setReviewRatingError('');
    }

    alert(
      `Review submitted!\nRating: ${review.rating}\nComment: ${review.comment}`
    );
    setIsReviewModalOpen(false);
  };

  const handleStarClick = (rating) => {
    setReview({ ...review, rating });
  };

  const renderStars = () => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <button
          key={i}
          onClick={() => handleStarClick(i)}
          aria-label={`Rate ${i} stars`}
          tabIndex="0" // Make the star keyboard accessible
          style={{
            fontSize: '2em',
            color: i <= review.rating ? 'gold' : 'gray',
            background: 'none',
            border: 'none',
            padding: 0,
            cursor: 'pointer',
          }}
        >
          ★
        </button>
      );
    }
    return stars;
  };

  // Custom Modal Component
  const Modal = ({ isOpen, onClose, title, children }) => {
    const modalRef = useRef(null);

    useEffect(() => {
      const handleKeyDown = (event) => {
        if (event.key === 'Escape' && isOpen) {
          onClose();
        }
      };

      if (isOpen) {
        document.addEventListener('keydown', handleKeyDown);

        // Focus on the modal when it opens
        if (modalRef.current) {
          modalRef.current.focus();
        }
      }

      return () => {
        document.removeEventListener('keydown', handleKeyDown);
      };
    }, [isOpen, onClose]);

    if (!isOpen) return null;

    return (
      <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex justify-center items-center">
        <div
          className="relative p-8 bg-white rounded-xl shadow-lg w-full max-w-md"
          ref={modalRef}
          tabIndex="0" // Make the modal focusable for keyboard navigation
        >
          <h2 className="text-2xl font-bold mb-4">{title}</h2>
          {children}
          <button
            onClick={onClose}
            className="absolute top-0 right-0 m-4 text-gray-600 hover:text-gray-800 focus:outline-none"
            aria-label="Close Modal"
          >
            <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
      </div>
    );
  };


  return (
    <div className="container mx-auto p-4">
      <button
        onClick={() => setIsQuoteModalOpen(true)}
        className={`bg-${theme.colors.primary} text-${theme.colors.white} py-2 px-4 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold`}
      >
        Request a Quote
      </button>

      <button
        onClick={() => setIsAppointmentModalOpen(true)}
        className={`bg-${theme.colors.primary} text-${theme.colors.white} py-2 px-4 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold ml-4`}
      >
        Book Appointment
      </button>

      {/* Button to open Review Modal */}
      <button
        onClick={() => setIsReviewModalOpen(true)}
        className={`bg-${theme.colors.primary} text-${theme.colors.white} py-2 px-4 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} text-lg font-semibold ml-4`}
      >
        Add a Review
      </button>

      {/* Review Modal */}
      <Modal
        isOpen={isReviewModalOpen}
        onClose={() => setIsReviewModalOpen(false)}
        title="Write a Review"
      >
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Rating:
          </label>
          <div>{renderStars()}</div>
          {reviewRatingError && (
            <p className="text-red-500 text-sm">{reviewRatingError}</p>
          )}
        </div>

        <div className="mb-4">
          <label htmlFor="reviewComment" className="block text-gray-700 text-sm font-bold mb-2">
            Comment:
          </label>
          <textarea
            id="reviewComment"
            placeholder="Your Review"
            className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
            value={review.comment}
            onChange={(e) => setReview({ ...review, comment: e.target.value })}
          />
        </div>

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleReviewSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Submit Review
        </motion.button>
      </Modal>

      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Request a Personalized Quote"
      >
        <label htmlFor="quoteName" className="block text-gray-700 text-sm font-bold mb-2">
          Your Name:
        </label>
        <input
          type="text"
          id="quoteName"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
          aria-invalid={!!quoteNameError}
          aria-describedby="quoteName-error"
        />
        {quoteNameError && (
          <p className="text-red-500 text-sm" id="quoteName-error">{quoteNameError}</p>
        )}

        <label htmlFor="quoteService" className="block text-gray-700 text-sm font-bold mb-2">
          Select a service:
        </label>
        <select
          id="quoteService"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
          aria-invalid={!!quoteServiceError}
          aria-describedby="quoteService-error"
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        {quoteServiceError && (
          <p className="text-red-500 text-sm" id="quoteService-error">{quoteServiceError}</p>
        )}

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleQuoteSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Get Your Quote
        </motion.button>
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Schedule Your Cleaning Appointment"
      >
       <label htmlFor="appointmentName" className="block text-gray-700 text-sm font-bold mb-2">
          Your Name:
        </label>
        <input
          type="text"
          id="appointmentName"
          placeholder="Your Name"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
          aria-invalid={!!appointmentNameError}
          aria-describedby="appointmentName-error"
        />
        {appointmentNameError && (
          <p className="text-red-500 text-sm" id="appointmentName-error">{appointmentNameError}</p>
        )}

        <label htmlFor="appointmentService" className="block text-gray-700 text-sm font-bold mb-2">
          Select a service:
        </label>
        <select
          id="appointmentService"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
          aria-invalid={!!appointmentServiceError}
          aria-describedby="appointmentService-error"
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
         {appointmentServiceError && (
          <p className="text-red-500 text-sm" id="appointmentService-error">{appointmentServiceError}</p>
        )}

        <label htmlFor="appointmentDate" className="block text-gray-700 text-sm font-bold mb-2">
          Select a date:
        </label>
        <input
          type="date"
          id="appointmentDate"
          className="w-full p-3 border rounded-xl mb-1 focus:outline-none focus:ring-2 focus:ring-blue-600"
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
          aria-invalid={!!appointmentDateError}
          aria-describedby="appointmentDate-error"
        />
        {appointmentDateError && (
          <p className="text-red-500 text-sm" id="appointmentDate-error">{appointmentDateError}</p>
        )}

        <motion.button
          whileHover={{ scale: 1.04 }}
          onClick={handleAppointmentSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Book Now
        </motion.button>
      </Modal>
    </div>
  );
};

export default App;
