import React, { useState, useEffect, useRef } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
  Menu,
} from "lucide-react";
import { motion } from "framer-motion";
import "tailwindcss/tailwind.css";

// **THEME DEFINITION**
const theme = {
  colors: {
    primary: "indigo-400", // Soft Indigo
    primaryHover: "indigo-500",
    secondary: "white",
    textPrimary: "gray-700", // Gentle gray
    textSecondary: "gray-500", // Subdued secondary text
    accent: "amber-300", // Warm Amber
    success: "green-400", // Calming Green
    error: "red-400", // Muted red
    white: "white",
    gray800: "gray-800",
    light: "gray-100", // Delicate light gray
    floralWhite: "#FFFAF0",
    lavender: "#E6E6FA", // Added lavender
  },
  font: {
    family: "font-lato font-sans", // Relaxed, modern font
    base: "text-base",
  },
  shadow: "shadow-2xl", // Luxurious shadow
  borderRadius: "rounded-[4rem]", // Pill-shaped
  transition: "transition duration-900 ease-in-out", // Extremely smooth
};

// **DATA**
const services = [
  {
    id: "home",
    name: "Home Sanctuary Cleaning",
    icon: <Home size={36} />, // Even larger
    description:
      "Transform your home into a tranquil sanctuary. Our specialized residential cleaning creates a harmonious and impeccably clean environment.",
  },
  {
    id: "office",
    name: "Workspace Oasis Cleaning",
    icon: <Briefcase size={36} />,
    description:
      "Cultivate a thriving workspace oasis. Our dedicated commercial cleaning service ensures a pristine and inviting atmosphere for enhanced productivity.",
  },
  {
    id: "deep",
    name: "Revitalizing Deep Cleaning",
    icon: <SprayCan size={36} />,
    description:
      "Experience the transformative power of our deep cleaning. We rejuvenate your space, meticulously removing impurities for a revitalized and refreshed ambiance.",
  },
];

const prePopulatedReviews = [
  {
    name: "Olivia",
    text: "Absolutely in love with the results! My home feels like a true sanctuary. The attention to detail is remarkable, and the atmosphere is so serene.",
    rating: 5,
  },
  {
    name: "Ethan",
    text: "Our workspace has been completely transformed. The cleaning service is exceptional, and the team is incredibly professional. A fantastic investment for our business!",
    rating: 5,
  },
  {
    name: "Isabella",
    text: "The deep cleaning service was beyond my expectations. My home feels incredibly clean and revitalized. I highly recommend this service for a truly transformative experience!",
    rating: 5,
  },
];

// **HELPER FUNCTIONS**
const getStarRating = (rating) => {
  const stars = [];
  for (let i = 0; i < 5; i++) {
    stars.push(
      <Star
        key={i}
        size={28} // Even larger
        className={`inline ${i < rating ? "text-amber-500" : "text-gray-300"}`} // Richer amber
      />
    );
  }
  return stars;
};

// **COMPONENTS**
const Notification = ({ message, type }) => (
  <motion.div
    initial={{ opacity: 0, y: -40 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -40 }}
    className={`fixed top-10 right-10 p-7 rounded-[3rem] flex items-center space-x-5 text-white ${
      type === "success" ? `bg-${theme.colors.success}` : `bg-${theme.colors.error}`
    } ${theme.shadow} ${theme.transition}`}
  >
    <CheckCircle size={32} />
    <span>{message}</span>
  </motion.div>
);

const Modal = ({ isOpen, onClose, children, title }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-400 bg-opacity-50 flex justify-center items-center z-50 backdrop-blur-md">
      <motion.div
        initial={{ scale: 0.95, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.95, opacity: 0 }}
        className={`bg-${theme.colors.lavender} p-14 rounded-[4rem] shadow-3xl w-11/12 md:w-96 relative`}
      >
        <button
          className="absolute top-6 right-6 text-gray-500 hover:text-gray-700 focus:outline-none"
          onClick={onClose}
          aria-label="Close Modal"
        >
          <XCircle size={32} />
        </button>
        {title && (
          <h2 className="text-3xl font-semibold mb-10 text-gray-700 text-center">
            {title}
          </h2>
        )}
        {children}
      </motion.div>
    </div>
  );
};

const ServiceCard = ({ service }) => (
  <motion.div
    whileHover={{ scale: 1.07, translateY: -10 }}
    className={`p-11 bg-${theme.colors.floralWhite} ${theme.shadow} ${theme.borderRadius} text-center hover:shadow-3xl ${theme.transition} cursor-pointer`}
  >
    <div className={`text-${theme.colors.accent} mx-auto mb-7`}>{service.icon}</div>
    <h3 className={`text-xl font-semibold mt-3 text-${theme.colors.textPrimary} mb-4`}>
      {service.name}
    </h3>
    <p className={`text-${theme.colors.textSecondary}`}>{service.description}</p>
  </motion.div>
);

const ReviewCard = ({ review }) => (
  <motion.div
    whileHover={{ scale: 1.05 }}
    className={`bg-${theme.colors.floralWhite} ${theme.shadow} ${theme.borderRadius} p-9 ${theme.transition}`}
  >
    <div className="text-amber-500 mb-6">{getStarRating(review.rating)}</div>
    <p className={`text-${theme.colors.textSecondary} italic mb-6`}>"{review.text}"</p>
    <p className={`text-${theme.colors.textPrimary} font-semibold`}>- {review.name}</p>
  </motion.div>
);

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [review, setReview] = useState({ name: "", text: "", rating: 5 });
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });

  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isLeaveReviewModalOpen, setIsLeaveReviewModalOpen] = useState(false);

  const [notification, setNotification] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const reviewsSectionRef = useRef(null);

  useEffect(() => {
    const savedReviews = JSON.parse(localStorage.getItem("reviews")) || [];
    setReviews(savedReviews.length > 0 ? savedReviews : prePopulatedReviews);
  }, []);

  useEffect(() => {
    localStorage.setItem("reviews", JSON.stringify(reviews));
  }, [reviews]);

  const handleReviewSubmit = () => {
    if (!review.name || !review.text) {
      showNotification("Please share your name and review to inspire us.", "error");
      return;
    }
    setReviews([...reviews, review]);
    setReview({ name: "", text: "", rating: 5 });
    showNotification("Your beautiful review has been shared. Thank you!", "success");
    setIsLeaveReviewModalOpen(false);
  };

  const handleQuoteSubmit = () => {
    if (!quote.name || !quote.service) {
      showNotification(
        "We'd love to know your name and the service you're interested in.",
        "error"
      );
      return;
    }
    showNotification(
      `Your quote request for ${quote.service} has been received with joy! Expect a personalized message shortly.`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    if (!appointment.name || !appointment.service || !appointment.date) {
      showNotification(
        "Kindly provide all the details, so we may schedule your cleaning with utmost care.",
        "error"
      );
      return;
    }
    showNotification(
      `Your appointment for ${appointment.service} on ${appointment.date} is now gracefully confirmed!`,
      "success"
    );
    setAppointment({ name: "", service: "", date: "" });
    setIsAppointmentModalOpen(false);
  };

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 6000); // Longer notification duration
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const scrollToReviews = () => {
    reviewsSectionRef.current?.scrollIntoView({ behavior: "smooth" });
    setIsMobileMenuOpen(false);
  };

  // **RENDER**
  return (
    <div
      className={`min-h-screen bg-${theme.colors.light} text-${theme.colors.textPrimary} ${theme.font.family} ${theme.font.base}`}
    >
      {/* Notification */}
      {notification && <Notification message={notification.message} type={notification.type} />}

      {/* Header */}
      <header
        className={`bg-gradient-to-r from-indigo-300 to-indigo-400 text-${theme.colors.white} p-8 flex justify-between items-center ${theme.shadow} sticky top-0 z-40`}
      >
        <a href="/" className="text-4xl font-bold flex items-center">
          <svg
            className="w-12 h-12 mr-4 text-amber-200"
            fill="none"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M4.5 15.75l7.5-7.5 7.5 7.5" />
            <path d="M4.5 18.75l7.5-7.5 7.5 7.5" />
          </svg>
          CleanBliss
        </a>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden text-white focus:outline-none p-4 rounded hover:bg-indigo-500"
          onClick={toggleMobileMenu}
          aria-label="Toggle Menu"
        >
          <Menu size={32} />
        </button>

        {/* Navigation Links */}
        <nav className="hidden md:block">
          <ul className="flex space-x-12">
            <li>
              <button
                onClick={scrollToReviews}
                className={`text-${theme.colors.white} hover:text-indigo-200 ${theme.transition} focus:outline-none`}
              >
                Client Experiences
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsQuoteModalOpen(true)}
                className={`text-${theme.colors.white} hover:text-indigo-200 ${theme.transition} focus:outline-none`}
              >
                Request Your Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsAppointmentModalOpen(true)}
                className={`text-${theme.colors.white} hover:text-indigo-200 ${theme.transition} focus:outline-none`}
              >
                Schedule Your Bliss
              </button>
            </li>
          </ul>
        </nav>
      </header>

      {/* Mobile Menu */}
      <motion.div
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -30 }}
        className={`md:hidden bg-indigo-400 text-${theme.colors.white} p-8 rounded-[3rem] ${theme.shadow} fixed top-0 left-0 w-full h-screen z-50 flex flex-col justify-center items-center space-y-9`}
      >
        <button
          className="absolute top-7 right-7 text-white focus:outline-none p-4 rounded hover:bg-indigo-500"
          onClick={toggleMobileMenu}
          aria-label="Close Menu"
        >
          <XCircle size={32} />
        </button>
        <ul className="flex flex-col space-y-9 text-center">
          <li>
            <button
              onClick={() => {
                scrollToReviews();
              }}
              className="block w-full text-xl font-semibold hover:bg-indigo-500 p-6 rounded-[3rem] transition duration-300 focus:outline-none"
            >
              Client Experiences
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsQuoteModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className="block w-full text-xl font-semibold hover:bg-indigo-500 p-6 rounded-[3rem] transition duration-300 focus:outline-none"
            >
              Request Your Quote
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsAppointmentModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className="block w-full text-xl font-semibold hover:bg-indigo-500 p-6 rounded-[3rem] transition duration-300 focus:outline-none"
            >
              Schedule Your Bliss
            </button>
          </li>
        </ul>
      </motion.div>

      {/* Hero Section */}
      <section className="bg-gradient-to-br from-indigo-50 to-indigo-100 text-center py-32 px-9 md:py-40">
        <h2 className="text-6xl md:text-7xl font-extrabold text-gray-600 mb-8">
          Discover the Art of Pure Cleanliness
        </h2>
        <p className="text-2xl md:text-3xl mt-7 text-gray-500 leading-relaxed">
          We are dedicated to curating impeccable cleaning experiences tailored to your unique
          lifestyle and preferences. Embrace the serenity of a flawlessly maintained space.
        </p>
        <motion.button
          whileHover={{ scale: 1.12 }}
          className={`mt-14 bg-${theme.colors.primary} text-${theme.colors.white} py-7 px-14 rounded-[4rem] hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-xl font-semibold`}
          onClick={() => setIsQuoteModalOpen(true)}
        >
          Unveil Your Complimentary Quote
        </motion.button>
      </section>

      {/* Services Section */}
      <section className="py-28 px-9 md:px-11">
        <h2 className="text-5xl md:text-6xl font-bold text-center mb-16 text-gray-600">
          Our Curated Suite of Cleaning Services
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-14">
          {services.map((service) => (
            <ServiceCard key={service.id} service={service} />
          ))}
        </div>
      </section>

      {/* Testimonials/Reviews Section */}
      <section
        ref={reviewsSectionRef}
        className={`py-28 px-9 md:px-11 bg-${theme.colors.lavender}`}
      >
        <h2 className="text-5xl md:text-6xl font-bold text-center mb-16 text-gray-600">
          Glowing Testimonials from Our Valued Clients
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-14">
          {reviews.map((review, index) => (
            <ReviewCard key={index} review={review} />
          ))}
        </div>
      </section>

      {/* Customer Review Button */}
      <section className="py-28 px-9 md:px-11 text-center">
        <motion.button
          whileHover={{ scale: 1.12 }}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-7 px-14 rounded-[4rem] hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-xl font-semibold`}
          onClick={() => setIsLeaveReviewModalOpen(true)}
        >
          Share Your Delightful Experience
        </motion.button>
      </section>

      {/* Footer */}
      <footer className={`bg-${theme.colors.gray800} text-${theme.colors.white} text-center p-10`}>
        <p className="text-lg"> 2024 CleanBliss. All rights reserved.</p>
      </footer>

      {/* Leave a Review Modal */}
      <Modal
        isOpen={isLeaveReviewModalOpen}
        onClose={() => setIsLeaveReviewModalOpen(false)}
        title="Share Your Delightful Experience"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-6 border rounded-[3rem] mb-8 focus:outline-none focus:ring-2 focus:ring-indigo-300"
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
        />
        <textarea
          placeholder="Write a review"
          className="w-full p-6 border rounded-[3rem] mb-8 focus:outline-none focus:ring-2 focus:ring-indigo-300"
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
          rows="6" // Increased height
        />

        {/* Star Rating Input */}
        <div className="mb-9">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-5">
            Rating:
          </label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-5xl focus:outline-none ${
                  star <= review.rating ? "text-amber-500" : "text-gray-300"
                }`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
              >
                <Star size={30} />
              </button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.05 }}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-6 px-12 rounded-[4rem] hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
          onClick={handleReviewSubmit}
        >
          Share Your Experience
        </motion.button>
      </Modal>

      {/* Request Quote Modal */}
      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Request Your Personalized Quote"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-6 border rounded-[3rem] mb-8 focus:outline-none focus:ring-2 focus:ring-indigo-300"
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
        />
        <select
          className="w-full p-6 border rounded-[3rem] mb-8 focus:outline-none focus:ring-2 focus:ring-indigo-300"
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <motion.button
          whileHover={{ scale: 1.05 }}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-6 px-12 rounded-[4rem] hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
          onClick={handleQuoteSubmit}
        >
          Discover Your Quote
        </motion.button>
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Schedule Your Cleaning Appointment"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-6 border rounded-[3rem] mb-8 focus:outline-none focus:ring-2 focus:ring-indigo-300"
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
        />
        <select
          className="w-full p-6 border rounded-[3rem] mb-8 focus:outline-none focus:ring-2 focus:ring-indigo-300"
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <input
          type="date"
          className="w-full p-6 border rounded-[3rem] mb-8 focus:outline-none focus:ring-2 focus:ring-indigo-300"
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
        />
        <motion.button
          whileHover={{ scale: 1.05 }}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-6 px-12 rounded-[4rem] hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
          onClick={handleAppointmentSubmit}
        >
          Book Your Bliss
        </motion.button>
      </Modal>
    </div>
  );
};

export default App;
