import React, { useState, useEffect, useRef } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
  Menu,
} from "lucide-react";
import "tailwindcss/tailwind.css";

// **THEME DEFINITION**
const theme = {
  colors: {
    primary: "blue-600",   
    primaryHover: "blue-700",
    secondary: "gray-50", 
    textPrimary: "gray-900",  
    textSecondary: "gray-700", 
    accent: "blue-500",    
    success: "green-500",
    error: "red-500",
    white: "white",
    gray800: "gray-800",
  },
  font: {
    family: "font-inter font-sans",
    base: "text-base",    // Base font size
  },
  shadow: "shadow-md",
  borderRadius: "rounded-lg",
  transition: "transition duration-200",
};

// **DATA (Move outside the component)**
const services = [
  {
    id: "home",
    name: "Home Cleaning",
    icon: <Home size={24} />,
    description: "Expert cleaning for homes.",
  },
  {
    id: "office",
    name: "Office Cleaning",
    icon: <Briefcase size={24} />,
    description: "Maintain a spotless office space.",
  },
  {
    id: "deep",
    name: "Deep Cleaning",
    icon: <SprayCan size={24} />,
    description: "Thorough deep cleaning services.",
  },
];

const prePopulatedReviews = [
  {
    name: "Isaac",
    text: "Fantastic service! My home has never been this clean.",
    rating: 5,
  },
  {
    name: "Kojo",
    text: "The office cleaning was thorough and professional.",
    rating: 4,
  },
  { name: "Big Ben", text: "Amazing deep cleaning. Highly recommended!", rating: 5 },
];


// **HELPER FUNCTIONS (Move outside the component)**
const getStarRating = (rating) => {
  const stars = [];
  for (let i = 0; i < 5; i++) {
    stars.push(
      <Star
        key={i}
        size={16}
        className={`inline ${i < rating ? "text-yellow-500" : "text-gray-400"}`} // Use dynamic class for filled/empty star
      />
    );
  }
  return stars;
};


// **COMPONENTS (Extract into separate files for larger projects)**

const Notification = ({ message, type }) => (
  <div
    className={`fixed top-6 right-6 p-3 rounded-lg flex items-center space-x-2 text-white ${
      type === "success" ? `bg-${theme.colors.success}` : `bg-${theme.colors.error}`
    } ${theme.shadow}`}
  >
    <CheckCircle size={18} />
    <span>{message}</span>
  </div>
);


const Modal = ({ isOpen, onClose, children, title }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center">
      <div className={`bg-white p-6 rounded shadow-lg w-11/12 md:w-96 relative`}>
        <button
          className="absolute top-3 right-3 text-gray-600 hover:text-gray-800"
          onClick={onClose}
          aria-label="Close Modal"
        >
          <XCircle size={20} />
        </button>
        {title && <h2 className="text-xl font-semibold mb-4 text-gray-800">{title}</h2>}
        {children}
      </div>
    </div>
  );
};


const ServiceCard = ({ service }) => (
  <div
    className={`p-6 bg-white ${theme.shadow} ${theme.borderRadius} text-center hover:shadow-lg ${theme.transition}`}
  >
    <div className={`text-${theme.colors.accent} mx-auto mb-2`}>{service.icon}</div>
    <h3 className={`text-lg md:text-xl font-semibold mt-2 text-${theme.colors.textPrimary}`}>
      {service.name}
    </h3>
    <p className={`text-${theme.colors.textSecondary} mt-2`}>{service.description}</p>
  </div>
);



const ReviewCard = ({ review }) => (
  <div className={`bg-white ${theme.shadow} ${theme.borderRadius} p-4`}>
    <div className="text-yellow-500 mb-2">{getStarRating(review.rating)}</div>
    <p className={`text-${theme.colors.textSecondary} italic mb-2`}>"{review.text}"</p>
    <p className={`text-${theme.colors.textSecondary} font-semibold`}>- {review.name}</p>
  </div>
);



const App = () => {
  const [reviews, setReviews] = useState([]);
  const [review, setReview] = useState({ name: "", text: "", rating: 5 }); // Added rating to the review state
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });

  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isLeaveReviewModalOpen, setIsLeaveReviewModalOpen] = useState(false);

  const [notification, setNotification] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const reviewsSectionRef = useRef(null);

  // **LOCAL STORAGE HOOKS**
  useEffect(() => {
    const savedReviews = JSON.parse(localStorage.getItem("reviews")) || [];
    setReviews(savedReviews.length > 0 ? savedReviews : prePopulatedReviews);
  }, []);

  useEffect(() => {
    localStorage.setItem("reviews", JSON.stringify(reviews));
  }, [reviews]);


  // **HANDLERS**
  const handleReviewSubmit = () => {
    if (!review.name || !review.text) {
      showNotification("Please fill in all fields!", "error");
      return;
    }
    setReviews([...reviews, review]);
    setReview({ name: "", text: "", rating: 5 }); // Reset rating to default
    showNotification("Review submitted successfully!", "success");
    setIsLeaveReviewModalOpen(false);
  };


  const handleQuoteSubmit = () => {
    if (!quote.name || !quote.service) {
      showNotification("Please enter your name and select a service!", "error");
      return;
    }
    showNotification(
      `Quote request for ${quote.service} submitted!`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };


  const handleAppointmentSubmit = () => {
    if (!appointment.name || !appointment.service || !appointment.date) {
      showNotification("Please fill in all fields!", "error");
      return;
    }
    showNotification(
      `Appointment booked for ${appointment.service} on ${appointment.date}!`,
      "success"
    );
    setAppointment({ name: "", service: "", date: "" });
    setIsAppointmentModalOpen(false);
  };


  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };


  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };


  const scrollToReviews = () => {
    reviewsSectionRef.current?.scrollIntoView({ behavior: "smooth" });
    setIsMobileMenuOpen(false);
  };


  // **RENDER**
  return (
    <div className={`min-h-screen bg-${theme.colors.secondary} text-${theme.colors.textPrimary} ${theme.font.family} ${theme.font.base}`}>

      {/* Notification */}
      {notification && <Notification message={notification.message} type={notification.type} />}


      {/* Header */}
      <header className={`bg-gradient-to-r from-blue-500 to-blue-700 text-${theme.colors.white} p-4 flex justify-between items-center ${theme.shadow}`}>
        <h1 className="text-2xl font-bold">CleanConnect</h1>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden text-white focus:outline-none p-2 rounded hover:bg-blue-800"
          onClick={toggleMobileMenu}
          aria-label="Toggle Menu"
        >
          <Menu size={24} />
        </button>

        {/* Navigation Links */}
        <nav className="hidden md:block">
          <ul className="flex space-x-4">
            <li>
              <button
                onClick={scrollToReviews}
                className={`text-${theme.colors.white} hover:text-blue-100 ${theme.transition}`}
              >
                Customer Reviews
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsQuoteModalOpen(true)}
                className={`text-${theme.colors.white} hover:text-blue-100 ${theme.transition}`}
              >
                Request Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsAppointmentModalOpen(true)}
                className={`text-${theme.colors.white} hover:text-blue-100 ${theme.transition}`}
              >
                Book Appointment
              </button>
            </li>
          </ul>
        </nav>
      </header>

      {/* Mobile Menu */}
      <div
        className={`md:hidden bg-blue-600 text-${theme.colors.white} p-4 rounded ${theme.shadow} ${
          isMobileMenuOpen ? "block" : "hidden"
        }`}
      >
        <ul className="flex flex-col space-y-2">
          <li>
            <button
              onClick={() => {
                scrollToReviews();
              }}
              className="block w-full text-left hover:bg-blue-700 p-2 rounded transition duration-200"
            >
              Customer Reviews
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsQuoteModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className="block w-full text-left hover:bg-blue-700 p-2 rounded transition duration-200"
            >
              Request Quote
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsAppointmentModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className="block w-full text-left hover:bg-blue-700 p-2 rounded transition duration-200"
            >
              Book Appointment
            </button>
          </li>
        </ul>
      </div>

      {/* Hero Section */}
      <section className="bg-gradient-to-br from-blue-100 to-blue-300 text-center py-12 px-4 md:py-16">
        <h2 className="text-3xl md:text-4xl font-bold text-gray-800">
          Your Trusted Cleaning Partner
        </h2>
        <p className="text-md md:text-lg mt-2 text-gray-700">
          Quality Cleaning Services Tailored to Your Needs.
        </p>
        <button
          onClick={() => setIsQuoteModalOpen(true)}
          className={`mt-6 bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition}`}
        >
          Get a Free Quote
        </button>
      </section>

      {/* Services Section */}
      <section className="py-10 px-4 md:px-6">
        <h2 className="text-2xl md:text-3xl font-bold text-center mb-6 text-gray-800">
          Our Services
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {services.map((service) => (
            <ServiceCard key={service.id} service={service} />
          ))}
        </div>
      </section>

      {/* Testimonials/Reviews Section */}
      <section ref={reviewsSectionRef} className="py-10 px-4 md:px-6 bg-gray-100">
        <h2 className="text-2xl md:text-3xl font-bold text-center mb-6 text-gray-800">
          What Our Clients Say
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {reviews.map((review, index) => (
            <ReviewCard key={index} review={review} />
          ))}
        </div>
      </section>

      {/* Customer Review Button */}
      <section className="py-10 px-4 md:px-6 text-center">
        <button
          onClick={() => setIsLeaveReviewModalOpen(true)}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-3 px-6 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition}`}
        >
          Leave a Review
        </button>
      </section>

      {/* Footer */}
      <footer className={`bg-${theme.colors.gray800} text-${theme.colors.white} text-center p-4`}>
        <p>Â© 2024 CleanConnect. All rights reserved.</p>
      </footer>


      {/* Leave a Review Modal */}
      <Modal
        isOpen={isLeaveReviewModalOpen}
        onClose={() => setIsLeaveReviewModalOpen(false)}
        title="Leave a Review"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
        />
        <textarea
          placeholder="Write a review"
          className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
        />

        {/* Star Rating Input */}
        <div className="mb-4">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-2">Rating:</label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-2xl ${star <= review.rating ? 'text-yellow-500' : 'text-gray-400'}`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
              >
                <Star size={20} />
              </button>
            ))}
          </div>
        </div>

        <button
          onClick={handleReviewSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-2 px-4 rounded hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full`}
        >
          Submit Review
        </button>
      </Modal>


      {/* Request Quote Modal */}
      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Request a Quote"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
        />
        <select
          className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <button
          onClick={handleQuoteSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-2 px-4 rounded hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full`}
        >
          Submit Quote
        </button>
      </Modal>


      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Book an Appointment"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
        />
        <select
          className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <input
          type="date"
          className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
        />
        <button
          onClick={handleAppointmentSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-2 px-4 rounded hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full`}
        >
          Book Now
        </button>
      </Modal>
    </div>
  );
};

export default App;
