import React, { useState, useEffect, useRef } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
  Menu,
} from "lucide-react";
import "tailwindcss/tailwind.css";

const themeColors = {
  primary: "blue-500",
  primaryLight: "blue-300",
  primaryDark: "blue-700",
  secondary: "gray-800",
  textPrimary: "gray-900",
  textSecondary: "gray-700",
  background: "gray-50",
  accent: "yellow-500",
  white: "white",
  success: "green-500",
  error: "red-500",
  grayLight: "gray-100"
};

const services = [
  {
    id: "home",
    name: "Home Cleaning",
    icon: <Home />,
    description: "Expert cleaning for homes.",
  },
  {
    id: "office",
    name: "Office Cleaning",
    icon: <Briefcase />,
    description: "Maintain a spotless office space.",
  },
  {
    id: "deep",
    name: "Deep Cleaning",
    icon: <SprayCan />,
    description: "Thorough deep cleaning services.",
  },
];

const prePopulatedReviews = [
  {
    name: "Isaac",
    text: "Fantastic service! My home has never been this clean.",
  },
  {
    name: "Kojo",
    text: "The office cleaning was thorough and professional.",
  },
  { name: "Big Ben", text: "Amazing deep cleaning. Highly recommended!" },
];

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [review, setReview] = useState({ name: "", text: "" });
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });
  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isLeaveReviewModalOpen, setIsLeaveReviewModalOpen] = useState(false); // State for Leave a Review modal

  const [notification, setNotification] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const reviewsSectionRef = useRef(null);

  useEffect(() => {
    const savedReviews = JSON.parse(localStorage.getItem("reviews")) || [];
    setReviews(savedReviews.length > 0 ? savedReviews : prePopulatedReviews);
  }, []);

  useEffect(() => {
    localStorage.setItem("reviews", JSON.stringify(reviews));
  }, [reviews]);

  const handleReviewSubmit = () => {
    if (!review.name || !review.text) {
      showNotification("Please fill in all fields!", "error");
      return;
    }
    setReviews([...reviews, review]);
    setReview({ name: "", text: "" });
    showNotification("Review submitted successfully!", "success");
    setIsLeaveReviewModalOpen(false); // Close modal after submit
  };

  const handleQuoteSubmit = () => {
    if (!quote.name || !quote.service) {
      showNotification("Please enter your name and select a service!", "error");
      return;
    }
    showNotification(
      `Quote request for ${quote.service} submitted!`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    if (!appointment.name || !appointment.service || !appointment.date) {
      showNotification("Please fill in all fields!", "error");
      return;
    }
    showNotification(
      `Appointment booked for ${appointment.service} on ${appointment.date}!`,
      "success"
    );
    setAppointment({ name: "", service: "", date: "" });
    setIsAppointmentModalOpen(false);
  };

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const scrollToReviews = () => {
    reviewsSectionRef.current?.scrollIntoView({ behavior: "smooth" });
    setIsMobileMenuOpen(false); // Close mobile menu after scrolling
  };

  return (
    <div className={`min-h-screen bg-${themeColors.background} text-${themeColors.textPrimary} font-inter font-sans`}>
      {/* Notification */}
      {notification && (
        <div
          className={`fixed top-6 right-6 p-3 rounded-lg flex items-center space-x-2 text-${themeColors.white} ${
            notification.type === "success" ? `bg-${themeColors.success}` : `bg-${themeColors.error}`
          }`}
        >
          <CheckCircle />
          <span>{notification.message}</span>
        </div>
      )}

      {/* Header */}
      <header className={`bg-gradient-to-r from-${themeColors.primary} to-${themeColors.primaryDark} text-${themeColors.white} p-4 flex justify-between items-center shadow-md`}>
        <h1 className="text-2xl font-bold">CleanConnect</h1>

        {/* Mobile Menu Button */}
        <button
          className={`md:hidden text-${themeColors.white} focus:outline-none p-2 rounded hover:bg-${themeColors.primaryDark}`}
          onClick={toggleMobileMenu}
          aria-label="Toggle Menu"
        >
          <Menu size={24} />
        </button>

        {/* Navigation Links (as Lists) - Hidden on Mobile by Default */}
        <nav className="hidden md:block">
          <ul className="flex space-x-4">
            <li>
              <button
                onClick={scrollToReviews}
                className={`text-${themeColors.white} hover:text-${themeColors.primaryLight} transition duration-200`}
              >
                Customer Reviews
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsQuoteModalOpen(true)}
                className={`text-${themeColors.white} hover:text-${themeColors.primaryLight} transition duration-200`}
              >
                Request Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsAppointmentModalOpen(true)}
                className={`text-${themeColors.white} hover:text-${themeColors.primaryLight} transition duration-200`}
              >
                Book Appointment
              </button>
            </li>
          </ul>
        </nav>
      </header>

      {/* Mobile Menu (Collapsible) */}
      <div
        className={`md:hidden bg-${themeColors.primary} text-${themeColors.white} p-4 rounded shadow-md ${
          isMobileMenuOpen ? "block" : "hidden"
        }`}
      >
        <ul className="flex flex-col space-y-2">
          <li>
            <button
              onClick={() => {
                scrollToReviews();
              }}
              className={`block w-full text-left hover:bg-${themeColors.primaryDark} p-2 rounded transition duration-200`}
            >
              Customer Reviews
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsQuoteModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className={`block w-full text-left hover:bg-${themeColors.primaryDark} p-2 rounded transition duration-200`}
            >
              Request Quote
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsAppointmentModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className={`block w-full text-left hover:bg-${themeColors.primaryDark} p-2 rounded transition duration-200`}
            >
              Book Appointment
            </button>
          </li>
        </ul>
      </div>

      {/* Hero Section */}
      <section className={`bg-gradient-to-br from-${themeColors.primaryLight} to-${themeColors.primary} text-center py-12 px-4 md:py-16`}>
        <h2 className="text-3xl md:text-4xl font-bold text-gray-800">
          Your Trusted Cleaning Partner
        </h2>
        <p className={`text-md md:text-lg mt-2 text-${themeColors.textSecondary}`}>
          Quality Cleaning Services Tailored to Your Needs.
        </p>
        <button onClick={() => setIsQuoteModalOpen(true)} className={`mt-6 bg-${themeColors.primary} text-${themeColors.white} py-3 px-6 rounded-full hover:bg-${themeColors.primaryDark} focus:outline-none focus:ring-2 focus:ring-${themeColors.primaryLight} transition duration-200`}>
          Get a Free Quote
        </button>
      </section>

      {/* Services Section */}
      <section className="py-10 px-4 md:px-6">
        <h2 className="text-2xl md:text-3xl font-bold text-center mb-6 text-gray-800">
          Our Services
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {services.map((service) => (
            <div
              key={service.id}
              className="p-6 bg-white shadow-md rounded-lg text-center hover:shadow-lg transition duration-200"
            >
              <div className={`text-${themeColors.primary} mx-auto mb-2`}>{service.icon}</div>
              <h3 className="text-lg md:text-xl font-semibold mt-2 text-gray-800">
                {service.name}
              </h3>
              <p className={`text-${themeColors.textSecondary} mt-2`}>{service.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Testimonials/Reviews Section */}
      <section ref={reviewsSectionRef} className={`py-10 px-4 md:px-6 bg-${themeColors.grayLight}`}>
        <h2 className="text-2xl md:text-3xl font-bold text-center mb-6 text-gray-800">
          What Our Clients Say
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {reviews.map((review, index) => (
            <div
              key={index}
              className="bg-white shadow-md rounded-lg p-4"
            >
              <div className={`text-${themeColors.accent} mb-2`}>
                <Star size={16} className="inline" /> <Star size={16} className="inline" /> <Star size={16} className="inline" /> <Star size={16} className="inline" />  {/* Add more stars dynamically if needed */}
              </div>
              <p className={`text-${themeColors.textSecondary} italic mb-2`}>"{review.text}"</p>
              <p className={`text-${themeColors.textSecondary} font-semibold`}>- {review.name}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Customer Review Button (Opens Modal) */}
      <section className="py-10 px-4 md:px-6 text-center">
        <button
          onClick={() => setIsLeaveReviewModalOpen(true)}
          className={`bg-${themeColors.primary} text-${themeColors.white} py-3 px-6 rounded-full hover:bg-${themeColors.primaryDark} focus:outline-none focus:ring-2 focus:ring-${themeColors.primaryLight} transition duration-200`}
        >
          Leave a Review
        </button>
      </section>

      {/* Footer */}
      <footer className={`bg-${themeColors.secondary} text-${themeColors.white} text-center p-4`}>
        <p>Â© 2024 CleanConnect. All rights reserved.</p>
      </footer>

      {/* Leave a Review Modal */}
      {isLeaveReviewModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-11/12 md:w-96 relative">
            <button
              className="absolute top-3 right-3 text-gray-600 hover:text-gray-800"
              onClick={() => setIsLeaveReviewModalOpen(false)}
            >
              <XCircle size={20} />
            </button>
            <h2 className="text-xl font-semibold mb-4 text-gray-800">
              Leave a Review
            </h2>
            <input
              type="text"
              placeholder="Your Name"
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={review.name}
              onChange={(e) => setReview({ ...review, name: e.target.value })}
            />
            <textarea
              placeholder="Write a review"
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={review.text}
              onChange={(e) => setReview({ ...review, text: e.target.value })}
            />
            <button
              onClick={handleReviewSubmit}
              className={`bg-${themeColors.primary} text-${themeColors.white} py-2 px-4 rounded hover:bg-${themeColors.primaryDark} focus:outline-none focus:ring-2 focus:ring-${themeColors.primaryLight} transition duration-200 w-full`}
            >
              Submit Review
            </button>
          </div>
        </div>
      )}

      {/* Request Quote Modal */}
      {isQuoteModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-11/12 md:w-96 relative">
            <button
              className="absolute top-3 right-3 text-gray-600 hover:text-gray-800"
              onClick={() => setIsQuoteModalOpen(false)}
            >
              <XCircle size={20} />
            </button>
            <h2 className="text-xl font-semibold mb-4 text-gray-800">
              Request a Quote
            </h2>
            <input
              type="text"
              placeholder="Your Name"
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={quote.name}
              onChange={(e) => setQuote({ ...quote, name: e.target.value })}
            />
            <select
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={quote.service}
              onChange={(e) => setQuote({ ...quote, service: e.target.value })}
            >
              <option value="" disabled>
                Select a service
              </option>
              {services.map((s) => (
                <option key={s.id} value={s.name}>
                  {s.name}
                </option>
              ))}
            </select>
            <button
              onClick={handleQuoteSubmit}
              className={`bg-${themeColors.primary} text-${themeColors.white} py-2 px-4 rounded hover:bg-${themeColors.primaryDark} focus:outline-none focus:ring-2 focus:ring-${themeColors.primaryLight} transition duration-200 w-full`}
            >
              Submit Quote
            </button>
          </div>
        </div>
      )}

      {/* Book Appointment Modal */}
      {isAppointmentModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-11/12 md:w-96 relative">
            <button
              className="absolute top-3 right-3 text-gray-600 hover:text-gray-800"
              onClick={() => setIsAppointmentModalOpen(false)}
            >
              <XCircle size={20} />
            </button>
            <h2 className="text-xl font-semibold mb-4 text-gray-800">
              Book an Appointment
            </h2>
            <input
              type="text"
              placeholder="Your Name"
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={appointment.name}
              onChange={(e) =>
                setAppointment({ ...appointment, name: e.target.value })
              }
            />
            <select
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={appointment.service}
              onChange={(e) =>
                setAppointment({ ...appointment, service: e.target.value })
              }
            >
              <option value="" disabled>
                Select a service
              </option>
              {services.map((s) => (
                <option key={s.id} value={s.name}>
                  {s.name}
                </option>
              ))}
            </select>
            <input
              type="date"
              className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={appointment.date}
              onChange={(e) =>
                setAppointment({ ...appointment, date: e.target.value })
              }
            />
            <button
              onClick={handleAppointmentSubmit}
              className={`bg-${themeColors.primary} text-${themeColors.white} py-2 px-4 rounded hover:bg-${themeColors.primaryDark} focus:outline-none focus:ring-2 focus:ring-${themeColors.primaryLight} transition duration-200 w-full`}
            >
              Book Now
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
