import React, { useState, useEffect, useRef } from "react";
import {
  Home,
  Briefcase,
  SprayCan,
  XCircle,
  CheckCircle,
  Star,
  Menu,
} from "lucide-react";
import { motion } from "framer-motion";
import "tailwindcss/tailwind.css";

// **THEME DEFINITION**
const theme = {
  colors: {
    primary: "sky-500", // Lighter, more bubbly blue
    primaryHover: "sky-600",
    secondary: "white", // White background for a clean look
    textPrimary: "gray-800",
    textSecondary: "gray-600", // Slightly lighter text
    accent: "teal-500", //  Teal as a modern accent
    success: "green-500",
    error: "red-500",
    white: "white",
    gray800: "gray-800",
    light: "gray-50", // Lighter background
  },
  font: {
    family: "font-inter font-sans", // Using Inter font
    base: "text-base", // Base font size
  },
  shadow: "shadow-md", // Reduced shadow for a cleaner look
  borderRadius: "rounded-3xl", // More rounded corners
  transition: "transition duration-300 ease-in-out", // Faster transition
};

// **DATA (Move outside the component)**
const services = [
  {
    id: "home",
    name: "Residential Bliss Cleaning",
    icon: <Home size={28} />, // Increased icon size for better visibility
    description:
      "Transform your home into a haven of cleanliness and tranquility. Our residential cleaning service offers meticulous attention to detail, leaving your space refreshed and revitalized.",
  },
  {
    id: "office",
    name: "Commercial Sparkle Cleaning",
    icon: <Briefcase size={28} />,
    description:
      "Elevate your workspace with our comprehensive commercial cleaning. We'll ensure a spotless and hygienic environment, promoting productivity and a positive impression for clients and employees alike.",
  },
  {
    id: "deep",
    name: "Intensive Renewal Cleaning",
    icon: <SprayCan size={28} />,
    description:
      "Experience the ultimate cleanliness with our deep cleaning service. We'll tackle every nook and cranny, removing accumulated dirt and grime for a truly pristine result.",
  },
];

const prePopulatedReviews = [
  {
    name: "Isaac",
    text: "Absolutely transformed my home! The attention to detail was outstanding. Highly recommend!",
    rating: 5,
  },
  {
    name: "Kojo",
    text: "Professional and efficient office cleaning service. Noticeably improved our workspace environment.",
    rating: 4,
  },
  {
    name: "Big Ben",
    text: "Incredible deep cleaning results! My place feels brand new. Worth every penny!",
    rating: 5,
  },
];

// **HELPER FUNCTIONS (Move outside the component)**
const getStarRating = (rating) => {
  const stars = [];
  for (let i = 0; i < 5; i++) {
    stars.push(
      <Star
        key={i}
        size={20} // Increased star size for better visibility
        className={`inline ${
          i < rating ? "text-yellow-400" : "text-gray-300"
        }`} // Use dynamic class for filled/empty star
        fill={i < rating ? "yellow" : "none"} // Conditionally fill stars
        stroke="currentColor" // Ensure stars have a stroke
      />
    );
  }
  return stars;
};

// **COMPONENTS (Extract into separate files for larger projects)**

const Notification = ({ message, type }) => (
  <motion.div
    initial={{ opacity: 0, y: -20 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -20 }}
    className={`fixed top-6 right-6 p-5 rounded-3xl flex items-center space-x-3 text-white ${
      type === "success" ? `bg-${theme.colors.success}` : `bg-${theme.colors.error}`
    } ${theme.shadow} ${theme.transition}`}
  >
    <CheckCircle size={24} />
    <span>{message}</span>
  </motion.div>
);

const Modal = ({ isOpen, onClose, children, title }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-70 flex justify-center items-center z-50">
      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.8, opacity: 0 }}
        className={`bg-white p-10 rounded-3xl shadow-xl w-11/12 md:w-96 relative`}
      >
        <button
          className="absolute top-4 right-4 text-gray-600 hover:text-gray-800 focus:outline-none"
          onClick={onClose}
          aria-label="Close Modal"
        >
          <XCircle size={28} />
        </button>
        {title && (
          <h2 className="text-3xl font-semibold mb-7 text-gray-800 text-center">
            {title}
          </h2>
        )}
        {children}
      </motion.div>
    </div>
  );
};

const ServiceCard = ({ service }) => (
  <motion.div
    whileHover={{ scale: 1.05, translateY: -5 }}
    className={`p-8 bg-white ${theme.shadow} ${theme.borderRadius} text-center hover:shadow-lg ${theme.transition} cursor-pointer border border-gray-100`} // Added subtle border
  >
    <div className={`text-${theme.colors.accent} mx-auto mb-5`}>{service.icon}</div>
    <h3 className={`text-xl font-semibold mt-3 text-${theme.colors.textPrimary} mb-2`}>
      {service.name}
    </h3>
    <p className={`text-${theme.colors.textSecondary}`}>{service.description}</p>
  </motion.div>
);

const ReviewCard = ({ review }) => (
  <motion.div
    whileHover={{ scale: 1.03 }}
    className={`bg-white ${theme.shadow} ${theme.borderRadius} p-6 ${theme.transition} border border-gray-100`} // Added subtle border
  >
    <div className="text-yellow-500 mb-4">{getStarRating(review.rating)}</div>
    <p className={`text-${theme.colors.textSecondary} italic mb-4`}>"{review.text}"</p>
    <p className={`text-${theme.colors.textSecondary} font-semibold`}>- {review.name}</p>
  </motion.div>
);

const App = () => {
  const [reviews, setReviews] = useState([]);
  const [review, setReview] = useState({ name: "", text: "", rating: 5 }); // Added rating to the review state
  const [quote, setQuote] = useState({ name: "", service: "" });
  const [appointment, setAppointment] = useState({
    name: "",
    service: "",
    date: "",
  });

  const [isQuoteModalOpen, setIsQuoteModalOpen] = useState(false);
  const [isAppointmentModalOpen, setIsAppointmentModalOpen] = useState(false);
  const [isLeaveReviewModalOpen, setIsLeaveReviewModalOpen] = useState(false);

  const [notification, setNotification] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const reviewsSectionRef = useRef(null);

  // **LOCAL STORAGE HOOKS**
  useEffect(() => {
    const savedReviews = JSON.parse(localStorage.getItem("reviews")) || [];
    setReviews(savedReviews.length > 0 ? savedReviews : prePopulatedReviews);
  }, []);

  useEffect(() => {
    localStorage.setItem("reviews", JSON.stringify(reviews));
  }, [reviews]);

  // **HANDLERS**
  const handleReviewSubmit = () => {
    if (!review.name || !review.text) {
      showNotification("Please provide your name and review text!", "error");
      return;
    }
    setReviews([...reviews, review]);
    setReview({ name: "", text: "", rating: 5 }); // Reset rating to default
    showNotification("Thank you for your review!", "success");
    setIsLeaveReviewModalOpen(false);
  };

  const handleQuoteSubmit = () => {
    if (!quote.name || !quote.service) {
      showNotification("Please enter your name and select a service!", "error");
      return;
    }
    showNotification(
      `Your quote request for ${quote.service} has been submitted! We'll be in touch soon.`,
      "success"
    );
    setQuote({ name: "", service: "" });
    setIsQuoteModalOpen(false);
  };

  const handleAppointmentSubmit = () => {
    if (!appointment.name || !appointment.service || !appointment.date) {
      showNotification("Please fill in all fields to book your appointment!", "error");
      return;
    }
    showNotification(
      `Your appointment for ${appointment.service} on ${appointment.date} is booked!`,
      "success"
    );
    setAppointment({ name: "", service: "", date: "" });
    setIsAppointmentModalOpen(false);
  };

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 4000);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const scrollToReviews = () => {
    reviewsSectionRef.current?.scrollIntoView({ behavior: "smooth" });
    setIsMobileMenuOpen(false);
  };

  // **RENDER**
  return (
    <div
      className={`min-h-screen bg-${theme.colors.light} text-${theme.colors.textPrimary} ${theme.font.family} ${theme.font.base}`}
    >
      {/* Notification */}
      {notification && <Notification message={notification.message} type={notification.type} />}

      {/* Header */}
      <header
        className={`bg-gradient-to-r from-sky-400 to-sky-600 text-${theme.colors.white} p-6 flex justify-between items-center ${theme.shadow} sticky top-0 z-40`} // Making header sticky
      >
        <a href="/" className="text-2xl font-bold flex items-center">
          <svg
            className="w-8 h-8 mr-2 text-teal-300"
            fill="none"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M4.5 15.75l7.5-7.5 7.5 7.5" />
            <path d="M4.5 18.75l7.5-7.5 7.5 7.5" />
          </svg>
          CleanConnect
        </a>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden text-white focus:outline-none p-3 rounded hover:bg-sky-700"
          onClick={toggleMobileMenu}
          aria-label="Toggle Menu"
        >
          <Menu size={28} />
        </button>

        {/* Navigation Links */}
        <nav className="hidden md:block">
          <ul className="flex space-x-8">
            <li>
              <button
                onClick={scrollToReviews}
                className={`text-${theme.colors.white} hover:text-sky-200 ${theme.transition} focus:outline-none`}
              >
                Customer Reviews
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsQuoteModalOpen(true)}
                className={`text-${theme.colors.white} hover:text-sky-200 ${theme.transition} focus:outline-none`}
              >
                Request Quote
              </button>
            </li>
            <li>
              <button
                onClick={() => setIsAppointmentModalOpen(true)}
                className={`text-${theme.colors.white} hover:text-sky-200 ${theme.transition} focus:outline-none`}
              >
                Book Appointment
              </button>
            </li>
          </ul>
        </nav>
      </header>

      {/* Mobile Menu */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        className={`md:hidden bg-sky-500 text-${theme.colors.white} p-6 rounded-3xl ${theme.shadow} fixed top-0 left-0 w-full h-screen z-50 flex flex-col justify-center items-center space-y-6 ${
          isMobileMenuOpen ? "block" : "hidden"
        }`}
      >
        <button
          className="absolute top-4 right-4 text-white focus:outline-none p-3 rounded hover:bg-sky-700"
          onClick={toggleMobileMenu}
          aria-label="Close Menu"
        >
          <XCircle size={28} />
        </button>
        <ul className="flex flex-col space-y-6 text-center">
          <li>
            <button
              onClick={() => {
                scrollToReviews();
              }}
              className="block w-full text-xl font-semibold hover:bg-sky-600 p-4 rounded-3xl transition duration-300 focus:outline-none"
            >
              Customer Reviews
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsQuoteModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className="block w-full text-xl font-semibold hover:bg-sky-600 p-4 rounded-3xl transition duration-300 focus:outline-none"
            >
              Request Quote
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                setIsAppointmentModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
              className="block w-full text-xl font-semibold hover:bg-sky-600 p-4 rounded-3xl transition duration-300 focus:outline-none"
            >
              Book Appointment
            </button>
          </li>
        </ul>
      </motion.div>

      {/* Hero Section */}
      <section className="bg-gradient-to-br from-sky-50 to-sky-200 text-center py-24 px-6 md:py-32">
        <h2 className="text-5xl md:text-6xl font-extrabold text-gray-800 mb-4">
          Experience the Joy of a Spotless Space
        </h2>
        <p className="text-xl md:text-2xl mt-5 text-gray-700 leading-relaxed">
          We deliver unparalleled cleaning services that transform your home or office into a
          haven of cleanliness and comfort.
        </p>
        <motion.button
          whileHover={{ scale: 1.08 }}
          onClick={() => setIsQuoteModalOpen(true)}
          className={`mt-10 bg-${theme.colors.primary} text-${theme.colors.white} py-5 px-10 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-xl font-semibold`}
        >
          Get a Free Quote
        </motion.button>
      </section>

      {/* Services Section */}
      <section className="py-20 px-6 md:px-8">
        <h2 className="text-4xl md:text-5xl font-bold text-center mb-12 text-gray-800">
          Our Specialized Services
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
          {services.map((service) => (
            <ServiceCard key={service.id} service={service} />
          ))}
        </div>
      </section>

      {/* Testimonials/Reviews Section */}
      <section
        ref={reviewsSectionRef}
        className="py-20 px-6 md:px-8 bg-gray-50"
      >
        <h2 className="text-4xl md:text-5xl font-bold text-center mb-12 text-gray-800">
          Rave Reviews from Our Satisfied Clients
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10">
          {reviews.map((review, index) => (
            <ReviewCard key={index} review={review} />
          ))}
        </div>
      </section>

      {/* Customer Review Button */}
      <section className="py-20 px-6 md:px-8 text-center">
        <motion.button
          whileHover={{ scale: 1.08 }}
          onClick={() => setIsLeaveReviewModalOpen(true)}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-5 px-10 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-4 focus:ring-${theme.colors.accent} ${theme.transition} text-xl font-semibold`}
        >
          Share Your Experience
        </motion.button>
      </section>

      {/* Footer */}
      <footer className={`bg-${theme.colors.gray800} text-${theme.colors.white} text-center p-8`}>
        <p className="text-lg"> 2024 CleanConnect. All rights reserved.</p>
      </footer>

      {/* Leave a Review Modal */}
      <Modal
        isOpen={isLeaveReviewModalOpen}
        onClose={() => setIsLeaveReviewModalOpen(false)}
        title="Share Your Experience"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-4 border rounded-3xl mb-5 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={review.name}
          onChange={(e) => setReview({ ...review, name: e.target.value })}
        />
        <textarea
          placeholder="Write a review"
          className="w-full p-4 border rounded-3xl mb-5 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={review.text}
          onChange={(e) => setReview({ ...review, text: e.target.value })}
          rows="4" // Added rows for better textarea display
        />

        {/* Star Rating Input */}
        <div className="mb-6">
          <label htmlFor="rating" className="block text-gray-700 text-sm font-bold mb-3">
            Rating:
          </label>
          <div>
            {[1, 2, 3, 4, 5].map((star) => (
              <button
                key={star}
                type="button"
                className={`text-4xl focus:outline-none ${
                  star <= review.rating ? "text-yellow-400" : "text-gray-300"
                }`}
                onClick={() => setReview({ ...review, rating: star })}
                aria-label={`Rate ${star} stars`}
              >
                <Star size={24} fill={star <= review.rating ? "yellow" : "none"}  stroke="currentColor"/>
              </button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.05 }}
          onClick={handleReviewSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-4 px-8 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Submit Review
        </motion.button>
      </Modal>

      {/* Request Quote Modal */}
      <Modal
        isOpen={isQuoteModalOpen}
        onClose={() => setIsQuoteModalOpen(false)}
        title="Request a Personalized Quote"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-4 border rounded-3xl mb-5 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={quote.name}
          onChange={(e) => setQuote({ ...quote, name: e.target.value })}
        />
        <select
          className="w-full p-4 border rounded-3xl mb-5 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={quote.service}
          onChange={(e) => setQuote({ ...quote, service: e.target.value })}
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <motion.button
          whileHover={{ scale: 1.05 }}
          onClick={handleQuoteSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-4 px-8 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Get Your Quote
        </motion.button>
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        isOpen={isAppointmentModalOpen}
        onClose={() => setIsAppointmentModalOpen(false)}
        title="Schedule Your Cleaning Appointment"
      >
        <input
          type="text"
          placeholder="Your Name"
          className="w-full p-4 border rounded-3xl mb-5 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={appointment.name}
          onChange={(e) =>
            setAppointment({ ...appointment, name: e.target.value })
          }
        />
        <select
          className="w-full p-4 border rounded-3xl mb-5 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={appointment.service}
          onChange={(e) =>
            setAppointment({ ...appointment, service: e.target.value })
          }
        >
          <option value="" disabled>
            Select a service
          </option>
          {services.map((s) => (
            <option key={s.id} value={s.name}>
              {s.name}
            </option>
          ))}
        </select>
        <input
          type="date"
          className="w-full p-4 border rounded-3xl mb-5 focus:outline-none focus:ring-2 focus:ring-sky-400"
          value={appointment.date}
          onChange={(e) =>
            setAppointment({ ...appointment, date: e.target.value })
          }
        />
        <motion.button
          whileHover={{ scale: 1.05 }}
          onClick={handleAppointmentSubmit}
          className={`bg-${theme.colors.primary} text-${theme.colors.white} py-4 px-8 rounded-full hover:bg-${theme.colors.primaryHover} focus:outline-none focus:ring-2 focus:ring-${theme.colors.accent} ${theme.transition} w-full text-lg font-semibold`}
        >
          Book Now
        </motion.button>
      </Modal>
    </div>
  );
};

export default App;
