import React, { useState, useEffect, useRef } from "react";
import "./styles.css";

const RaffleComponent = () => {
  const [entries, setEntries] = useState(["Alice", "Bob", "Charlie", "David", "Emma"]);
  const [winner, setWinner] = useState(null);
  const [prize, setPrize] = useState("A Luxurious Getaway");
  const [timeRemaining, setTimeRemaining] = useState(10000);
  const [timerActive, setTimerActive] = useState(false);
  const [notification, setNotification] = useState({ message: "", type: "", visible: false });
  const winnerRef = useRef(null);
  const entriesRef = useRef(null);
  const notificationTimeoutRef = useRef(null);

  useEffect(() => {
    let intervalId;
    const pickWinner = () => {
      if (entries.length > 0) {
        const randomIndex = Math.floor(Math.random() * entries.length);
        setWinner(entries[randomIndex]);
        setTimeout(() => {
          winnerRef.current?.scrollIntoView({ behavior: "smooth" });
        }, 500);
      } else {
        setWinner("Oops! Looks like no one participated.");
        showNotification("No entries found. Unable to pick a winner.", "info");
      }
    };

    if (timerActive) {
      intervalId = setInterval(() => {
        setTimeRemaining((prevTime) => {
          const newTime = prevTime - 1000;
          if (newTime <= 0) {
            clearInterval(intervalId);
            setTimerActive(false);
            pickWinner();
            return 0;
          }
          return newTime;
        });
      }, 1000);
    }

    return () => clearInterval(intervalId);
  }, [entries, timerActive]);

  const handleStartTimer = () => {
    setTimerActive(true);
  };

  const handleResetTimer = () => {
    setTimeRemaining(10000);
    setWinner(null);
    setTimerActive(false);
  };

  const showNotification = (message, type = "info", duration = 3000) => {
    setNotification({ message, type, visible: true });

    if (notificationTimeoutRef.current) {
      clearTimeout(notificationTimeoutRef.current);
    }

    notificationTimeoutRef.current = setTimeout(() => {
      setNotification((prevState) => ({ ...prevState, visible: false }));
    }, duration);
  };

  return (
    <div className="raffle-container">
      <h1>Raffle Draw</h1>
      <div className="entries" ref={entriesRef}>
        <h2>Participants</h2>
        <ul>
          {entries.map((entry, index) => (
            <li key={index}>{entry}</li>
          ))}
        </ul>
      </div>
      <div className="timer-controls">
        <button onClick={handleStartTimer} disabled={timerActive}>Start Timer</button>
        <button onClick={handleResetTimer}>Reset Timer</button>
      </div>
      <p>Time Remaining: {timeRemaining / 1000} seconds</p>
      <div ref={winnerRef} className="winner">
        {winner && <h2>Winner: {winner}</h2>}
      </div>
      {notification.visible && (
        <div className={`notification ${notification.type}`}>{notification.message}</div>
      )}
    </div>
  );
};

export default RaffleComponent;
